/*Table structure for table `books` */

DROP TABLE IF EXISTS `books`;

CREATE TABLE `books` (
  `bid` int(11) NOT NULL AUTO_INCREMENT,
  `bname` varchar(12) DEFAULT NULL,
  `bauthor` varchar(6) DEFAULT NULL,
  `bintroduction` varchar(180) DEFAULT NULL,
  `bimg` varchar(50) DEFAULT NULL,
  `bcategory` varchar(40) DEFAULT NULL,
  `bnum` int(11) NOT NULL,
  `btime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `readtimes` int(11) DEFAULT '1',
  `totalwords` int(11) DEFAULT NULL,
  `collectiontimes` int(11) DEFAULT '1',
  PRIMARY KEY (`bid`),
  UNIQUE KEY `bnum` (`bnum`)
) ENGINE=InnoDB AUTO_INCREMENT=91 DEFAULT CHARSET=utf8;

/*Data for the table `books` */

insert  into `books`(`bid`,`bname`,`bauthor`,`bintroduction`,`bimg`,`bcategory`,`bnum`,`btime`,`readtimes`,`totalwords`,`collectiontimes`) values (1,'元尊','天蚕土豆','天地为炉，万物为铜，阴阳为炭，造化为工。气运之争，蟒雀吞龙。究竟是蟒雀为尊，还是圣龙崛起，凌驾众生？这是气掌乾坤的世界，磅礴宏伟，一气可搬山，可倒海，可翻天，可掌阴阳乾坤。世间源气分九品，三品称玄，六品成天，九品号圣。吾有一口玄黄气，可吞天地日月星。','book/a1.jpg','玄幻',1,'2019-12-31 21:28:08',8,220,3),(2,'圣墟','辰东','在破败中崛起，在寂灭中复苏。沧海成尘，雷电枯竭，那一缕幽雾又一次临近大地，世间的枷锁被打开了，一个全新的世界就此揭开神秘的一角……','book/a2.jpg','玄幻',2,'2019-12-31 10:11:09',27,452,2),(3,'斗罗大陆','唐家三少','唐门外门弟子唐三，因偷学内门绝学为唐门所不容，跳崖明志时却发现没有死，反而以另外一个身份来到了另一个世界，一个属于武魂的世界，名叫斗罗大陆。这里没有魔法，没有斗气，没有武术，却有神奇的武魂。这里的每个人，在自己六岁的时候...','book/a3.jpg','玄幻',3,'2019-12-31 21:28:34',9,293,2),(4,'全职法师','乱','一觉醒来，世界巨变。藏匿于西湖下的图腾玄蛇，屹立时如摩天大厦。游荡在古都城墙外的亡灵大军，它们只听从皇陵下传出的低语。埃及金字塔中的冥王，它和它的部众始终觊觎着东方大地！伦敦有着伟大的驯龙世家...','book/a4.jpg','玄幻',4,'2019-12-31 21:29:04',10,868,6),(5,'修罗武神','善良的蜜蜂','论潜力，不算天才，可玄功武技，皆可无师自通。　　论魅力，千金小姐算什么，妖女圣女，都爱我欲罢不能。　　论实力，任凭你有万千至宝，但定不敌我界灵大军。　　我是谁？天下众生视我为修罗，却不知，我以修罗成武神...','book/a5.jpg','玄幻',5,'2019-12-04 12:02:21',1,890,0),(6,'武炼巅峰','莫默','武之巅峰，是孤独，是寂寞，是漫漫求索，是高处不胜寒逆境中成长，绝地里求生，不屈不饶，才能堪破武之极道。凌霄阁试炼弟子兼扫地小厮杨开偶获一本无字黑书...','book/a6.jpg','玄幻',6,'2019-12-31 21:29:14',1,1530,1),(7,'逆天邪神','火星引力','掌天毒之珠，承邪神之血，修逆天之力，一代邪神，君临天下！','book/a7.jpg','玄幻',7,'2019-12-04 12:02:53',1,571,0),(8,'伏天氏','净无痕','东方神州，有人皇立道统，有圣贤宗门传道，有诸侯雄踞一方王国，诸强林立，神州动乱千万载，值此之时，一代天骄叶青帝及东凰大帝横空出世，东方神州一统！...','book/a8.jpg','玄幻',8,'2019-12-06 19:35:14',3,458,0),(9,'万古神帝','飞天鱼','八百年前，明帝之子张若尘，被他的未婚妻池瑶公主杀死，一代天骄，就此陨落。八百年后，张若尘重新活了过来，却发现曾经杀死他的未婚妻，已经统一昆仑界...','book/a9.jpg','玄幻',9,'2019-12-31 21:27:01',8,822,2),(10,'斗破苍穹','天蚕土豆','这里是属于斗气的世界，没有花俏艳丽的魔法，有的，仅仅是繁衍到巅峰的斗气！新书等级制度：斗者，斗师，大斗师，斗灵，斗王，斗皇，斗宗，斗尊，斗圣，斗帝。','book/a10.jpg','玄幻',10,'2019-12-31 10:08:24',35,529,7),(11,'帝霸','厌笔萧生','千万年前，李七夜栽下一株翠竹。八百万年前，李七夜养了一条鲤鱼。五百万年前，李七夜收养一个小女孩。今天，李七夜一觉醒来，翠竹修练成神灵，鲤鱼化作金龙...','book/a11.jpg','玄幻',11,'2019-12-04 12:03:58',1,1153,0),(12,'剑来','烽火戏诸侯','大千世界，无奇不有。我陈平安，唯有一剑，可搬山，倒海，降妖，镇魔，敕神，摘星，断江，摧城，开天！————我叫陈平安，平平安安的平安，我是一名剑客。','book/a12.jpg','玄幻',12,'2019-12-29 22:13:51',1,530,1),(13,'完美世界','辰东','一粒尘可填海，一根草斩尽日月星辰，弹指间天翻地覆。群雄并起，万族林立，诸圣争霸，乱天动地。问苍茫大地，谁主沉浮？！一个少年从大荒中走出，一切从这里开始...','book/a13.jpg','玄幻',13,'2019-12-04 12:04:19',1,658,0),(14,'大主宰','天蚕土豆','大千世界，位面交汇，万族林立，群雄荟萃，一位位来自下位面的天之至尊，在这无尽世界，演绎着令人向往的传奇，追求着那主宰之路。无尽火域，炎帝执掌，万火焚苍穹。武境之内，武祖之威，震慑乾坤。西天之殿，百战之皇，战威无可敌。北荒之丘，万墓之地，不死之主镇天地...','book/a14.jpg','玄幻',14,'2020-01-01 11:30:46',7,496,0),(15,'斗罗大陆4终极斗罗','唐家三少','一万年后，冰化了。斗罗联邦科考队在极北之地科考时发现了一个有着金银双色花纹的蛋，用仪器探察之后，发现里面居然有生命体征，赶忙将其带回研究所进行孵化。蛋孵化出来了，可孵出来的却是一个婴儿，和人类一模一样的婴儿，一个蛋生的孩子。','book/a15.jpg','玄幻',15,'2019-12-04 12:04:52',1,168,0),(16,'九星毒奶','育','这是一个充满了星力的平行世界。星图、星技、星宠、觉醒者大行其道。魂穿而来的江晓，体内蕴含着一张奇特的内视星图，成为一名稀有的医疗','book/b1.jpg','科幻',16,'2019-12-31 21:42:57',11,280,1),(17,'学霸的黑科技系统','晨星LL','系统，积分能兑钱吗？”“不能。”“那我要你何用！”“本系统能让你当上学霸，你还要钱干啥？”','book/b2.jpg','科幻',17,'2019-12-04 12:05:23',1,525,0),(18,'极品捉鬼系统','解三千','妖王：“等等！我闻到了人类装逼的气息，撤……。”妖王：“十万妖兵听令，给我拿下那犯人。”万众瞩目之下，楚浩扔出一柄剑：“这轩辕剑你拿好，以后别在我面前装逼。”这天，这地，这沧海，这宇宙，谁都无法阻止我。...','book/b3.jpg','科幻',18,'2019-12-04 12:05:45',1,434,0),(19,'吞噬星空','我吃西红柿','星空中。“这颗星球，通体土黄色，没有任何生命存在，直径21000公里，咦，竟然蕴含‘星泪金’矿脉，真是天助我也，将这颗星球吞噬掉后，我的实力应该能恢复到受...','book/b4.jpg','科幻',19,'2019-12-31 21:28:48',3,477,1),(20,'诸天尽头','凤嘲凰','一觉醒来，罗素脑海中就多出了一个可以穿梭诸天世界的系统。杀手、怪兽、女巫、异形、魔鬼、神明……无限的世界有无限的可能，亦有无限的历险，而踏破无限之人，必将留下属于自己的传说。从平凡到不朽，在诸天的道路上寻觅尽头！...','book/b5.jpg','科幻',20,'2019-12-04 12:06:20',1,435,0),(21,'诸界末日在线','烟火成城','当游戏中的第一件法宝出现在现实世界，所有人类都为之陷入疯狂。这是最好的时代，人们进入游戏，将装备、神通、修为统统带回现实世界；这是最坏的时代，游戏中的妖魔侵入现实，实力恐怖到让人绝望，整个现实世界走向末日。资深玩家顾青山，与最终BOSS同归于尽...','book/b6.jpg','科幻',21,'2019-12-04 12:06:35',1,437,0),(22,'诸天投影','裴屠狗','国术世界中，他国术大成，拳压王无敌！大明江湖中，他横行无忌，天下第一！手捏生死，拳破虚空！白蛇之中，画一卷万妖之图！遮天世界，魔主天下，威慑禁区！完美之中，我为魔皇！西游之中，论道太上，掌压灵山！封神之中，四圣摆下诛仙剑','book/b7.jpg','科幻',22,'2019-12-04 12:06:53',1,482,0),(23,'史上最强店长','南极烈阳','商通万界，让所有人给我打工。周阳意外获得万界商铺系统，交易万千位面。“我曾举行一次小型拍卖会，来到的皇帝就有一百多位，听说还有两个叫李世民的皇帝。”...','book/b8.jpg','科幻',23,'2019-12-04 12:07:15',1,20,0),(24,'黎明之剑','远瞳','高文穿越了，但穿越的时候稍微出了点问题。在某个异界大陆上空飘了十几万年之后，他觉得自己可能需要一具身体才算是成为一个完整的穿越者，但他并没想到自己好不容易成功之后竟然还需要带着这具身体从棺材里爬出来，并且面对两个吓蒙了的曾曾曾曾...','book/b9.jpg','科幻',24,'2019-12-08 12:29:24',1,260,1),(25,'末世大回炉','二十二刀流','丧尸爆发，人类绝境来临，地球磁场瞬变，一切回归最初，回到原始，回归本真。末世，我来了！','book/b10.jpg','科幻',25,'2019-12-04 12:07:39',1,402,0),(26,'我能看见经验值','红颜三千','贺晓天在睡觉前，只是抱怨了一句无聊。结果却在醒来后，发现整个世界都变了！眼睛左上角的光屏，成为了他唯一的依靠。“好吧，这一切我都能接受。可是最起码，你不应该告诉我，到底该怎么获得经验值吗？...','book/b11.jpg','科幻',26,'2019-12-04 12:08:14',1,397,0),(27,'我在绝地求生捡碎片','清酒半壶','江司明突然发现，自己玩的绝地求生跟别人的不一样！他能在游戏里找到各种颜色的碎片，合成后能变成技能、属性、才艺、金钱、古董、甚至丹药、功法等等各色逆天宝贝！靠着在游戏里不断捡碎片，江司明完成了人生逆袭，发家致富！','book/b12.jpg','科幻',27,'2019-12-04 12:08:36',1,3,0),(28,'修真四万年','蜗牛真人','倘若这宇宙，真是一片残酷血腥的黑暗森林，我们修真者，也会燃烧自己的生命，绽放出微弱的火花！”“哪怕这火花再微弱，再短暂，再渺小，可是只要我们源源不断，前赴后继，终有一日，火花会点燃杂草，杂草会燎到灌木，灌木会蔓延大树！...','book/b13.jpg','科幻',28,'2019-12-04 12:09:43',1,2639,0),(29,'从姑获鸟开始','活儿该','身如鸿毛，命如野草。见过最黑的夜，所以心中炽烈明亮的火焰，从不动摇。...','book/b14.jpg','科幻',29,'2019-12-04 12:09:57',1,155,0),(30,'末日轮盘','幻动','当生命停止的刹那，叶钟鸣回到了十年前，那个末日开始的下午。是上天对自己的眷顾？还是又一次的惩罚？真的要重新体验一次残酷冰冷的末世吗？叶钟鸣决定活下去，不为了别的，就为了那些曾经同生共死的战友，不离不弃的爱人！还有，他要找到答案...','book/b15.jpg','科幻',30,'2019-12-04 12:10:18',1,1230,0),(31,'凡人修仙之仙界篇','忘语','凡人修仙，风云再起时空穿梭，轮回逆转金仙太乙，大罗道祖三千大道，法则至尊《凡人修仙传》仙界篇，一个韩立叱咤仙界的故事，一个凡人小子修仙的不灭传说','book/c1.jpg','仙侠',31,'2019-12-31 21:27:20',6,149,1),(32,'遮天','辰东','冰冷与黑暗并存的宇宙深处，九具庞大的龙尸拉着一口青铜古棺，亘古长存。这是太空探测器在枯寂的宇宙中捕捉到的一幅极其震撼的画面。九龙拉棺，究竟是回到了上古，还是来到了星空的彼岸？一个浩大的仙侠世界，光怪陆离，神秘无尽。热血似火山沸腾，激情若瀚海汹涌，欲望如深渊无止境','book/c2.jpg','仙侠',32,'2019-12-08 12:31:30',1,636,1),(33,'雪中悍刀行','烽火戏诸侯','江湖是一张珠帘。大人物小人物，是珠子，大故事小故事，是串线。情义二字，则是那些珠子的精气神。...','book/c3.jpg','仙侠',33,'2019-12-04 12:11:08',1,462,0),(34,'诛仙','萧鼎','这世间本是没有什么神仙的，但自太古以来，人类眼见周遭世界，诸般奇异之事，电闪雷鸣，狂风暴雨，又有天灾人祸，伤亡无数，哀鸿遍野，决非人力所能为，所能抵挡。遂以为九天之上，有诸般神灵，九幽之下，亦是阴魂归处，阎罗殿堂。于是神仙之说，流传于世。无数人类子民，诚心叩拜...','book/c4.jpg','仙侠',34,'2019-12-10 21:11:54',5,181,1),(35,'凡人修仙传','忘语','一个普通山村小子，偶然下进入到当地江湖小门派，成了一名记名弟子。他以这样身份，如何在门派中立足,如何以平庸的资质进入到修仙者的行列，从而笑傲三界之中！...','book/c5.jpg','仙侠',35,'2019-12-08 12:31:49',1,937,1),(36,'永恒国度','孤独漂流','凌晨，天灾降临。天道生变，万灵欲念执念充斥天地，天道不能承受，恶念横行，众生意念演变幻想世界，恶念，善念，执念，欲望纠缠不休，天道无法承受，已到极限，极则变，幻想降临，万界相融，万物霜天竞自由。此为新纪元开启，是毁灭，亦为新生。这一纪元为永恒纪元...','book/c6.jpg','仙侠',36,'2019-12-04 12:12:06',1,405,0),(37,'从前有座灵剑山','国王陛下','灵剑派成立于九州历四二三三年，几千年来始终致力于为行业提供一流的修仙人才，如今位列万仙盟五大超品宗派之一，掌门风吟真人担任万仙盟七大常务长老，修为盖世。灵剑派坚持和平与发展的主题，门派核心价值理念是求真、求善、求种','book/c7.jpg','仙侠',37,'2019-12-04 12:12:27',1,887,0),(38,'星辰变','我吃西红柿','名孩童，天生无法修炼内功。春去秋来，时光如梭，这个孩童长大了，变成了一名青年，真正改变他的命运，是一颗流星化作的神秘晶石——流星泪。这颗流星泪在青年无所觉中，融入了青年的体内，青年他也仿佛破茧化蝶一般蜕变，而随之而来的，一切都发生了变化...','book/c8.jpg','仙侠',38,'2019-12-04 12:12:47',1,676,0),(39,'仙逆','耳根','顺为凡，逆则仙，只在心中一念间……请看耳根作品《仙逆》...','book/c9.jpg','仙侠',39,'2019-12-31 21:27:07',5,648,1),(40,'一念永恒','耳根','一念成沧海，一念化桑田。一念斩千魔，一念诛万仙。唯我念...','book/c10.jpg','仙侠',40,'2019-12-04 12:13:32',1,825,0),(41,'道君','跃千愁','一个地球神级盗墓宗师，闯入修真界的故事……桃花源里，有歌声。山外青山，白骨山。五花马，千金裘，倚天剑。应我多情，啾啾鬼鸣，美人薄嗔。天地无垠，谁家旗鼓，碧落黄泉，万古高楼。为义气争雄！为乱世争霸！...','book/c11.jpg','仙侠',41,'2019-12-04 12:13:51',1,350,0),(42,'莽荒纪','我吃西红柿','纪宁死后来到阴曹地府，经判官审前生判来世，投胎到了部族纪氏。这里，有夸父逐日……有后羿射金乌……更有为了逍遥长生，历三灾九劫，纵死无悔的无数修仙者…………纪宁也成为了一名修仙者，开始了他的修仙之路','book/c12.jpg','仙侠',42,'2019-12-04 12:14:05',1,417,0),(43,'不朽凡人','鹅是老五','在这里，拥有灵根才能修仙，所有凡根注定只是凡人。莫无忌，只有凡根，一介凡人！...','book/c13.jpg','仙侠',43,'2019-12-30 17:26:20',1,799,1),(44,'我欲封天','耳根','我若要有天不可无我若要无天不许有！...','book/c14.jpg','仙侠',44,'2019-12-04 12:15:19',1,495,0),(45,'极道天魔','滚开','妖魔横行，世人苦难。神兵魔刃，遮耀天下。手机上的一款游戏修改器，意外成了路胜脑海里的异能。也成了他在这个黑暗乱世唯一的依靠。...','book/c15.jpg','仙侠',45,'2019-12-04 12:15:50',1,388,0),(46,'都市极品医神','风会笑','五年前，废物叶辰犹如地上蠕虫般被人耻笑！但是五年后，他带着一百位上古大能回来了！吊打一切，谁与争锋！','book/d1.jpg','都市',46,'2019-12-29 21:55:55',5,114,1),(47,'最强狂兵','烈焰滔滔','一代兵王含恨离开部队，销声匿迹几年后，逆天强者强势回归都市，再度掀起血雨腥风！简单粗暴是我的行事艺术，不服就干是我的生活态度！','book/d2.jpg','都市',47,'2019-12-04 12:17:43',1,2468,0),(48,'校花的贴身高手','鱼人二代','传奇杀手回归都市，奉旨泡妞保护校花！校花闺蜜缠上我，霸道总裁赖上我，火辣警花追上我，温柔护士爱上我！可是……我是校花的贴身高手啊，你们最好离我远一点，不然大小姐又要吃醋了！...','book/d3.jpg','都市',48,'2019-12-30 17:26:32',1,1494,1),(49,'修真聊天群','圣骑士的传说','某天，宋书航意外加入了一个仙侠中二病资深患者的交流群，里面的群友们都以‘道友’相称，群名片都是各种府主、洞主、真人、天师。连群主走失的宠物犬都称为大妖犬离家出走。整天聊的是炼丹、闯秘境、炼功经验啥的。突然有一天，潜水良久的他突然发现...','book/d4.jpg','都市',49,'2019-12-31 21:28:59',2,811,1),(50,'第一序列','会说话的肘子','这次是一个新的故事。浩劫余生，终见光明,如何以平庸的资质进入到修仙者的行列，从而笑傲三界之中！...','book/d5.jpg','都市',50,'2019-12-30 17:26:25',1,129,1),(51,'极品全能学生','花都大少','一场车祸改变了我的屌丝人生。各种奇遇接连而来。考试满分，刮刮乐必中，篮球天才，游泳健将选一个？不，老子就是全能。...','book/d6.jpg','都市',51,'2019-12-04 12:17:25',1,1300,0),(52,'我的冰山总裁老婆','黄枫雨天','他是赫赫有名的战争机器，让地下世界闻风丧胆的“死神。”因一场事故回归都市，跟冰山女总裁订立有名无实的婚姻。这是一个无耻小男人，征服一个冰山女总裁跟一群美女的故事！叶雄语录：哥这么无耻，会不会下地狱？','book/d7.jpg','都市',52,'2019-12-04 12:17:57',1,905,0),(53,'逍遥兵王','暗夜行走','为兄弟，他两肋插刀，为女人，他怒为红颜，为国家，他誓死如归。一代军刀重归都市，凭着一双铁拳再战天下，一声怒喝，世界皆震，且看军中王者洛天如何携美俯众生，霸气凌天下。...','book/d8.jpg','都市',53,'2019-12-04 12:18:15',1,116,0),(54,'最强医圣','左耳思念','带着一身通天本领强势回归。会治病、会算命、会炼药、会摆阵、会炼符……“这个世界上没有我办不到的事情！...','book/d9.jpg','都市',54,'2019-12-04 12:18:28',2,565,0),(55,'都市超级医圣','断桥残雪','财法侣地，修行其实是一项非常耗钱的奢侈运动。无意中得到传说中修道，炼丹，医药学家葛洪的传承，淳朴的山里人葛东旭开始努力赚钱。当大多数人还在读高中时，他为了炼丹修行已经开始一边读书一边赚钱。当大多数人读大学还在为交女朋友的开销发愁时，他已经是一名老板...','book/d10.jpg','都市',55,'2019-12-04 12:18:45',1,89,0),(56,'妖孽仙皇在都市','傲才','紫薇仙域唯一立于仙道之巅的仙皇萧尘，重回都市，发现了很多有趣的事。曾经他丢下的垃圾功法，被一堆人奉为武道圣典。曾经哭着求着喊他师父的人...','book/d11.jpg','都市',56,'2019-12-04 12:19:03',1,729,0),(57,'极品小神医','青莲剑仙','实习生秦浩获得先祖传承，走上医道修真之路，行医治病，符咒捉鬼，闲来踩踩富二代，打打装逼脸，邻家小妹投怀，妩媚姐姐送抱，校花也来倒贴，小日子美上天','book/d12.jpg','都市',57,'2019-12-04 12:19:19',1,796,0),(58,'我的贴身校花','带玉','一个普通的高中生，跟随自称是世界顶级杀手的无耻老头子学功夫，无意间又得到一瓶丹药，让他身怀数种异能，从此开始了美妙的生活...','book/d13.jpg','都市',58,'2019-12-30 17:26:44',1,2288,1),(59,'大王饶命','会说话的肘子','高中生吕树在一场车祸中改变人生，当灵气复苏时代来袭，他要做这时代的领跑者。物竞天择，胜者为王。...','book/d14.jpg','都市',59,'2019-12-04 12:19:42',1,280,0),(60,'手术直播间','真熊初墨','一个外科的小医生，一不小心得到了系统加持，横扫医学界，妙手回春，活人无数...','book/d15.jpg','都市',60,'2019-12-30 17:26:50',1,480,1),(61,'轮回乐园','那一只蚊子','苏晓签订轮回契约，进入各个世界执行任务。他曾目睹一个世界崩灭为尘粒，也曾与被遗忘的王者持刃而战。暗鸦在低语，黑渊下巨兽咆哮。欢迎来到，轮回乐园','book/e1.jpg','游戏',61,'2019-12-31 21:27:15',2,599,1),(62,'超神机械师','齐佩甲','韩萧，《星海》骨灰级代练，被来自东（zuo）方(zhe)的神秘力量扔进穿越大军，携带玩家面板变成NPC，回到《星海》公测之前，毅然选择难度最高的机械系','book/e2.jpg','游戏',62,'2019-12-04 12:24:23',1,371,0),(63,'全职高手','蝴蝶蓝','网游荣耀中被誉为教科书级别的顶尖高手，因为种种原因遭到俱乐部的驱逐，离开职业圈的他寄身于一家网吧成了一个小小的网管，但是，拥有十年游戏经验的他...','book/e3.jpg','游戏',63,'2019-12-04 12:24:34',1,535,0),(64,'重生之最强剑神','天运老猫','重头再来，为了掌控命运，再次进入了这款“活着的游戏”。这一次，他不会在受人所制。身为200级剑王，这一世他要攀向更高峰。游戏赚钱技巧！游戏副本攻略！...','book/e4.jpg','游戏',64,'2019-12-04 12:24:42',1,599,0),(65,'我是幕后大佬','一刀斩斩斩','继承了异界老祖遗产的陆无最近很愁。因为除了府君这个虚名，老祖不但什么都没有留下，甚至原本的下属也全部背叛，瓜分了本属于他的领地。在这种情况下...','book/e5.jpg','游戏',65,'2019-12-04 12:24:55',1,219,0),(66,'超凡黎明','文抄公','穿越之后才发现，东方有灵气复苏，西方有诸神黎明。蒸汽大炮与神秘的碰撞，西方超凡的渐渐苏醒。【爆破大师】+【蒸汽专家】=【机械大师】...','book/e6.jpg','游戏',66,'2019-12-04 12:25:16',1,153,0),(67,'联盟之魔王系统','神秘的大西瓜','理科学神陈牧，目标最高学府的好学生，却被系统选中，必须成为英雄联盟的大魔王！','book/e7.jpg','游戏',67,'2019-12-04 12:25:28',1,381,0),(68,'英雄联盟之谁与争锋','乱','当中国运动员嘲笑玩玩电子游戏也能拿冠军的时候，以电子竞技为生的你要做的是用自己42码的鞋子去量一量他那清高的脸码有多长！当父母、亲戚...','book/e8.jpg','游戏',68,'2019-12-04 12:25:42',1,364,0),(69,'网游之最强','八二年自','带着一身通天本领强势回归。会治病、会算命、会炼药、会摆阵、会炼符……“这个世界上没有我办不到的事情！...','book/e9.jpg','游戏',69,'2019-12-29 22:10:26',1,18,1),(70,'文明之万界领主','飞翔de懒猫','正享受这高三最后一个暑假的准大学生罗辑，在接受了游戏好友的邀请，成为‘文明崛起’这个游戏的玩家之后，他整个人生在这一刻发生了翻天覆地的变化...','book/e10.jpg','游戏',70,'2019-12-04 12:26:07',1,422,0),(71,'游戏大神是学霸','四宝锦绣','某一天，当粉丝期盼的选手，拿了冠军，他们欢呼雀跃，可当他们突然发现这位选手出现在某科研成果的大佬的名单里面，所有人都凌乱了。...','book/e11.jpg','游戏',71,'2019-12-04 12:26:22',1,139,0),(72,'无限气运主宰','落花独立','穿越异界，成为帝国唯一继承人，日后更可继承皇位，没毛病，这很主角！只可惜时不我待，晚生了十几年，未能力挽狂澜于国破家亡之前！','book/e12.jpg','游戏',72,'2019-12-04 12:26:33',1,395,0),(73,'联盟之佣兵系统','初四兮','S5，被称之为LPL最有希望夺冠的一年。是外援横行的一年。是SKT拿到冠军最轻松的一年。是uzi出道后首次未能进入到S赛的一年。是LPL最耻辱的一年！李牧偶得佣兵系统，他誓要改变这一切！...','book/e13.jpg','游戏',73,'2019-12-30 17:27:17',1,231,1),(74,'英雄联盟之兼职主播','永远的黄昏','语老师：“你们的体育老师病了，这节课上英语课。”某同学：“老师，体育老师站在你后面呢！”苏晨：“……”A同学：“我的瑞文光速QA是我体育老师教我的。...','book/e14.jpg','游戏',74,'2019-12-04 12:27:14',1,236,0),(75,'英雄联盟之王者荣耀','知白守黑','他，是韩服最强路人王，各路强者对他心存敬畏；他，是S4世人眼中无冕之王，所有荣光加诸一身；他，是最神秘的中国天才，没有人知道他的来路...','book/e15.jpg','游戏',75,'2019-12-04 12:27:26',1,479,0),(76,'邪王追妻：废材逆天小姐','苏小暖','她，21世纪金牌杀手，却穿为苏府最无用的废柴四小姐身上。他，帝国晋王殿下，冷酷邪魅强势霸道，天赋卓绝。世人皆知她是草包废材，任意欺压凌辱','book/f1.jpg','古言',76,'2019-12-04 12:27:37',1,1049,0),(77,'医妃权倾天下','承九','她是太子的未婚妻，却在大婚前夕被赐给双腿皆废，瘫痪在床的前战神王爷……新婚夜，本该瘫痪在床的新郎将她压在身下：“本王是要杀你灭口，还是把你毒哑？','book/f2.jpg','古言',77,'2019-12-04 12:27:52',1,170,0),(78,'盛世嫡妃','凤轻','一纸诏书，一场赐婚。三无千金，废材王爷。世人皆言：绝配！喜帕下她浅笑吟吟，历经生死她只愿今生静好。喜堂上他心冷如冰，受尽羞辱，终有一日他会将天下踩于足下。他是我的夫君，何人敢欺害！我必除之！...','book/f3.jpg','古言',78,'2019-12-30 17:26:00',1,704,1),(79,'纨绔世子妃','西子情','说我嚣张？说我纨绔？说我就是一个顶着云王府嫡女的名头，打着内定太子妃的幌子，其实就是天圣皇朝第一废物？靠！懂什么叫穿越？知道什么叫装？却被哥哥爷爷逼着管家...','book/f4.jpg','古言',79,'2019-12-31 21:27:25',1,360,2),(80,'炮灰攻略','莞尔wr','意外死亡后百合得到了生存的机会，为了保持现状，她不得不穿越各式各样的剧情文中完成任务。新书：长嫡谢氏谋的，是傅家百年气运。傅侯爷谋的，是权势前程。梦里的她是被博弈输掉的废棋...','book/f5.jpg','古言',80,'2019-12-04 12:29:00',1,433,0),(81,'外室女','释家传人','穿越成古代不记名的外室女。处境艰难，前途坎坷。','book/f6.jpg','古言',81,'2019-12-30 17:26:06',1,63,1),(82,'绝世神偷：废材七小姐','夜北','她是二十四世纪的神偷，却穿越到了一个白痴废柴的身上，没爹没妈，还要看家族里那些人的脸色。白痴？废物？很好，她很快就会让这群愚蠢的地球人知道，什么叫后悔莫及','book/f7.jpg','古言',82,'2019-12-04 12:29:35',1,481,0),(83,'重生之侯府嫡女','蔓妙游离','前世，心爱之人竟与她亲妹联手设计，毁她清誉、夺她亲儿，害她受尽苦楚。再次醒来，她重生到了六年前，是母亲与哥哥过世的三年后，更是她命运的重大转折年...','book/f8.jpg','古言',83,'2019-12-04 12:29:44',1,190,0),(84,'香蜜沉沉烬如霜','电线','一颗匪夷所思的葡萄美人，一只烧焦的凤凰男，一条闪亮的美男鱼。外加一粒领衔客串的绝情丹。呃……其实，双修它是一门值得深入探讨的行为艺术...','book/f9.jpg','古言',84,'2019-12-29 22:13:37',2,253,0),(85,'特工皇妃楚乔传','潇湘冬儿','正享受这高三最后一个暑假的准大学生罗辑，在接受了游戏好友的邀请，成为‘文明崛起’这个游戏的玩家之后，他整个人生在这一刻发生了翻天覆地的变化...','book/f10.jpg','古言',85,'2019-12-04 12:30:22',1,121,0),(86,'凤凰错：替嫁弃妃','阿彩','试问女子的容颜能有多值钱？她是先帝亲点的皇后，却在颜容半毁时被一纸圣旨从后变妃。一旨双嫁，绝色的妹妹代她入宫为后，而她远嫁给那少年封王，权倾朝野，冷酷残暴的雪亲王...','book/f11.jpg','古言',86,'2019-12-04 12:30:53',1,96,0),(87,'嫡谋','面北眉南','前一世，所谓的血脉至亲告诉她，能为家族利益献身是她身为任家女子一生最大的荣耀。结果她与姐姐反目成仇，让母亲垂泪早逝，累父亲血溅箭下','book/f12.jpg','古言',87,'2019-12-04 12:31:07',1,175,0),(88,'嫡女重生记','六月浩雪','在家是小透明，嫁人后是摆设，最后葬身火海尸骨无存，这是韩玉熙上辈子的写照。重活一世，韩玉熙努力上进，只愿不再做陪衬与花瓶，然后觅得如意郎君，平安富贵过一生。可惜事与愿违...','book/f13.jpg','古言',88,'2019-12-04 12:31:24',1,766,0),(89,'妙偶天成','冬天的柳叶','甄家四姑娘争强好胜，自私虚荣，费尽心机设计和镇国公家的世子一同落了水。然后，一个呆萌吃货就在甄四姑娘落水后穿来了...','book/f14.jpg','古言',89,'2019-12-04 12:32:34',1,110,0),(90,'超级贤婿','血徒','莫名其妙的成为了沈家的上门女婿，虽然是个冒牌的，可咱也是个“贤”良淑德的好女婿，诗词歌赋样样精通人无耻则无敌！当然，同时作为皇帝老头儿的女婿，深表压力！咱作为穿越人士，无亲无故，当然要立志当个好贤婿...','book/f15.jpg','古言',90,'2019-12-04 12:33:14',1,251,0);

/*Table structure for table `bookscontent` */

DROP TABLE IF EXISTS `bookscontent`;

CREATE TABLE `bookscontent` (
  `bcid` int(11) NOT NULL AUTO_INCREMENT,
  `bcname` varchar(30) DEFAULT NULL,
  `bcchapter` varchar(30) DEFAULT NULL,
  `bccontent` varchar(20000) DEFAULT NULL,
  `bc_bnum` int(11) DEFAULT NULL,
  `chapter_num` int(11) NOT NULL,
  PRIMARY KEY (`bcid`),
  KEY `bookscontent_ibfk_1` (`bc_bnum`),
  CONSTRAINT `bookscontent_ibfk_1` FOREIGN KEY (`bc_bnum`) REFERENCES `books` (`bnum`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=141 DEFAULT CHARSET=utf8;

/*Data for the table `bookscontent` */

insert  into `bookscontent`(`bcid`,`bcname`,`bcchapter`,`bccontent`,`bc_bnum`,`chapter_num`) values (1,'大主宰','第一章 北灵院','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;烈日如炎，灼热的阳光从天空上倾洒下来，令得整片大地都是处于一片蒸腾之中，杨柳微垂，收敛着枝叶，恹恹不振。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在那一片投射着被柳树枝叶切割而开的明亮光斑的空地中，数百道身影静静盘坐，这是一群略显青涩的少年少女，而此时，他们都是面目认真的微闭着双目，鼻息间的呼吸，呈现一种极有节奏之感，而随着呼吸的吐纳，他们的周身，仿佛是有着肉眼难辨的细微光芒出现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;微风悄然的吹拂而来，衣衫飘动，倒是略显壮观。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这数百道身影前方，有着一座石台，石台上，同样是有着一道身影安静的盘坐，他双手在身前相合，十指交叉，双目紧闭，犹如是进入了某种修炼状态之中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这道身影也是少年模样，他有着一头柔软而略显散乱的黑发，尚还显得稚嫩的脸庞有点清瘦，让人看起来有着一种相当舒服的感觉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而此时，在这名少年的周身，正有着肉眼可见的光芒绽放着，在那种光芒下，仿佛是有着一股玄奥的能量，正在对着他的体内涌去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;石台下，一些少年突然悄悄的睁开眼睛，他们望着石台上那少年周身的光芒，皆是忍不住的舔舔嘴，脸庞上露出了一些羡慕钦佩之色，而后那股安静便是被他们的窃窃私语声开始打破。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“牧哥真厉害，我们都还在感应天地灵气，他就已经成功晋入灵动境了，真不愧是我们东院地届的第一人啊。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哈，那是当然，莫说东院了，我想整个北灵院同等年龄中，恐怕都没几人能和牧哥比。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一名靠前的灰衣少年似乎与石台上的少年颇为熟悉，他听得大伙的窃窃私语，不由得得意一笑，压低声音道：“牧哥可是被选拔出来参加过‘灵路’的人，我们整个北灵境中，可就牧哥一人有名额，你们应该也知道参加‘灵路’的都是些什么变态吧？当年我们这北灵境可是因为此事沸腾了好一阵的，从那里出来的人，最后基本全部都是被‘五大院’给预定了的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“五大院？”不少少年听得这对于他们而言极端耀眼的名字，都是忍不住的咽了口唾沫，眼中满是向往与火热之色，那里，算是所有少年人心中的终极梦想所在，只不过“五大院”选拔极端的严苛，能够进入其中的，莫不是天才之姿，谁若是能够进入其中，那也真正算是前途无量了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“牧哥是很厉害……不过，不过牧哥好像只参加了一年……我听别人说，牧哥是第一个‘灵路’时间未曾完结就被驱逐出来的人……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有着一名少年犹豫了一下，悄悄地说道，但旋即他又赶紧补充道：“牧哥的能力我们都知道，就算那‘灵路’中都是来自大世界各处的天才妖孽，可牧哥也绝不会逊色，这样被驱逐出来，一定是受到了不公对待！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;众多少年少女面面相觑，这事情在北灵院甚至整个北灵境也不算什么秘密，他们在对此感到遗憾的同时，又相当的好奇，他们很想知道，究竟是因为什么原因，这个出色得让同样有着几分傲气的他们都信服的牧哥，竟然会被那“灵路”主动的驱逐出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那灰衣少年撇了撇嘴，不以为然的道：“哼，肯定是那‘灵路’里有人嫉妒牧哥，这才使用手段把他逼走，不过没关系，以牧哥的能力，迟早也能够进入‘五大院’，到时候自然让人明白。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;众多少年捎了捎头，虽然他们也知道他们口中这位牧哥天赋极强，但五大院也不是这么好进入的啊，毕竟他在那“灵路”中，只是修炼了一年时间，还谈不上成功的完成修炼，这与那些从“灵路”真正出来的天才妖孽相比，应该还是要差一些的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“啪！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过就在他们说话间，一块碎木突然从石台上飞下，然后甩在那灰衣少年额头上，一道轻笑的骂声随之传来：“苏凌，你们真当我是摆设吗？信不信我告诉莫师，让你们接下来的假期都留在东院补习修炼？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;众多少年少女忙抬起头来，只见得石台上修炼中的少年已经睁开了双目，漆黑的双目犹如夜空，其中灵气十足，在其嘴角，也是噙着一抹笑容，那笑容阳光而柔和，犹如点睛之笔一般，令得少年的面目，变得有些帅气起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不得不说，这是一个挺有味道的少年郎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嘿嘿，牧尘哥别啊，好不容易放点假，我还指望着回去乐乐呢，我爹要是知道我干这么丢人的事，非打死我不可。”那灰衣少年捂着额头，嘿嘿直笑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;周围的少年少女也是哄笑出声，气氛热闹。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你也知道你爹凶狠，三月之内，你若再无法晋入灵动境，你就等着挨揍吧。”那被称为牧尘的少年摇了摇头，没好气的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“灵动境哪有这么好晋入，我又不是牧哥你这样可以随随便便参加‘灵路’的变态。”那苏凌撇了撇嘴，旋即忙止住嘴巴，这件事情虽然在整个北灵境都不算什么秘密，而且牧尘本人也对此并不避讳，但这种驱逐总归不会是什么光彩的事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;名为牧尘的少年闻言则是一笑，神情并没有太大的波动，只是微微抬起头来，目光望着那割碎着光斑的树枝，眼神略显怀念与复杂。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;灵路啊……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不知道那几个家伙现在怎么样了，应该也已经结束修炼了吧？如果这样的话，或许不久后他们便是能够进入“五大院”了吧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;还有，她……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘抿了抿嘴，脑海中掠过一道不管何时都背负着一柄暗黑长剑，有着窈窕身姿，冷漠而漂亮的容颜的黑裙少女。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;倩影跳动间，那璀璨如银河般的耀眼银发，也是随之飘舞。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是这个神秘冷漠，修炼起来让人感到疯狂的少女，在那灵路中，莫名其妙的追杀了他大半年，而那让得牧尘咬牙切齿的理由是他救了她一次。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过，在最后他被逼迫的离开时，她却是第一个毫不犹豫拔剑挡在他身前的人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想到那素来没有多少情感，有着成为祸水级别潜力的小脸在那时流露出的一丝冰冷杀意，牧尘也是忍不住的有些恍惚。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;真是怀念啊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，这不是咱们北灵境那唯一一个参加了‘灵路’的小牧哥吗？又在带人修炼啊？莫师还真是器重你呢。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而就在牧尘沉侵在那种复杂心情中时，突然有着一道略显刺耳的声音传来，他脸庞平静的抬起头来，只见得不远处突然有着十数道身影慢吞吞的走来，那为首一人，是一位面容桀骜的少年，他此时正嘴挑着草根，笑眯眯的望着牧尘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“刘彻，你们西院的人跑我们东院干什么？找揍不成？！”那之前被牧尘称为苏凌的少年见到这群人，面色却是一沉，站起身来冷笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唰！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;空地上那数百名东院的学生，也是在此时站起来，目光不善的望着这群来人，人数汇聚起来，倒是相当的有气势。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这北灵院中，分为东院与西院，两院之间经常产生各种竞争，不过总体来说，以往一直是西院强于东院，在西院面前，东院的学生也是大多避着走，可这一年来，局势却是变化了不少，而这种变化的原因，便是因为牧尘的存在。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三月之前的一场两院地届学员比试中，西院这一届排名第三的薛东，落败于牧尘之手，倒是让得东院不少学生出了一口恶气，也令得西院的嚣张气焰变弱了一些。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而眼下，这些西院的家伙，竟然跑过来挑衅牧尘，这可让得苏凌他们有些忍不了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，现在的东院真是越来越得瑟了，以为出了一个牧尘就真能跟我们西院叫板不成？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那刘彻见到东院人多势众，却是丝毫不见惧色，反而是嘴角一撇，手指指向不远处的高台，咧嘴笑道：“你们敢动手试试？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏凌他们目光投望而去，只见得在那高台上，有着数道身影，那些身影正笑眯眯的望着这边，而在见到那些有点熟悉的面孔后，苏凌等人面色都是变了一变。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是西院天届的学长们……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在北灵院中，不仅分为东西两院，而且还分为天地两届，而牧尘他们则是地届，眼下高台上的这些人，便是西院天届的学长，实力比起他们自然是要厉害许多。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而在苏凌他们面色因此变化时，那高台上的天届学长们也是居高临下的笑望着他们，彼此交谈。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那是东院的牧尘吧？现在可是我们北灵院甚至北灵境的名人呢，没想到这种年龄就晋入灵动境了，虽然只是灵动境初期，不过也有资格升入天届了，倒是厉害啊。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是还不错，东院倒是出了个人才，以后等他升上东院天届，我们西院天届怕就要有些压力了哦。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这小子据说被选中了参加‘灵路’呢，不过不知道为什么被驱逐出来了，倒是有点滑稽，第一次听说这种事情。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“莫不是选错人了，这才把他丢出来吧？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哈哈。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这群人当中，有着一名红衣女孩，她身段修长，肌肤如雪，一张美丽的瓜子脸颊看上去显得有些妩媚，她慵懒的斜靠着栏杆，狭长的美目望向空地上的对恃，然后目光停留在那名为牧尘的少年身上，似是饶有兴致。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，红绫，你似乎与这牧尘还认识吧？”有着一名天届的学长笑着道，从众人的站位来看，显然她才是这个小圈子的中心。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯，他父亲是北灵境域主之一，与我父亲也算是有些关系，小时候曾在一起玩过。”那被称为红绫的女孩漫不经心的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“据说当初他好像喜欢你来着？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;红绫狭长的美目眨了眨，她望着不远处那笔直盘坐的身躯，此时有着一道光束穿透柳树枝叶，刚好是落在少年俊逸的脸庞上，形成一圈淡淡的光弧，舒服而好看，这令得她微怔了一下，隐约的还能够记起小时候那跟着她屁股后面的小男孩，只不过那时候的他，倒是没什么引人注目的地方，她也是并没有给予过多的注意，然而谁能想到，如今这个彼此关系有些疏远的少年，却是能够成为“北灵境”中唯一一个获得参加“灵路”资格的人，当时的牧尘在这北灵境可谓是风头极盛，那种风头，直到后来他突然被驱逐出“灵路”后方才开始淡去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“小时候的事情，哪能算什么喜欢。”红绫似是不在意的一笑，不过那明亮眸子倒是多看了牧尘一眼，如今的后者随着进入北灵院，也是开始崭露头角，虽然还不至于成为北灵院第一人，但被这种优秀的人喜欢这种事传出来，于她而言还是有些面子的，即便她心中清楚其实这件事还是谣言成分居多，但这般年龄的女孩，终归是有些虚荣的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哈哈，红绫的眼光可不一般，这牧尘虽然还算不错，可还达不到让红绫动心的地步，你难道没见到连林修都失败了么？那可是咱们北灵院总榜第七的牛人呢，现在都晋入灵动境中期了，这牧尘与他比还是有点差距的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“看来我们这北灵院，能够让得红绫多看一眼的，也就柳慕白大哥了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;柳慕白这个名字一出来，就连这些天届的学长们神情都是顿了顿，显然是感受到不小的压力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;北灵院总榜第一，柳慕白，其父亲更是北灵境第一大域的域主，威名显赫。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不论从样貌还是实力或者背景来说，这都是一个在北灵院中随时能够引来一些少女发花痴的名字。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在西院的学员眼中，谁都知道那柳慕白与红绫走得微近，虽然至今为止依旧未将这朵骄傲的西院之花摘下，但想来应该只是时间问题罢了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果牧尘是顺利的通过了那“灵路”的历练，获得进入“五大院”的资格，那名气自然是能够压过柳慕白，但可惜的是，他不知道怎么回事竟然被驱逐出来了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这样一来，谁若是再将两人放在一起相比，无疑就只能令得旁人一笑了。</p>',14,1),(2,'大主宰','第二章 被踢出灵路的少年','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏凌他们望着高台上的那些西院学长，一时间气势也是弱了一些，特别是当他们见到那名为红绫的女孩时，眼神先是火热一下，然后又是缩缩头，这可是北灵院中真正的风云人物，那等追随者，就连他们东院都是有着不少。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“喂，牧哥，那是西院的红绫学姐啊，传闻你小时候喜欢她来着……”苏凌凑近牧尘，低声嘿嘿地笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘闻言，不由得白了他一眼，他小时候的确与红绫有过交集，但那种年龄，哪懂得什么叫做喜欢，只是单纯的想要找个玩伴而已，这些年随着他父亲与红绫父亲之间不和，双方关系也是愈发的疏远，这种谣言，真不知道什么时候兴起来的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘无奈的摇了摇头，然后望着那一脸挑衅的刘彻，身体之上，有着淡淡的灵力光芒散发出来，他双掌也是缓缓紧握。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有话就赶紧说吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘看了一眼不远处高台上的几人，视线只是在那道纤细的倩影上停了一下，然后收回了目光：“虽然有他们在，但我想要揍你的话，即便他们拦住了我，你也得受一顿皮肉之苦。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于西院这些家伙的骄狂，他同样是有些不喜。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那刘彻听得牧尘这话，眼神顿时一怒，不过他刚欲喝斥，却是见到牧尘嘴巴微微一抿，那一霎，少年原本显得柔和灿烂的脸庞，却是陡然间有着一股冷峻的味道散发出来，那种冷峻之下，仿佛是一种刀锋般的冰寒与凌冽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那种感觉，犹如慵懒云层之中悄然涌动的惊蛰，不经意之间，露出锋利峥嵘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那股味道，可绝对不是这种年龄的单纯少年所能够具备的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刘彻嘴中的喝斥被生生的吞了下来，他惊疑的望着此时的牧尘，面色变幻一下，对于后者的实力，他显然也是有些忌惮，上一次牧尘打败他们西院地届薛东的时候，他可正在现场呢。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，倒的确是有点张狂呢。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;高台上的那数名西院天届的学长也是听见此话，当即不由得挑挑眉，感觉面子有些不太好看，特别是这里还有着红绫在场。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;红绫也是有点讶异的望着牧尘，想来是没料到看似柔和的少年，却是会有着这般强势阳刚的一面，这与当年的那个小男孩似乎有些不一样呢。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一旁的数名西院天届学长已是忍不住的翻身跃下高台，缓缓的走过去，而见到他们走来，苏凌等人都是退后了一步，神色中满是戒备。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;红绫见状，倒并未阻拦，她毕竟是西院的人，而且她也是有点想要看看，这个曾经在她眼中显得平凡的小男孩，在褪去幼时平凡的光环后，究竟是否真的拥有着耀眼的光芒。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“童哥。”那刘彻见到这几人走来，皆是一喜，再望向牧尘他们的目光中，已是多了几分得意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘见状，眉头微微皱了一下，只是那神色间却并没有多少忌惮，虽然他们都是天届的学长，但实力也都只是处于灵动境初期，真要动起手来，也不见得他们能讨到好处。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那领先一位被称为童哥的学长点了点头，目光玩味的看向牧尘，刚欲说话，一道冷叱声，突然自那不远处传来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“童冠，你们西院天届的人跑到我们东院来欺负地届的学生，未免也太不将我东院放在眼里了吧？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突如其来的呵斥声，令得众人都是一怔，转过头来，只见得不远处十数道身影快步而来，在那当先处，是一名高挑少女，少女玄衣如墨，勾勒出曼妙有致的曲线，脸颊俏美，乌黑长发被挽成马尾，修长双腿纤细笔直，那美貌程度，倒是丝毫不弱于那红绫。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而此时，这少女俏脸微带着寒意的望着童冠等人，其身后的那些人也是有点面色不善。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是芊儿学姐。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那苏凌等人见到这玄衣少女，却是立即亢奋起来，一些少年稚嫩的脸庞都是泛红了一些，后者与那红绫被称为北灵院两朵院花，那追求者可是数不胜数，没想到今日竟然在这里遇见了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，原来是芊儿啊。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那童冠见到这玄衣少女，也是愣了愣，旋即神情有些不太自然，唐芊儿不仅在这北灵院追求者众多，而且其本身实力更是达到了灵动境中期，在这北灵院天届之中，也算是颇为优秀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那被称为唐芊儿的少女来到牧尘身旁，她蹙着纤细柳眉打量着童冠，然后看向高台上的红绫，两女毫不相让的对视，彼此眼中仿佛是有着一些火花闪现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你们来这里干什么？”唐芊儿收回目光，冷声质问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“瞧这话说得，虽然我们是西院的人，但西院也是北灵院，所以我们来这里应该也不算什么违规吧？”那童冠耸耸肩，笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唐芊儿冷哼一声，然后她伸出纤细玉手，拍了拍牧尘的肩膀，尖俏下巴一扬：“牧尘是我们东院的人，你们再敢来寻他麻烦，就别怪我不客气了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说着话时，她那乌黑马尾跳动着，英姿飒爽，颇有点大姐头的风范。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“看来你女人缘倒是挺不错的。”童冠冲着牧尘一笑，笑容有点讥讽，也有点嫉妒，他对唐芊儿也是有些念想，可却从未获得过半点好脸色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“女人缘也是实力的一种啊。”牧尘笑了笑，仿佛并未听出那童冠言语间的嘲讽，与灵路之中的那些家伙相比，这童冠着实是显得嫩了一些。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那高台上的红绫看了牧尘一眼，心头略感失望，缓缓的收回目光，还是跟以往一样的怯弱与懒散吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“算了，今天我们也不是来找你麻烦的，只是来告诉你一件事。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;见到挑不起牧尘的怒火，童冠也是倍感无趣，然后对着刘彻挥了挥手，后者一声冷笑，开口道：“牧尘，我是代柳阳大哥来给你传个话，十天之后，两院比试，他会挑选你作为他的对手。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“柳阳？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;听得这个名字，苏凌他们面色都是变了一下，这柳阳可是西院地届真正的第一人，据说也是在不久前突破到了灵动境。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦，对了，再告诉你一个事，三天前柳阳大哥在测验的时候，发现他竟然身怀灵脉，虽然只是人级……”刘彻幸灾乐祸的望着牧尘，笑吟吟的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“人级灵脉？！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此话一出，不仅苏凌他们面色剧变，就连那唐芊儿都是微微动容，整个北灵院中，拥有灵脉的学员，绝不会超过一手之数，没想到那柳阳竟然也有。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“人级灵脉啊……”童冠咂了咂嘴，旋即怜悯的看着牧尘，柳阳也是灵动境初期的实力，但若再加上人级灵脉的话，恐怕就连灵动境中期的人都难以将其击败，这牧尘看来是要倒霉了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“柳阳大哥说，如果你不敢接受的话也没关系，只要那一天别出现就行了，他也不会为难你。”刘彻怪笑道，若是牧尘那一天真的不出现的话，恐怕他在东院的名声，也得瞬间支离破碎了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你们也太欺负人了！”唐芊儿柳眉微竖，喝道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“芊儿学姐，这可怪不得我们，做人本就应该低调一点，可不要认为因为一些运气获得了‘灵路’资格，就真傲得没边了。”那刘彻撇了撇嘴，道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唐芊儿一怒，就欲上前，但却是被一只手掌握住了皓腕，她转过头，然后就见到了牧尘那稚嫩脸庞上扬起来的灿烂笑容：“嗯，回去告诉柳阳吧，我等他。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有魄力！那我们就等你到时候的出色表现了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刘彻冲着牧尘竖起大拇指，咧嘴一笑，然后就大笑着转身而去，那模样，显然已是对十天之后的比试有些迫不及待了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那刘彻，童冠等人纷纷离去，高台上的红绫也是淡淡的瞥了他们一眼，转身而去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“牧哥，你真要接受那柳阳的挑战？那家伙现在有灵脉的话，可不好对付啊。”苏凌望着离去的童冠等人，有些担忧的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“尽力而为吧。”牧尘随意的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“喂，你笨啊，他们明显是在挑衅你，这种挑战你现在完全可以不用接的！”唐芊儿恼道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“芊儿姐放心吧，我会有分寸的。”牧尘微笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唐芊儿蹙着柳眉，明眸盯着少年那还有些稚嫩的脸庞，后者的脸庞上挂着灿烂柔和的笑容，不过不知为何，她总是感觉到那漆黑的眸子深处，仿佛隐藏着什么东西一般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哼，反正到时候丢人的又不是我。”唐芊儿轻哼一声，不满的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“芊儿姐似乎很关心我的样子？”牧尘望着面前那薄恼的俏美脸颊，忍不住地笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我管你去死。”唐芊儿俏脸微红了一下，旋即白了牧尘一眼，道：“我是担心你到时候被打得半死，还丢我们东院的脸。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘笑了笑，唐芊儿父亲也是北灵境域主之一，而且与他父亲关系极好，两家素来往来颇多，因此他与唐芊儿之间关系也挺不错的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“对了，我刚听来一个消息。”唐芊儿挥手将苏凌他们遣开，然后眸子盯着牧尘：“我是从院长他们那里得来的，据说‘灵路’已经正式结束了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘的身体微微一顿，仰起头来，轻吐了一口气，总算是结束了么……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“据说这次灵路中出了不少极其变态的家伙，其中有一个更恐怖，好像拥有着万年难遇的天级灵脉，五大院为了抢夺他，可是争得头破血流。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;灵脉分天地人三种，对于修炼有着不小的裨益，一般说来，拥有灵脉的人修炼速度比起常人，都会快捷不少，而北灵院这么多年来，从未出现过地级灵脉，更别说天级灵脉，由此可见，这天级灵脉有着多么的稀罕。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唐芊儿吐了吐红润香舌，显得极为的娇俏可爱：“天级灵脉啊，我都从未见过，参加天路的果然都是一些变态，对了对了，你也参加过天路，知道那家伙是谁吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“应该是叫做……姬玄吧？”牧尘淡淡的道，一个记忆尤深的名字呢。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你认识他？”唐芊儿惊奇的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯，认识，的确是一个很厉害很厉害的人。”牧尘一笑，眼眸微垂，道：“差点把他给宰了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唐芊儿那漂亮的大眼睛顿时瞪圆了起来，有些难以置信的盯着眼前那突然间仿佛变得冷冽起来的稚嫩脸庞，呐呐的道：“真的？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“假的，这你也信啊？我只是个半途就被踢出来的家伙，哪能跟这种被五大院抢得头破血流的牛人相比？”牧尘脸庞上的冷冽瞬间烟消云散，他望着唐芊儿那震惊模样，不由得笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唐芊儿恨得咬了咬银牙，那模样仿佛恨不得咬他两口，但最终还是忍耐了下来，哼道：“你才晋入灵动境，还没修炼灵诀吧？我可是听说那柳阳已经在着手修炼了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“灵诀啊，这次回去也能修炼了，正好，我爹说让我晋入灵动境了就回去一趟。”牧尘笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯，那我先走了。”唐芊儿点点头，牧尘父亲好歹也是北灵境域主之一，应该会给牧尘准备不错的灵决，旋即她也就不再多说，挥挥玉手，乌黑马尾一甩，便是扭着蛮腰轻盈而去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“对了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唐芊儿莲步一顿，偏头看了看牧尘，想了想，道：“如果没太大把握的话，我帮你把柳阳那家伙揍一顿吧？虽然他大哥柳慕白有点麻烦……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“对我有点把握好吧？”牧尘无奈，这种办法也能想出来啊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哼，不识好人心。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唐芊儿恼道，这北灵院别人想要她帮忙她还不肯呢，偏偏这家伙总是这幅可恶模样，想到此处，她也是怒气冲冲的远去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘望着唐芊儿离去的动人倩影，也是一笑，旋即喃喃自语：“既然灵路已经结束，那么她应该也进五大院了吧？不知道是哪一院？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想到在那最后离开的时候，葱郁林间，那有着长长银发的少女，用一对深邃的看不见底的青碧色眸子静静的看着他。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我会在五大院等你，你如果不来……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女纤细小手握着那黑色的长剑，缓缓的停留在他的胸膛面前，轻轻一刺，点在他心脏处：“我帮你杀了他，但……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女凝视着他，眸子如清澈琉璃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我不喜欢容易承认失败的人，而且这一次，你可不算输，至少，我很喜欢。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;轻风拂来，扬起少女骄傲而璀璨的银色长发，那素来冰冷的白皙小脸上，浮现了一抹嫣红，虽然浅浅淡淡，但却有着惊心动魄般的美丽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所以，一定要来！</p>',14,2),(3,'大主宰','第三章 牧域','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;北灵境分九域，各由一主掌控，而这九域之间，又是彼此联合或对立，从而也是令得北灵境一直的保持了九域之况。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而除了九域之外，北灵境内还有着一个不可忽视的强大势力，那便是北灵院，不过比起彼此纷争的九域而言，北灵院却是素来中立，他们并没有争夺地盘的野心，只是固守一隅，吸纳学生，而正因为如此，反令得北灵院在北灵境地位愈发崇高。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而且，北灵院的手里，还掌握着晋入“五大院”的名额，这是最让得各域之主垂涎的东西，因为他们都很明白，虽然他们在这北灵境地位不低，可与那“五大院”相比，却着实是有些不入眼，所以，为了能够获得这些名额，只要待得家中儿女年龄一到，便是会立即的送去北灵院修行。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因此，在这北灵境中，最不能得罪的势力，便是北灵院。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧域，牧城。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当牧尘自那传送灵阵之中走出时，繁华的喧闹之声便是传入耳中，他望着牧城这繁荣景象，也是微微一笑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这牧城便是牧域的主城，而他父亲牧锋，则是这牧域之主。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“少主？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“少主，您从北灵院回来了啊？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“快去通报域主！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在传送灵阵的周围，有着牧域的守卫镇守，而当他们在见到自灵阵中走出的少年时，先是一愣，顿时大喜的围拢了过来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“麻烦大家了，我自己回去就好了。”牧尘冲着那些守卫一笑，他在这里长大，很多人都是相熟，虽然他是域主之子，但性子却是极好，所以在这牧城中也是极受欢迎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他别过那些热情的守卫，便是对着城中飞奔而去，半晌后，一座巨大的府院出现在眼中，在那府前，有着大大的“牧府”二字。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧府之前，显然是有着重兵把守，不过牧尘倒是并未理会，直接奔了进去，而那些守卫在辨认出他后，也是温和一笑，远远的行了一礼后便是不再理会。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“老爹！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘冲进府院，直奔客厅，然后他便是在那里见到两道身影，在那主位之上，是一名身着墨袍的男子，男子身体挺拔，腰杆笔直如枪，面庞坚毅，唯有着那头上一些飘出来的白发，令得他多了一点沧桑，但隐约也是能够看出来，他年轻时必然也是俊朗之人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而此人，正是牧尘的父亲，牧锋，同时也是这牧域之主。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在牧锋下手，是一名削瘦的中年男子，男子眼眶深陷，看上去有点阴翳，微抿的嘴唇，显得有些凌厉森然之气，只不过那种森然，在一见到冲进客厅的牧尘时，顿时散去了许多，眉宇之间，有着一抹温和的笑意浮现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“回来便回来了，大呼小叫的做什么。”牧锋放下手中的东西，望着进门来的少年，不由得笑骂一声，只是那眼神深处，却是有着浓浓的暖意在涌动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“域主，难得小牧回来，自然是要高兴点。”那削瘦中年男子笑着道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“还是周叔理解。”牧尘一屁股坐在椅上，冲着那削瘦男子笑嘻嘻的道，言语间也颇为的亲近，周叔名为周野，乃是他父亲的生死之交，当年伴随着父亲在这北灵境厮杀，最终帮助他成为了这牧域之主，而且也是从小看着牧尘长大，所以一直都是将牧尘看做自己晚辈，感情自然是极好。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咦？你晋入灵动境了？”牧锋笑了笑，然后目光突然顿在牧尘身上，有些惊喜的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;周野闻言，也是有些惊讶的看向牧尘，果然是察觉到后者体内有着灵气的波动散发出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“前些时候晋入的。”牧尘点点头，说起此事时倒是略显平淡，并没有太多的自得之色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“看来你这次是为了灵诀跑回来的吧。”牧锋笑道，唯有在晋入了灵动境后，才能够修炼灵决，而修炼了灵诀，方才可以真正的将灵力的威力发挥出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“十天后要跟人动手，没修炼灵决或许会有点麻烦。”牧尘撇撇嘴，见到牧锋与周野投来的疑惑目光，于是就将那柳阳的挑战简略的说了一下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这柳家的人还真没一个是好玩意。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;周野听完，眼神却是冷厉了一些，道：“这些年柳家也是越来越嚣张，虽然柳域算是北灵境第一，不过他们莫非真以为我们牧域是软柿子不成？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧锋不在意的笑了笑，道：“倒是没想到那柳阳竟然也身怀灵脉，柳家倒的确是得天独厚啊。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“一道人级灵脉而已。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;周野道，不过话这样说着，他眉头也是微微皱起，看向牧尘，道：“那柳阳也是灵动境初期的实力，再加上灵脉之助，恐怕就算是灵动境中期的人都难以对付，小牧你有把握？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘笑了笑，拥有天级灵脉的人他都动过，一个人级灵脉，又算得了什么？跟灵路中的那些变态相比，柳阳还显得嫩了些。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧锋望着牧尘那有些稚嫩的面庞，他能够感觉到自己这个儿子自从灵路中回来后似乎是有些变化，虽然平日里这小家伙看上去温和，但牧锋毕竟是经历了太多杀伐的人，所以他能察觉到，在那温和稚嫩下，似乎是隐藏着一种冷冽的锐气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那种锐气，犹如潜龙蛰伏，不鸣则已，一旦爆发，必将势如雷霆。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这种变化，令得牧锋有些惊讶的同时也略感欣慰，对于这唯一的儿子，他可是极为在意的，而至于为何牧尘会被那灵路半途驱逐出来，虽然他很疑惑，但牧尘没说，他也没多问，他相信这个儿子不会做什么让他失望的事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“走吧，我带你去选灵诀。”牧锋站起身来，对着牧尘大手一挥，然后便是对着院后而去，牧尘对着周野打了一声招呼后也是迅速的跟了上去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;跟随着牧锋穿梭过后院，最后在那有着重重守卫的一扇石门之前停了下来，牧锋手掌印上，一股雄浑的灵力弥漫开来，化为一道道光线，弥漫了石门。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘站在牧锋身后，感受着那种雄浑的灵力波动，忍不住的舔了舔嘴，老爹不愧是神魄境的强者，这灵力波动，竟然强到这种地步。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修炼之中，初始为感应境，感应天地灵气，接着便是灵动境，到了这一层次，方才能够吸纳灵气入体，而灵动境之后，则是灵轮境，体内灵气凝聚成轮，那等灵力凝聚程度，也远非灵动境可比。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;过了灵轮境，便是神魄境了，这算是一个分水岭般的境界，牧锋便是处于这个层次，而一旦晋入神魄境，即便是在这北灵境中，都能够算做真正的强者，而且这个层次的强者，战斗力更是远远的超越灵轮境，因为晋入神魄境，便是能够获得一个奇特的能力，那便是炼化兽魄。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所谓兽魄，便是天地万兽的精魄，一旦将其炼化，便是能够获得一些灵兽之力，再配合本身灵力，那战斗力自然是突飞猛涨。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在天地间，有一“万兽录”，此录分天地两榜，其上记载天地无数灵兽，而牧锋所炼化的灵兽之魄，便是当年他机缘巧合之下所获，名为龙炎雕，在那万兽录地榜之上，排名第八十五，这些年牧锋纵横北灵境，成为这牧域之主，这龙炎雕灵魄，功不可没。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嘎吱。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在牧尘微微分神间，那厚重的石门，也是在此时缓缓的开启，沉重的嘎吱声音，传荡开来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;石门开启，一股尘封的气息便是扑面而来，牧锋随手一扇，而后迈步走进，牧尘也是赶紧跟上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;石门之后，是一间密室，密室中有着昏暗的火光，其中一排排的石架现入眼中，在那石架上，摆放着一卷卷的玉简，隐约有着淡淡的光芒闪烁着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘眼睛有点发亮的望着密室之中的众多玉简，显然，这些全部都是灵诀……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧锋拍了拍手，他望着密室，目光不经意的看了一眼深处，旋即淡淡地笑道：“你老爹所有存货都在这里，随便选吧，我倒是要看看你能选中什么。”</p>',14,3),(4,'大主宰','第四章 大浮屠诀','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘黑色眸子亮晶晶的望着眼前的石室，牧锋作为牧域之主，在这北灵境也算是有数的强者，这些年来的存货自然也不会弱。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而对于自己老爹，牧尘也没半点客气，快步走进石室，然后便是随手拿起一卷玉简，目光一瞟，玉简之上，便是有着光芒字体浮现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;凡级中品灵诀，火云功。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘眨了眨眼，灵诀一般说来，繁琐的分为功法灵诀，攻击灵诀，防御灵诀等等，而这些各类的灵诀，又大致分为三等，神级，灵级，凡级，每一等又分为上中下三品，眼下这火云功，便是一卷凡级中品的功法灵诀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘把玩了一下这玉简便是将其放下，显然是兴趣不大，而后漫步走进深处，偶尔拿起一卷，不过紧接着又是放了回去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧锋便是慢吞吞的跟在牧尘身后，任由他挑选着这里的诸多灵诀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;百剑灵诀，凡级上品。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;破山典，凡级上品。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;琳琅满目的灵诀充斥着牧尘的眼球，虽说大多都是凡级的灵诀，不过牧尘也明白，这些灵诀若是拿出去，也足以引起不少人的哄抢，牧锋的收藏，在这北灵境可不算普通。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘的步伐，在游走了好半晌后，也终于是抵达了石室的深处，但却依旧未能见到心仪的灵诀，而后他抬起头，望向了最后的石架，那里，有着摆放着三个打开的玉盒。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你小子倒是野心不小，这三卷灵诀可是你老爹拼了性命得来的。”牧锋见到牧尘目光投向那三个玉盒，不由得笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘闻言也是有点好奇的走上，然后自玉盒中取出一卷玉简，这玉简通体晶莹，摸上去还有着一些温度，显然不是凡品。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“炎龙法……”牧尘扫过玉简之上，眼中掠过一抹惊讶之色：“竟然是灵级下品的灵诀？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他可是很清楚灵级灵诀所拥有的价值，若是拿去拍卖的话，没个百万灵币恐怕是别想的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯，我所修炼的便是这炎龙法，是我当年获得炎龙雕灵魄时所得。”牧锋点了点头，道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘把玩了一下这卷玉简，接着又是将另外两卷都是取了出来，这两卷灵诀，一卷名为“震天诀”，一卷名为“聚灵鉴”，都是处于灵级下品的品阶。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘对这三卷玉简有些爱不释手，一时间也是有点犹豫该做何种选择。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“选好了吗？这三卷灵诀都是不相上下，你可以取一修炼，日后若是再有更适合的灵诀，转修便可。”牧锋在一旁淡笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘犹豫着，手掌在三卷玉简上漂浮着，最后停留在那“聚灵鉴”之上，这卷灵诀虽然不擅长攻击，但对于打好底子却是有着不小的功效。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘手掌悬浮在那“聚灵鉴”之上，然而就在他准备做出选择时，心中突然不知为何微微触动了一下，而后眼神不自觉的漂移了一点，再接着，在那三个玉盒的阴影处，他看见了一块布满着尘灰的暗黑色玉简。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是什么？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘怔了一下，手掌伸出，在那暗黑色的玉简抓在了手中，目光扫过，只见得那粗糙的玉简表面，有着模糊的字迹浮现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“大浮屠诀？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘望着这模糊的四个字，眼中掠过一抹疑惑，这上面竟然没写明灵诀的品阶？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘疑惑的看向牧锋，却是愣了愣，此时的后者，正面色有些复杂的望着他手中的暗黑色玉简，那神色中充满着温柔的怀念。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“老爹？”牧尘喊了一声，扬了扬手中的玉简：“这是什么灵诀啊？怎么没写明品阶啊？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“一卷普通灵诀罢了，你选其他的吧。”牧锋收回目光，缓缓的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘皱了皱眉头，他手掌磨挲着这黑色玉简，沉默了一下，笑道：“我要它吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧锋身体顿了顿，他盯着牧锋，而后者也是与其对视着，少年稚嫩的脸庞，在此时却是有着一些倔强。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你真要选择它？”牧锋沉默了片刻，道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘点了点头，道：“不知道为什么，总感觉若是不选择它的话，我会后悔的，老爹，这灵诀到底是什么来路啊？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧锋眼神复杂的看着那玉简，好半晌后方才长长的吐了一口气，苦笑一声，用仅有自己能够听见的声音喃喃道：“静，真不愧是你的儿子啊。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这卷玉简，是你娘留下来的，准确的说，应该是留给你的，不过她也说过，你若是没选择到它，那就让它一直的尘封下去。”牧锋轻声道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“娘？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘身体忍不住的颤抖了一下，嘴中喃喃着这一个有些陌生但却心神微颤的字眼，他似乎从未见过他的母亲，只是隐隐的，在那内心深处有着一道模糊但却格外深刻的温柔影子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自从他懂事以来，也并没有纠缠着牧锋询问关于母亲的消息，而牧锋也同样并没有提及过，两父子间，有时候仿佛都是选择回避了那对于他们而言都至关重要的人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“娘应该还在的吧？她……在哪里？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘磨挲着玉简，犹豫了片刻，终于是忍不住的问出了这些年心中最想要知道的事，幼时的他，曾经雕刻过许多的木雕，那些木雕完全相同，只不过却没有清晰的面目，因为那木雕的原形，便是他内心深处那一道若隐若现的温柔影子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;每一个木雕，都是一丝寄托与思念。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你娘的事情，很复杂，现在告诉你也于事无补，不过你如果真的想要知道的话，那就修炼它吧，当你把它修炼到某个层次时，自然会知晓。”牧锋沉默了许久，最后双掌缓缓紧握，他目光炯炯的盯着牧尘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“娘的离开，是不是跟我有关系？”牧尘突然问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你一直都是她最牵挂的人，为了你，她可以放弃一切。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧锋并没有直接的回答，只是大手轻轻的揉了揉牧尘的脑袋，自嘲的道：“是你爹没本事，不能让你们母子在一起。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我曾经努力过，不过……还是失败了，抱歉。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘默默点头，稚嫩的脸庞上扬起一抹灿烂的笑容：“爹想要再见到娘吧？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“很想，很想啊，真想一家团聚啊。”牧锋抬起头，喃喃的低声中，同样是有着无尽的思念。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘手掌紧紧的握拢，粗糙的暗黑色玉简在其掌心散发着温暖的温度，许久后，他抬头冲着牧锋微微一笑：“我选择它了，老爹，放心吧，你没做到的事，我来帮你完成，你若是信我的话，终有一天，我会让我们一家团聚，谁也阻拦不得！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧锋怔怔的望着那面庞稚嫩，眼神坚定的少年，一股酸楚之意荡漾在他的胸膛，这令得他眼睛都是泛红了一些，最后重重点头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;静，我们的儿子，可不会平凡的。</p>',14,4),(5,'大主宰','第五章 大千世界','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;阳光照耀进有着茂密树枝延伸的庭院中，形成一道道光束，光束之中，细微的光点随之飞舞，令得整个庭院都显得通透无比。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘盘坐在石椅上，一手托着脸庞，一手把玩着那粗糙的暗黑色玉简，他最终选择了这“大浮屠诀”，也为了他给予自己父亲的那个承喏。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过对于这“大浮屠诀”，就连牧锋也不太清楚它究竟是什么品阶，只是模糊的说明这东西似乎极为的厉害，至于为什么会很厉害，他给的理由是，因为你娘很厉害很厉害……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;娘很厉害很厉害？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘眨了眨眼睛，牧锋能够白手起家的在这北灵境中打拼出这辽阔的牧域，成为北灵境有名的强者，其实说起来也算是挺不错的了，而那连他都说很厉害的话，那娘该是到什么程度了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“娘那么厉害的话，怎么会看上老爹？”牧尘咧咧嘴，看来老爹年轻的时候，也与娘有过一场美好的邂逅啊，那应该会是一个不错的故事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘整理了一些纷乱的心绪，而后便是将目光投向手中的暗黑色玉简，十天后他便是要与柳阳交手，那家伙同样是灵动境初期的实力，并且还拥有着人级灵脉，虽然在灵路之中他同样遇见过拥有比柳阳更强的灵脉的变态，但灵路毕竟是有些奇特。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所谓灵路，其实便是一个庞大无比的试炼场，灵路的创建者，便是“五大院”，每隔三年，灵路便是会开启，而至于灵路名额的选拔也是颇为的奇特，这并非是人为选择，而是由一件名为“审判之镜”的神器探测而出，这种探测千奇百怪，但能够被这神器挑选而中的人，大多都是天赋绝佳之人，而一般说来，只要能够顺利的通过灵路的历练，便是能够顺利的晋入“五大院”修行。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;灵路是由一片位面形成，在那片奇特的位面中，没有人能够动用任何能量，也就是说，在那灵路种，不论是灵气，还是那些从其他位面传来的斗气，元力等等，都是无法的动用。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;进入灵路的挑选者，会面临各种各样的生死险境以及残酷历练，在无法动用灵力的情况下，他们只能够凭借着单纯的肉体以及自己的敏锐以及智慧来化解各种危险，这种将人逼入死境的方式，倒的确是一种磨练人意志的好方法。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在进入灵路时，这些挑选者便是被告知，想要追求至高的力量，那就必须拥有着任何事物都无法动摇的意志，唯有掌控了这种意志，方才能够成为真正的强者。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;万千年以来，大多“五大院”中出来的顶尖强者，十之七八，都是曾经参加过灵路的试炼，由此可见，这灵路的历练有着多么的重要，这也是为什么那么多天才对此趋之若鹜的主要原因。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“五大院……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘手掌微微紧握，那个地方，他可是一定要去的，不仅仅是为了答应了她，而且，只有从“五大院”出来，他方才拥有着在那外面世界立足的资格。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于外面的世界，牧尘知晓得并不多，但偶尔也是能够从牧锋那里知道一些，他所处的这个大千世界，浩瀚无尽，万族林立，北灵境在其中就犹如海洋中的沙粒，微不足道，而他们所处的这片大千世界，又联通着无数的位面，不过那些位面，都是只能称为下位面，因为这里的世界似乎比那些位面更为的高等，这大千世界，正是那无数位面的中心。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过虽然号称下位面，但能够从下位面来到大千世界的人，却几乎个个都拥有着惊才绝艳的天赋，那必然是每一个位面之中最顶尖的强者，不然的话，他们不可能突破位面的桎梏。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘有一次曾经在北灵院的高层会议中偷听到过一些有关那些自下位面而来的牛人消息，好像其中最为出名的，是两个人，他们的名字牧尘并不知晓，只是听说过他们的称谓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;炎帝。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;武祖。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很霸气的称谓，虽然他们来自下位面，但按照牧尘的猜测，恐怕即便是在这大千世界中，他们也算是真正的至尊，那位武祖，曾在这大千世界掀起过天大波澜，据说他单枪匹马的冲进了在这大千世界算做庞然大物的冰灵族之内，而冰灵族倾族之力，竟是无法阻挡那手持雷杖的男人，那一战，惊天动地，此事传出，大千世界为之震动，而且传闻他这般惊天之举，只是为了救回他所爱之人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那位潇洒的炎帝倒是相比而言比较低调，但传言他也曾经去过火灵族，而火灵族请出他们那位活了数万载的老祖出来，竟然都未能在这位炎帝手中取得上风，让得后者飘然而去，这倒是将那火灵族举族上下震得目瞪口呆。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这些来自下位面的至强者，即便是来到了这大千世界，依然风骚如旧，霸气尽显。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那外面的世界，方才是真正的精彩纷纭，令人心神荡漾。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只不过，牧尘也明白，现在他可还达不到那种层次，但是他相信，只要给予他足够的时间，他一定可以。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘望着手中的暗黑色玉简，稚嫩的脸庞上，扬起一抹笑容，那么，就从它开始吧……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘双手轻合，双目也是缓缓的闭上，掌心之中的暗黑色玉简，有着淡淡的温暖光芒散发出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;视线黑暗，牧尘心神一动，催动着体内的灵气顺着手臂经脉，最后灌注进入手中的玉简之内。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嗡嗡。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着灵气的灌注，那玉简顿时发出细微的嗡鸣之声，而后黑暗开始被撕裂，仿佛是有着什么东西，顺着手掌，钻进了牧尘身体之中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“以身化浮屠，证灵之大道……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;玄奥而晦涩的口诀，犹如古老的钟吟之声一般，悄然的在牧尘脑海之中回荡，而他也是连忙紧守心神，将这些修炼口诀，牢牢的记在心中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;钟吟之声，逐渐的散去，那修炼口诀之中蕴含了太多的东西，大多数都是如今的牧尘无法触及，所以他明智的只是选择暂时接收一些他所能够承受并且理解的部分，不过即便是如此，那口诀的晦涩程度依旧令得他脑子有些微疼，他可还从没见过如此玄奥复杂的口诀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只是牧尘显然也是有着惊人的天赋，不然也不会获得那灵路资格，他沉侵在那口诀的玄奥之中，感悟着其中的奥妙，许久后，终于开始催动灵气，按照那修炼口诀之中的经脉路线运转，而那一条条经脉路线也是颇为的怪异，若是能够按图索骥的话，则是能够发现，那些经脉路线，仿佛是在体内形成了一座奇异的塔形之状。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;灵气顺着那些怪异的经脉路线悄然的运转，这种第一次的运转，显然是极端的消耗精力，不过牧尘却是强忍着那一丝疲倦，努力的维持着灵气运转的完善。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这种古怪的经脉路线，运转起来极为的艰难，即便是牧尘竭力控制，但依旧是不断的失败着，不过好在他对此没有丝毫的骄躁之气，依旧是保持着心静状态，一次又一次的运转着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这种过程，持续了整整一下午的时间，在经历了不知道多少次的失败后，一道灵气，终于是在牧尘小心翼翼的掌控下，自那最后一道经脉路线之中成功的钻出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而当灵气自那最后一条经脉中钻出来后，原本晶莹的灵气，顿时开始变化，其色泽逐渐的变得幽黑，一种深邃之色，散发出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这时候的灵气，方才能够称为真正的灵力，而且，这一道幽黑灵力，虽然看上去幽静内敛，但牧尘却是能够隐约的感觉到，在那种幽静之下，似乎隐藏着一种令人心颤的凌厉与霸道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;显然，这种由大浮屠诀修炼出来的灵力，质量可是相当之高，绝对不是寻常灵诀修炼出来的灵力可以媲美！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;娘留下来的东西，果然厉害！牧尘满身大汗，但又有些兴奋的暗自想道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一道幽黑的灵力，被牧尘收入气海之中，然后安静盘踞，待得日后他坚持修炼，这种灵力，也将会越来越庞大。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嗡！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而就在那道幽黑灵力盘踞气海的瞬间，牧尘身体突然猛的一震，脑海之中也是传来了一阵眩晕，隐隐约约的，仿佛是有着什么东西在他的身体最深处震动共鸣了起来，一种极为奇妙的感觉，自牧尘心中升起，然后很快的又是消散而去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“怎么回事？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘很快的回过神来，心中升起一些惊疑不定，急忙探测体内，却并未发现任何不对的地方，先前的感觉，犹如是错觉一般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但心神坚定的牧尘却并不认为那真的是错觉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是因为大浮屠诀？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘心思急转，最后看向了气海之中的幽黑灵力，沉思许久，却并没有寻找到半点的端倪，因此他只能逐渐的放弃，这大浮屠诀是他娘亲留给他的，虽然他从来都未曾见过她，可他却是坚信一个道理，娘绝对不会留给他什么对他不好的东西！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“还是因为这个？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘突然心神一动，只见得那气海深处，一道黑芒闪掠而来，最后悬浮在那幽黑灵力之上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黑芒闪现，最后现出原形，那竟然是一页薄薄的黑纸，黑纸之上，没有任何的字迹，只是隐约能够见到一些极端模糊的奇特纹路。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黑纸静静的悬浮在气海之内，任由那一道幽黑灵力将其温养，那种古朴之中，透着一种无法言明的神秘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘望着这一页神秘黑纸，也是有些出神，这东西是在他在灵路之中偶然所得，不过自从得来之后，就没见过它有任何的作用，但隐隐的，牧尘又是能够感觉到这神秘黑纸，必然不会寻常之物，只不过现在的他，并没有实力解开它所拥有的秘密罢了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;先前那种莫名的共鸣震动，是因为它吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘思索了片刻，却是缓缓摇头，这黑纸虽然神秘，但先前的动静，显然不是它引起的……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苦思无果，牧尘最终还是只能放弃，将这事压下，再度盯着那盘踞在气海中的幽黑灵力，按照那口诀之中所说，这大浮屠诀应该分为三个境界，一为筑基，二为凝形，三为化塔。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只不过现在他显然连筑基都还没达到，只能算是最初步的入门而已，想要晋入这筑基境界，还需要不小的苦修才行。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;庭院之中，牧尘紧闭的双目缓缓的睁开，他望着自己那浑身的汗水，也是忍不住的摇了摇头，这大浮屠诀的确难以修炼，这才只是刚刚入门而已，竟然便是艰难到这种程度，真不知道将其修炼到所谓“化塔”境界，又该会多么的困难。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不知道这大浮屠诀的灵力，能够达到多少层振幅？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牧尘心头微动，忍不住的有些好奇，灵诀还有着一个重要的作用，便是能够振幅增强灵力，一般说来，凡级上品的功法灵诀，应该能够使得灵力振幅五层，灵级下品的灵诀，能够振幅十层，这种振幅层次越深，那么灵力爆发时的威力，便越加的厉害。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而一般也是能够用这种办法，来大致的分辨一卷灵诀的品阶如何。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想到此处，牧尘眼中的好奇愈发的浓重，只见得其手掌一旋，一道黑色灵气，便是在其掌心浮现，而后他手掌缓缓的握拢，眼神逐渐的火热起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他要来试试，这大浮屠诀，究竟会有多厉害！</p>',14,5),(6,'都市极品医神','第一章 滚吧！ 我不想杀人！','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江城高铁站，夏日炎炎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;熙攘的人群中出现了一道消瘦的身影，引入注目。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身影的主人是一个青年，青年穿着一件洗的几乎发白的军绿T恤，戴着一顶鸭舌帽，身上斜跨着一个老式帆布包。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他手里捏着一张身份证，身份证上显示青年叫叶辰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰这一身打扮和周围的人实在有些格格不入。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江城虽然不是一线城市，但也在二线城市的前列，所以哪怕是外来务工人员来这里也会稍微注意些形象，但是叶辰的打扮实在有些寒碜.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在这时，叶辰身边经过了一位高挑美女，美女扫了一眼叶辰的装扮，眉头不禁一颦。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是这一颦，竟然让叶辰看出了这位美女福德宫发黑，这是典型的血光冲灾之势。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;换句话说，今天她必有血光之灾！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“美女，我看你今日有血光之灾呀，不如流点血冲灾，以防后患。”叶辰好心提醒道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;谁料，此话一出，那高挑美女脸蛋变得通红，更是紧张的翻开手提包，当发现护舒宝带了才长吁一口气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随后，美女恶狠狠的瞪了一眼叶辰，更是呸了一声，骂道：“流氓！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;流氓？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰有些懵了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自己好心提醒，到头来怎么还被骂成流氓呢？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是什么世道啊！难道他不在的五年，女人都变成这样了吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰无奈的摇摇头，向着高铁站外走去，但是当整个江城最高的大厦浮现在叶辰面前时，他的脚步突然停下了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他抬起头，眸子如鹰一般锐利，嘴里更是喃喃道：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“江城！我叶辰终于回来了！五年前，云湖山庄那场宴会，让我叶家满门被灭，只留我一人苟活于世，如果不是母亲用最后一丝力气把我推入东钱湖，或许江城再无叶家！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说到这里，叶辰双拳突然攥紧，一股强大的煞气向着四面八方扩散而去，周围的游客更是感觉到一只无形的手仿佛扼住了他们的咽喉！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刹那之间，便是地狱！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“赵成豪！万强！周政龙！还有那些冷眼看着我父母被杀的人！你们一定不知道我叶辰还活着吧！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“或许我还要感谢你们，如果不是你们，我也不会被师傅救下，更不会带着一身通天地泣鬼神的术法回来！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这一次，我发誓，和那件事有关的任何人，我都要让你们千倍偿还！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;数秒之后，叶辰终于松开了手，招了一辆出租车，向着市中心而去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果再晚几秒钟，可能身后的路人都不能幸免。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一路上，往事不断浮现，叶辰痛的几乎要窒息。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他本来生在一个大家族，衣食无忧，虽然总被圈子里的人叫做废物，但一家三口也算其乐融融。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但就是五年前云湖山庄的那场聚会！他的命运轨迹彻底改变！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他的父亲为了救下一个小女孩得罪了一个来自京城的男人！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;男人勃然大怒！一巴掌拍在父亲的脸上！更是当着无数人的面亲手杀了他的父亲！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;整个过程，没有一个人站出来制止！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;包括那些曾经讨好叶家的家族！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;包括那些父亲昔日的好友！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;甚至还包括他一直信任的叔叔！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他疯狂，他挣扎，他当时更是拿起一把餐刀向着那个男人冲去！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是最终却被那个男人一只手拦住了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他清晰的记得那个男人淡漠的眼神，以及冰冷的话语：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“江城叶家？算什么东西？哪怕你是京城的家族，老子也能一手覆灭！还有你这个不自量力的小东西，听说你是这里出了名的废物？呵呵，还想杀我？就算给你一百年，也改不了你是废物的事实！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;让所有人意外的是，一个女人在这个时候突然冲了过来，用尽她全身的力气，一把将少年推到了东钱湖！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;坠湖的刹那，少年亲眼看着母亲向着那个男人冲去！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;带着誓死的决心。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰本以为人生就到此结束了，却没想到他在东钱湖的下游被一个老头救下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;老头带着他踏入了与世隔绝的地方，教他无上炼丹之法，教他修炼《九天玄阳决》。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这五年，在那飘渺之地，没有人知道叶辰发生了什么。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只知道那里横空出世一个恶魔，代号嗜血狂龙！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江城华美集团。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰看了看手上的纸条，又看了看大门上的标志，确定没错的时候，才走了进去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一次，他本打算直接去京城寻找那个云湖山庄出现的男人，毕竟这五年来，他最想杀的就是这个人，对于这个人的线索，他也只知道身边的人都称呼他龙爷，来自京城，其余都是空白。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是临走的时候却被老头要求一定要先去一趟江城，找一个叫夏若雪的女孩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;老头曾经云游至江城，和夏若雪的奶奶有些爱恨纠葛，叶辰甚至怀疑，夏若雪会不会就是这老头的孙女。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三天前，老头运转天机堪破阵，发现夏若雪的百日内必有大灾，并且此灾极有可能让她香消玉殒，而叶辰的命格恰好是天道都察觉不了的紫气卧龙格，所以要破局，除了叶辰以外别无他法。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;至于如何破局，老头从未提起，只是让叶辰走一步看一步。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“站住！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，一道冰冷的声音响起，只见叶辰面前挡着两个身高足足有一米九的魁梧汉子，两人身上都穿着安保制服，皮肤黝黑，眼神犀利，手臂的肌肉盘起，极具视觉冲击力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰眉头一挑，自然不可能和这种保安动手，解释道：“你好，我找夏若雪。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其中一个保安听到夏若雪的名字，冷笑一声：“你找夏总，就凭你？呵，你有预约吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“没有。”叶辰如实道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那请问你有华美集团的工作证吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“也没有。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;听到这里，保安脸上的高傲之色越发浓郁，更是居高临下的看着叶辰，伸出手，指了指大门，不屑道：“既然都没有，那是门，请你出去！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;华美集团作为华夏五百强之一，更是江城数一数二的龙头企业，作为华美集团的保安，自然有种高人一等的感觉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他们不需要看任何人脸色，只需要将这种闲杂人等驱逐就行！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在这时，几个西装革履的男人从一辆奔驰E200的车上走了下来，先是走到前台询问了一翻，似乎也是找夏若雪，得到的结果自然和叶辰一模一样，几人摇摇头，考虑了几秒，最终选择在一旁的沙发等待。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰也不打算和面前的两个保安牵扯，指了指大厅的沙发道：“那这样，我在那里等一会吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说完，他便径直向着沙发走去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是还没走几步，又再次被那两个保安挡住了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“小子，你特妈聋了是吧，别逼老子说第三遍，那是门，滚出去！”一个保安指着大门气势汹汹的喊道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰眉头紧皱，他刚来江城，好像也没得罪这两个家伙，至于这么一直刁难吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随后，他又指了指那几个西装男，询问道：“为什么他们可以坐着等，我却不能？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其中一个保安从头到脚的扫视了一番叶辰，讥讽道：“就你这种乡巴佬还想见夏总？也不撒泡尿照照镜子，滚吧，如果再不滚，信不信我特妈把你丢出去。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰无奈的摇摇头，他算是明白了，这两个保安狗仗人势，很明显，自己这一身穿着根本不配坐着等！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;没想到，五年过去，江城还是那个江城！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“如果我不想走呢？”叶辰脸色沉了下来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不走？我看你是找死！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;话语落下，其中一个保安更是一只手拍在了叶辰的肩膀之上，手臂青筋暴起！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他的嘴角露出了残忍的笑容，因为只要他轻轻发力，眼前这个瘦弱的小子必然飞出三米之外！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在前几天，同样有个不识相的乡巴佬挑战他的权威，结果直接被他一只手丢了出去，现在还在医院躺着！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那个保安手臂骤然发力，但是叶辰纹丝未动！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;渐渐的，保安的笑容凝固了！脸上更是变的惊恐了起来！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为他发现眼前的青年就像是一座山峦一般！根本抬不动！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，一股死亡之感沿着手臂向着他的后背蔓延！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他的全身湿透了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另一个保安察觉到了同伴有些不对劲，笑道：“石头，你不行啊，大白天还流这么多汗，是不是昨天晚上你老婆把你榨干了？还是我来吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说完，他的一只手也向着叶辰而去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“滚吧！我不想杀人。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，叶辰冰冷的声音响起，如滚滚惊雷！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，他的左脚微微一踏，周身竟然出现了一道气流，直接向着那两个保安撞去！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两人只感觉到一股强大的力量撞击在胸口之上，再也忍不住，吐出一口鲜血，直接飞了出去！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最后身子重重的撞在大厅的钢化玻璃之上！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“轰！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;整片玻璃轰然倒塌！发出一声巨响！</p>',46,1),(7,'都市极品医神','第二章 美女解围！','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一刻，整个大厅静如死寂。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有人都目瞪口呆的看着那个有些单薄的青年。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“尼玛，这是拍戏吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我去，这特妈是什么妖怪？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这……杀……杀人了！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那些前台的小美女更是吓的直接躲在了后面，在她们看来，集团的这两个保安已经算是可怕了，结果这个一身地摊货的青年居然更可怕！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;关键她们根本没看到这家伙出手啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰扫了一眼那两个已经昏厥的保安，无奈的摇摇头，随后便来到大厅等待区，一屁股坐在了沙发之上，甚至还顺手拿起一本杂志看了起来，完全像个没事的人一般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“对了，你们知道夏若雪什么时候下来吗？”叶辰放下杂志，突然看向身旁坐着的那几个西装革履的男子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那几个原本打算等夏若雪的合作商吓的直接从沙发上站来起来，向着外面跑了出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他们可不想死啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉，我又不伤害你们，至于吗？算了，再等一会吧。”叶辰摇了摇头，继续看着手上的杂志。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;华美集团保安部第一时间知道了这件事，迅速反应，很快就调来了十几名保安。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;十几个保安一个个全副武装，左手拿着防爆盾，右手则持着一根电棍，直接将叶辰包围了起来了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;保安队长徐强也是及时赶到，看了一眼外面的玻璃和受伤的两个保安，眉头紧皱，随后便来到了叶辰面前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;徐强是特种兵退役，当年在东南军区也算数一数二的人物，但是当看到叶辰，他本能的居然感觉到了一丝威胁。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是一种常年在生死边缘混迹的死亡直觉！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;徐强踏出一步，试探道：“这位先生，你这样在华美集团闹事，不好吧。我们已经联系了江城警方，要不了多久，武装特警就会赶过来。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰抬起头，看了一眼徐强，淡淡道：“你应该是那两个家伙的头头吧，你难道不了解一下具体情况？我只不过想坐在这里等人而已。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;徐强心里咯噔一下，那两个保安的性格他也知道，一直以来就仗着自己的身份摆威风，显然今天是踢到铁板了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是眼前这个青年怎么处理？看这个家伙有恃无恐的样子，难道集团里有人？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;徐强缓和了几分语气，问道：“这位先生，不知道你是在等谁？我可以帮你通知一下。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰喜上眉梢，连忙道：“夏若雪。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;几乎瞬间，徐强的脸色变了，如果是其他人，他还真的能通知。但是夏若雪却不一样！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她作为华美集团的总裁兼CEO，更是省上第一家族夏家的千金，江南省三花之首！岂是随随便便就能见的！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而且夏若雪说过，没有预约，她一概不见！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;眼前的青年从某种程度来说，很危险！绝不能让他见到夏总！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“先生不好意思，夏总今天不在，有什么事情你可以留下联系方式，我可以代为转告。”徐强道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰很清楚，夏若雪就在上面，这一次，他故意把动静闹大，就是为了让她出来，所以他肯定不会让徐强转告。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我下午没什么事，就在这坐坐吧，华美集团不会连坐都不让坐吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;徐强一眼看出了叶辰的意图，冷哼一声：“先生，你这是敬酒不吃吃罚酒，既然如此，那就对不住了。动手，拿下这小子！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;话语落下，十几个保安向着叶辰而去，同时，徐强变拳为爪，直取叶辰要害！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他很清楚，沙发上的男人非常危险！必须杀招！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰依旧拿着杂志，神色淡然，根本没把眼前的人当回事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在徐强要触碰到叶辰的时候，一道清冷的声音响起：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“等一下！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;听到这个声音，所有人都停下了，人群中很快就钻出了一个女子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女子一头大波浪形黑发纹理恰到好处，修长的大腿穿着黑色丝袜，显出身材的完美绝伦。白色的衬衫配合着齐膝的西裙，使她看上去如女神一样不可侵犯。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过最吸引人的视线的还是那对异常高耸的胸脯，丰挺浑圆的胸脯使她衬衫涨到了极限，但却没有丝毫下垂的样子，给人一种惊心动魄的视觉冲击。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女子的出现几乎让在场的所有人失了神！因为她的容貌在整个华美集团，除了夏如雪以外，无人可比。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;徐强脸色也是微红，更是低下头，尊敬道：“孙总监。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡没有回答，而是目光紧紧的盯着叶辰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;眼神之中有些惊讶和意外。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而叶辰同样如此！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他的双眸之中精光闪耀！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如同野兽盯上了自己的猎物一般！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为他认识眼前的这个女人！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡！那个叶辰这五年来魂牵梦萦的三个女人之一！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;多年前，两人曾做过一年的同桌，后来上了高中，由于身份悬殊，两人断绝了联系。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰本以为两人这辈子都不会有太多交集了，直到云湖山庄那件事发生，他被师傅在东钱湖救上来之后，他又去了一趟叶家。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那时候，叶家大院已经被法院查封！叶家更是成为了整个江城人人喊打的存在！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那时候，所有家族都对外宣布和叶家一刀两断，不再往来！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那时候，江城大小企业联合侵占了父亲一手创下了天正集团。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那时候，甚至连为他们一家三口立碑的人都没有!</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;几乎所有人都笼罩在京城那个男人的阴影之下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;除了一个人，那就是他的初中同学孙怡！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡不顾家人反对，去江城第一医院的太平间要来了叶家夫妇的尸体，亲自去火葬场火化，更是在天砀山亲手为叶家三口立了墓碑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一个手无缚鸡之力的女人，一个和叶家没有半点关系的女人，就这样忍住流言和恐惧，让叶辰父母的亡魂得以安息。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不光所有人不理解，就连叶辰也不理解。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他叶辰何德何能遇到这么好的女孩为他付出！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不知道过了多久，孙怡笑了，对着叶辰笑了，笑靥如花。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个笑容让在场所有男性既羡慕又嫉妒。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡可是华美集团无数男人心中的梦中女神啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一年下来，孙怡收到了几百封情书，但是无一例外，全都被拒绝了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;甚至这一年，她几乎没有对任何男性笑过！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是此时此刻，她居然对一个乡巴佬笑了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个乡巴佬凭什么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡饶有趣味的看着叶辰，开口道：“看来是我眼花了，我差点以为你是我以前的同学，还别说，真的很像呢。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;毕竟五年过去了，叶辰的变化太大了，一般人还真认不出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说完，孙怡还拍了拍徐强的肩膀，吩咐道：“徐队长，就别为难这位小兄弟了，他想等就让他等一会吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;徐强点点头，不再说话。</p>',46,2),(8,'都市极品医神','第三章 你算什么东西？','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;吩咐好一切，孙怡便向着外面走去，更是打开了一辆甲壳虫的驾驶座，坐了进去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在她刚打算开车的时候，副驾驶的大门突然打开，叶辰顺势坐了进来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰之所以跟了上来，是因为他发现孙怡的印堂之处有着一团黑雾，黑雾泛着些许血色！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是要出大事的前兆！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不管是为了刚才的解围还是五年前的那件事，他都不能让孙怡受半点伤害！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡看着副驾驶的叶辰，先是一惊，随后笑道：“小兄弟，你这是做什么，感谢我吗？其实之前的事情我也从别人那里了解了，是那几个保安有错在先，你没必要感谢我，对了，你为什么一定要见夏如雪？你不会也有什么美容偏方要推广吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她之所以这么说，是因为上次也有和叶辰差不多穿着的人，上门推销什么美容偏方，说是能让华美集团的美容产业更上一层楼，结果被保安扔了出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这年头，乡下赤脚医生来城里大多都是这个目的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰看了一眼孙怡，他发现这个小姑娘真的变化太大了，读书的时候他还经常嘲笑她是飞机场，记得有一次还把她气哭了，但是现在看来，这火爆的身材，傲人的胸围估摸都有D罩杯了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你上我车也不会是为了推销这个偏方吧？”孙怡发现叶辰看着自己的胸，她也没有生气，甚至还刻意的挺了挺。不知道为什么，她对眼前的这个青年有着一种莫名的好感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;或许就是因为像死去的那个老同学吧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰回过神来，自然不可能解释老头的事情，只能应付道：“这都被你发现了，我对我的偏方也很有信心，不仅能美容，还能让你的身材变得更好。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他回来，最大的倚仗不是他的修为，而是他敢与阎王抢命的医术！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他随手抛出一张驻颜丹的丹方，就足以让全世界的美容公司疯抢！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦，这么厉害呀？那小兄弟，你摸摸我的腿看看，你说我的腿还能更好看吗？”孙怡来了兴趣，将修长的美腿靠近叶辰了些。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她只不过开玩笑，却没想到叶辰的手真的伸了上来，更是在大腿上狠狠的摸了一把。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“说实话，你这腿很完美，根本不需要用我的药方。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡脸蛋瞬间红了起来，娇嗔道：“哼，看你这人很老实，没想到也和其他男人一个样！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“明明是你让我摸的呀?”叶辰摸了摸鼻子无奈道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡白了一眼叶辰，直接驱动车子，向着龙湖大酒店而去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“既然你摸了我的腿，我正好要参加一个聚会，罚你帮我挡下所有酒！对了，还没自我介绍了，我叫孙怡，华美集团的市场总监。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡一只手操控着方向盘，一只手向着叶辰伸了过来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两只手握在了一起，叶辰感觉到格外的柔软和细腻。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我叫叶……叶诚，诚实守信的诚。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;既然对方没认出自己，叶辰也不想多说什么，如果让孙怡知道他没有死，可能只有惊吓吧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰注意到，他说出名字的刹那，孙怡身躯一颤，当听到后面的解释，才长吁一口气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好巧，你和我那个老同学的名字读音一样，就是字不一样……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;二十分钟后。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;龙湖大酒店，帝王宴会厅。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡和叶辰出现的刹那，所有人的视线都不约而同的落在两人的身上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡的身材实在太火爆和耀眼了，一米七的身高，腿的比例几乎逆天，一身得体的穿着和旁边的叶辰形成鲜明的对比。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有人都没有想到华美集团的孙总监会带这样一个人来参加聚会。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡带着叶辰来到了最中间的一张玻璃圆桌坐下，已经有七八个人坐在那里了，都是男人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他们看着孙怡的眼神是火热的，甚至恨不得马上把孙怡扑到。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“孙总监，总算把你盼来，真是百闻不如一见啊，我是云生集团的创始人郭海东。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“孙总监，我叫钱涌，海涌酒店就是我家的产业……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;几个站起来介绍的男人显得格外热情，无非是想在孙怡面前表现一番自己的实力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰对这种聚会提不起兴趣，目光不断注意着周围，他要搞清楚，哪些东西会对孙怡产生威胁。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很快，宴会开始了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;坐在孙怡这一桌的男人不知道是有意还是无意，都找各种理由敬孙怡的酒，孙怡显然也是熟络这种酒桌文化，见招拆招，一圈下来，只喝了一小杯红酒。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰好几次想要帮助孙怡挡酒，但是都被拒绝了，毕竟这次的宴会，孙怡代表的整个华美集团，也不能伤了和气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;直到不远处坐在主桌的那个男人站了起来，整个宴会的气氛都变了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;男人一身定制西装，手里拿着两杯酒，脸上充斥着高傲！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡当看到这个男人走过来，整个人变得惊慌失措，就连身躯也有些颤抖。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这家伙怎么会在这里！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“孙小姐，上次你不告而别，也太不给我陈锋面子了吧，这杯赔罪酒，你是不是该喝？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋一上来，语气完全不是请求，而是命令！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;上次，他可是花了好大代价把孙怡约了出来，更是在酒里下了药！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;让他没想到的是，孙怡居然发现了酒不对劲，直接泼在了他的脸上！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这几天，这件事可在圈子里传遍了，让他很没有面子！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡有些为难，连忙道：“陈少，实在不好意思，我这次开车来的，不太好喝酒，要不我以茶代酒，向你赔罪？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋冷哼一声，将其中一杯酒一饮而尽：“别给我扯犊子！楼上我已经开好了总统套房，孙小姐喝醉的话住在上面就行了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;和孙怡同桌的几个男人自然认出了陈锋，也不说话，一副看好戏的样子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;毕竟眼前的男人可是江城陈家二少爷，谁敢得罪？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;甚至还有人催促道：“孙总监，陈少敬你酒可是给你面子啊，还不赶快喝了？再不喝，陈少发起火来就麻烦了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“对啊，孙总监，只不过是一杯酒的事情，有什么好怕的，这么多人，陈少还会把你吃了不成？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡看着陈锋递过来的酒有些进退两难，万一这酒再有问题，她今天可能真的走不出龙湖酒店了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋什么德行，整个江城的人都知道！只要是他看中的女人，哪怕是初中高中生都下的了手！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这种人渣的酒怎么能喝。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋见孙怡久久不肯接过他手中的酒，有些恼怒，彻底变脸：“臭婊子，你真以为自己是什么货色，妈的，别在老子面前装烈女，今天你就算特妈的不想喝，也得喝！就算你背后的夏如雪来了，也特妈一样！这是江城，老子的地盘！听到没有！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;气氛彻底变了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡就这样孤零零的站着，一动不动，眼眶渐渐泛红。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她出生在普通家庭，早早的放弃了学业，踏入社会，好不容易在华美集团混上了总监的位子，在外人看来已经高人一等了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是在面对眼前这些江城真正的人物之时，根本力不从心！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她太渺小，渺小到在这些人面前如一粒尘埃！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她甚至恨自己是个女人！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她缓缓抬起手，向着那杯酒而去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在外人看来，孙怡仿佛已经认命了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋的嘴角更是浮现了胜利的笑容，他甚至已经想到今天晚上孙怡在自己胯下承欢的画面。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在孙怡的手指要触碰到酒杯的刹那，一道冰冷的声音响起：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你算什么东西，敢让她喝酒？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p>',46,3),(9,'都市极品医神','第四章 捅破天才罢休！','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一刻，整个世界仿佛都安静下来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有人都没想到，在这种时候，居然还有人站出来为孙怡出头，找死不成！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋算什么东西？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他陈锋可是江城如日中天的陈家少爷！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈家虽然在商界的力量不强，但是在政界，陈家可是有两位在江城任高官啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;更为关键的是，陈家的背后还站着一位大人物！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;据说是一位古武者！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这也是陈锋肆无忌惮的原因！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有人都瞪大眼睛看着说话的叶辰，有些人甚至已经想到了这个青年惨死街头的画面。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;之前巴结陈锋的钱涌更是气愤的站了起来，指着叶辰骂道：“你这个不知死活的东西，你知道你在和谁说话吗？我看你身上的衣服加起来连一百都不到吧，就你这种乡巴佬还敢得罪陈少，信不信我他妈的一个电话，让你在江城混不下去！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另一个人附和道：“这种垃圾和他废话什么，直接拖出去！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在这边乱糟糟的时候，陈锋笑了，笑的几乎癫狂。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哈哈，小子，你很好！你是这么多年来第一个敢和我这么说话的，想玩英雄救美，你觉得你够格吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋一步一步向着叶辰靠近，眼神透着一丝杀意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然这是一个法制社会，不能杀人，但是在江城，他陈家就特妈是法！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡脸色惨白，她没有想到事情会这么发展！甚至还把刚认识的叶诚牵扯进来！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果早知道陈锋会在这，打死她也不来！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;眼下她开始担忧叶辰的处境，毕竟这些人可都不善茬，说不定真会闹出人命。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她再也顾不上其它，直接夺过陈锋手中的酒杯，更是道歉道：“陈少，对不起，实在对不起，我的朋友没见过世面，不是故意顶撞你，这酒我喝，喝完以后你想要我这么样就怎么样，但是能不能请你放过我的朋友。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她的声音颤抖，甚至带着一丝祈求。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋注意到整个宴会厅的客人都看向了这边，眼神瞬间变得凶残起来：“臭婊子，你有什么资格和我谈条件，你特妈自身都难保了，还想救别人，今天老子晚上不干死你这个臭——”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“啪——”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;话还没说完，一声清脆的巴掌声响彻整个大厅！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋直接倒在了地上，嘴角渗出了一丝鲜血！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他居然被人扇了一巴掌！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一刻，在场所有人都懵了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一个个看向叶辰就好像见鬼一般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这厮居然不光敢骂陈家少爷，居然连巴掌都敢扇！真不要命了吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“开口闭口这个词，你妈难道没教你尊重女性吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰终于说话了，更是顺手接过了孙怡手上的酒杯，直接泼在了陈锋的身上！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“既然你这么喜欢喝酒，这杯酒就给你喝！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;原本还有些懵的的陈锋，彻底清醒，他指着叶辰怒声道：“你个杂碎，你特妈知道我是谁吗！你居然敢扇老子！还特妈泼我酒，今天我要你死！谁也保不住你！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说完，他便看向不远处的两个保镖，嘶吼道：“你们这群废物还愣着做什么，给我杀了他！出了事，有老子顶着！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两个保镖这才反应过来，抽出一根伸缩棍，直接向着叶辰而去！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两人很快就包围了叶辰，更是毫不犹豫的一棍砸下！速度快到极致！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一棍要是打到人身上，绝对会失去战斗力！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这两个保镖很清楚，陈锋受了伤，是他们的失职，如果连这小子也拿不下的话，他们就真的麻烦了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在棍子要落在叶辰身上的时候，叶辰动了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他变拳为掌，直接一掌拍在了一个保镖的身上，同时，他的身子快速转动，又是一掌拍在另一个保镖的身上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“碰！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两个人几乎同时飞了出去，更是砸在了一旁的圆桌之上！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“碰！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;圆桌的玻璃瞬间碎裂！发出一道惊天巨响！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;秒杀！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;彻彻底底的秒杀！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在众人惊骇的目光中，叶辰踏前一步，来到陈锋的面前，慢慢蹲了下来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;看着叶辰的脸庞不断放大，陈锋整个人如坠冰窖，他发现眼前的家伙根本不是人，而是魔鬼！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;敢挑战江城陈家权威的魔鬼！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他甚至看到了对方眼眸中的杀意！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仿佛只要对方想，就真的会杀他！而且是当着这么多人的面杀他！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我泼你酒，你可服？”叶辰道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“服，服，服，我服！”陈锋颤抖着声音说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰反手一巴掌甩了出去，又喝道：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我扇你巴掌，你可服？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我服！我真的知道错了，大哥……你……你究竟怎么样才能放过我……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋真的要哭了！这特妈到底是什么怪物啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰颇为满意的点了点头，直接捡起旁边的手机，站起身，随后将手机甩在了陈锋的身上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“打电话给陈正国，让他跪在我的面前，我就考虑放过你。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说完，叶辰转过身，双手负在身后！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有都没有注意到，当他转身的刹那，双眸带着怎么样的杀意！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为当年云湖山庄那场聚会，陈家家主陈正国正好在场！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他甚至清晰的记得，在父亲死后，这个男人是如何辱骂自己的父亲！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;什么！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在场所有人都怀疑自己听错了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这家伙居然让陈锋打电话给陈正国！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈正国是谁？他可是江城陈家的家主！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;整个江城，谁敢直呼陈正国的名字！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;更不用说让陈正国下跪了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一开始，大家只不过觉得这个青年狂！却没有想到这个青年简直狂的没有边际！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋也是愣住了，他本来还想着事后找机会让父亲出手，万万没想到，这家伙居然直接让他打电话给父亲！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这和找死有什么区别？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;脑残？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那个……你……你确定？”陈锋试探道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰没有回答，而是淡然自若的坐回了自己的位置，拿起筷子开始夹菜。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最终，陈锋还是爬了起来，在角落里打了那个电话。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;没有人知道陈锋在电话里说了什么，只知道打完电话的陈锋整个人都变了，眼神无比的阴毒和狰狞。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡自然注意到了这一点，她看向身旁淡然自若的叶辰，心里要多悔恨又多悔恨。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她根本就不该把这小子带来！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这家伙简直就是个神经病！非要把江城的天捅破才善罢甘休不成！</p>',46,4),(10,'都市极品医神','第五章 唉，一定要我出手吗？','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡用手肘戳了戳叶辰，低声道：“叶辰，你发什么神经，你还年轻，别把自己的命搭上啊。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“陈家比你想象的还可怕！这样吧，待会你假装去上厕所，然后想办法跑出去，越远越好。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随后，孙怡从自己的包里拿出一张银行卡和车钥匙，从下面递给叶辰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她美眸看了一圈周围，。轻声道：“这张卡你收着，里面有三十万，密码是6个1，这是我这些年所有的积蓄，还有这把车钥匙，你待会开车跑，越偏越好，记住，千万不要回头……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰整个人怔住了，他没想到，这丫头到了这个时候，居然还为别人着想。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那你呢？”叶辰转过身，双眸紧紧的盯着孙怡。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你管我干什么，我一个女孩子，他们能杀了我不成，你快走，再不走来不及了……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在这时，外面响起了一道威严的声音！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“今天，谁也别想踏出这个宴会厅半步！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈正国来了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;宴会厅的大门被推开，十几个穿着黑色西服的保镖站成两排！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这些人的眼神冰冷，充斥着杀意！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;实力很强！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很快，一个国字脸的中年男子走了进来，他的脸上写满的威严和愤怒！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他就是江城陈家家主！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈正国的身后跟着一个老者，老者弓着背，脚步轻盈，眸子扫视着全场。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰自然也注意到了这个老者，稍稍有些意外，因为对方居然是一个修炼者！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;确切的来说也不算是修炼者，对方的体内没有丹田，只有一些特殊的气流，按照老头的说法，这应该就是华夏隐藏着的古武者。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有意思，江城居然也有这种高手。”叶辰嘴角勾勒出一道弧度。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡看到陈正国来了，心里咯噔一下，嘴里念叨着：“完了，这次全完了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她已经想到了两人接下去的处境，她看了一眼身旁的叶诚，整个人差点气炸！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这家伙居然还在一个劲的吃菜！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;都这个时候了，居然还像饿死鬼投胎一般！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;你这这辈子都没吃过饭吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“叶诚啊，你到底在干嘛！陈正国来了，你怎么还有心思吃东西啊！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡几乎要哭了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰连忙夹了一块排骨给孙怡：“这排骨味道真好，你也尝一口看看，真没骗你……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡：“……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;钱涌：“……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;宴会厅所有人：“……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈锋当看到自己的父亲出现，立马冲了过去，一把眼泪一把鼻涕对陈正国哭诉道：“爸爸，就是这个家伙，他刚才扬言要杀了我，灭我陈家！你给我安排的那两个保安都被他打趴下了……对了，还有，他扇我，甚至说要让你跪下……爸，你一定要为我做主啊！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈正国扫了一眼自己的儿子，发现后者居然整张脸都肿了，这下，他的脸色彻底铁青了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这次宴会，虽然没有江城的顶级人物，但参会的也都是江城有头有脸的企业家，如果事情传出去了，他陈家不就成为江城最大的笑话了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他压制住内心的愤怒，看向所有人，以毋庸置疑的口气道：“动手的，自己站出来吧，跪下磕十个头，我陈家会考虑给你一个全尸。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这就是陈家的姿态！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有人的目光都不约而同的落在了叶辰的身上，本以为那个小子会吓的全身发抖。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;却没想到作为当事人的叶辰居然还在吃东西！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;是的！没看错！还在吃！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“卧槽，这到底哪里来的饿死鬼啊！陈家家主发话了也不理？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这不会是神经病医院跑出来的吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你们说，会不会这家伙觉得自己要死了，最后吃一顿好的？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“应该是这样吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有人看向叶辰的目光都变的同情起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈正国看了一眼叶辰的方向，冷哼一声，对身旁的几个保镖道：“把那小子带过来！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;十几个保镖瞬间冲了上去，没有任何说话，一记手刀直接斩下！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡也是感觉到耳边阵阵风声，连忙对叶辰提醒道：“小心！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在手刀快要落下的时候，叶辰终于放下了筷子，身子微微一侧，直接抓住了那只手！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你们，远远不够格。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他那冰冷的声音好像有着某种魔力，响彻在整个宴会厅。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那个被抓住手的保镖失神片刻，又清醒过来，从腰间抽出一把匕首，直接向着叶辰而去！同时对其余人说道：“点子有点扎，一起上！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刹那之间，七八只拳头向着叶辰砸了过来！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;生死只在一念之间！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉，一定要我出手吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰无奈的摇摇头，随后手臂一震！松开了那个保镖的手，只见一股淡淡的气流向着四面八方扩散而去！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随后“轰！”的一声，那个保镖的身子直接砸在了周围的同伴身上！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;短短几秒，十几个保镖，横七竖八的躺在了地上！丧失了作战能力！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一刻，整个世界都寂静了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为这种战斗，视觉冲击力太强了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一个瘦弱的青年！而且还是背对着十几个人！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只出了一次手！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;居然几秒不到，全部倒下？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;尼玛，这些保镖是特妈玩偶吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙怡彻底懵了！她的呼吸急促，胸口也是不断起伏，波澜壮阔。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她瞪大眼睛看向身旁的叶辰，完全说不出话来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这家伙不是从乡下来推销药方骗钱的？这年头，这种乡下的医生都这么凶了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“叶诚……你……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;叶辰给孙怡投过去一个信任的目光，开口道：“你说过让我挡酒。所以——从今天开始，没有人敢让你喝一杯酒！没有人！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说完，他猛的转过身，冰冷的目光直接射向陈正国！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;五年前，他是叶家废物！苟且偷生！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;五年后，他叶辰回来了！带着一身通天地泣鬼神的修为回来了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈正国也被这冰冷的眼神吓到了，更是向后退了几步，好在身后的老者扶住了他。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他总感觉这个青年有些眼熟，但是却又说不上来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“明老，这个家伙难道……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;明老点了点头，苍老的声音说道：“你猜对了，此人应该和我是一类人，不过看对方年纪，应该跨入没多久，不足为惧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陈正国听到这个消息，不禁长吁一口气，恭恭敬敬道：“既然如此，那就有劳明老出手了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于外界而言，明老只不过是陈家的管家，但是只有陈正国自己清楚，明老在陈家的地位仅此于在武夷山修行的老爷子！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;更为关键的是，明老是传说中的古武者！实力通天！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他曾经亲眼看到过明老只用一根手指，就把地面劈出一道裂痕！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;恐怖如斯！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这几年，陈家在江城的地位扶摇直上，最大的原因也是因为明老！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p>',46,5),(11,'斗破苍穹','第一章 陨落的天才','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“斗之力，三段！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着测验魔石碑上面闪亮得甚至有些刺眼的五个大字，少年面无表情，唇角有着一抹自嘲，紧握的手掌，因为大力，而导致略微尖锐的指甲深深的刺进了掌心之中，带来一阵阵钻心的疼痛…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎，斗之力，三段！级别：低级！”测验魔石碑之旁，一位中年男子，看了一眼碑上所显示出来的信息，语气漠然的将之公布了出来…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中年男子话刚刚脱口，便是不出意外的在人头汹涌的广场上带起了一阵嘲讽的骚动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“三段？嘿嘿，果然不出我所料，这个“天才”这一年又是在原地踏步！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哎，这废物真是把家族的脸都给丢光了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“要不是族长是他的父亲，这种废物，早就被驱赶出家族，任其自生自灭了，哪还有机会待在家族中白吃白喝。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉，昔年那名闻乌坦城的天才少年，如今怎么落魄成这般模样了啊？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“谁知道呢，或许做了什么亏心事，惹得神灵降怒了吧…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;周围传来的不屑嘲笑以及惋惜轻叹，落在那如木桩待在原地的少年耳中，恍如一根根利刺狠狠的扎在心脏一般，让得少年呼吸微微急促。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少年缓缓抬起头来，露出一张有些清秀的稚嫩脸庞，漆黑的眸子木然的在周围那些嘲讽的同龄人身上扫过，少年嘴角的自嘲，似乎变得更加苦涩了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这些人，都如此刻薄势力吗？或许是因为三年前他们曾经在自己面前露出过最谦卑的笑容，所以，如今想要讨还回去吧…”苦涩的一笑，萧炎落寞的转身，安静的回到了队伍的最后一排，孤单的身影，与周围的世界，有些格格不入。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个，萧媚！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;听着测验人的喊声，一名少女快速的人群中跑出，少女刚刚出场，附近的议论声便是小了许多，一双双略微火热的目光，牢牢的锁定着少女的脸颊…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女年龄不过十四左右，虽然并算不上绝色，不过那张稚气未脱的小脸，却是蕴含着淡淡的妩媚，清纯与妩媚，矛盾的集合，让得她成功的成为了全场瞩目的焦点…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女快步上前，小手轻车熟路的触摸着漆黑的魔石碑，然后缓缓闭上眼睛…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在少女闭眼片刻之后，漆黑的魔石碑之上再次亮起了光芒…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“斗之气：七段！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧媚，斗之气：七段！级别:高级！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“耶！”听着测验员所喊出的成绩，少女脸颊扬起了得意的笑容…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“啧啧，七段斗之气，真了不起，按这进度，恐怕顶多只需要三年时间，她就能称为一名真正的斗者了吧…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不愧是家族中种子级别的人物啊…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;听着人群中传来的一阵阵羡慕声，少女脸颊上的笑容更是多了几分，虚荣心，这是很多女孩都无法抗拒的诱惑…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;与平日里的几个姐妹互相笑谈着，萧媚的视线，忽然的透过周围的人群，停在了人群外的那一道孤单身影上…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;皱眉思虑了瞬间，萧媚还是打消了过去的念头，现在的两人，已经不在同一个阶层之上，以萧炎最近几年的表现，成年后，顶多只能作为家族中的下层人员，而天赋优秀的她，则将会成为家族重点培养的强者，前途可以说是不可限量。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉…”莫名的轻叹了一口气，萧媚脑中忽然浮现出三年前那意气风发的少年，四岁练气，十岁拥有九段斗之气，十一岁突破十段斗之气，成功凝聚斗之气旋，一跃成为家族百年之内最年轻的斗者！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当初的少年，自信而且潜力无可估量，不知让得多少少女对其春心荡漾，当然，这也包括以前的萧媚。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;然而天才的道路，貌似总是曲折的，三年之前，这名声望达到巅峰的天才少年，却是突兀的接受到了有生以来最残酷的打击，不仅辛辛苦苦修炼十数载方才凝聚的斗之气旋，一夜之间，化为乌有，而且体内的斗之气，也是随着时间的流逝，变得诡异的越来越少。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;斗之气消失的直接结果，便是导致其实力不断的后退。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从天才的神坛，一夜跌落到了连普通人都不如的地步，这种打击，让得少年从此失魂落魄，天才之名，也是逐渐的被不屑与嘲讽所替代。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站的越高，摔得越狠，这次的跌落，或许就再也没有爬起的机会。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个，萧薰儿！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;喧闹的人群中，测试员的声音，再次响了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着这有些清雅的名字响起，人群忽然的安静了下来，所有的视线，豁然转移。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在众人视线汇聚之处，一位身着紫色衣裙的少女，正淡雅的站立，平静的稚嫩俏脸，并未因为众人的注目而改变分毫。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女清冷淡然的气质，犹如清莲初绽，小小年纪，却已初具脱俗气质，难以想象，日后若是长大，少女将会如何的倾国倾城…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这名紫裙少女，论起美貌与气质来，比先前的萧媚，无疑还要更胜上几分，也难怪在场的众人都是这般动作。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莲步微移，名为萧薰儿的少女行到魔石碑之前，小手伸出，镶着黑金丝的紫袖滑落而下，露出一截雪白娇嫩的皓腕，然后轻触着石碑…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;微微沉静，石碑之上，刺眼的光芒再次绽放。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“斗之气：九段！级别：高级！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着石碑之上的字体，场中陷入了一阵寂静。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“…竟然到九段了，真是恐怖！家族中年轻一辈的第一人，恐怕非薰儿小姐莫属了。”寂静过后，周围的少年，都是不由自主的咽了一口唾沫，眼神充满敬畏…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;斗之气，每位斗者的必经之路，初阶斗之气分一至十段，当体内斗之气到达十段之时，便能凝聚斗之气旋，成为一名受人尊重的斗者！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;人群中，萧媚皱着浅眉盯着石碑前的紫裙少女，脸颊上闪过一抹嫉妒…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着石碑上的信息，一旁的中年测验员漠然的脸庞上竟然也是罕见的露出了一丝笑意，对着少女略微恭声道：“薰儿小姐，半年之后，你应该便能凝聚斗气之旋，如果你成功的话，那么以十四岁年龄成为一名真正的斗者，你是萧家百年内的第二人！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;是的，第二人，那位第一人，便是褪去了天才光环的萧炎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“谢谢。”少女微微点了点头，平淡的小脸并未因为他的夸奖而出现喜悦，安静的回转过身，然后在众人炽热的注目中，缓缓的行到了人群最后面的那颓废少年面前…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎哥哥。”在经过少年身旁时，少女顿下了脚步，对着萧炎恭敬的弯了弯腰，美丽的俏脸上，居然露出了让周围少女为之嫉妒的清雅笑容。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我现在还有资格让你怎么叫么?”望着面前这颗已经成长为家族中最璀璨的明珠，萧炎苦涩的道，她是在自己落魄后，极为少数还对自己依旧保持着尊敬的人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎哥哥，以前你曾经与薰儿说过，要能放下，才能拿起，提放自如，是自在人！”萧薰儿微笑着柔声道，略微稚嫩的嗓音，却是暖人心肺。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，自在人？我也只会说而已，你看我现在的模样，象自在人吗？而且…这世界，本来就不属于我。”萧炎自嘲的一笑，意兴阑珊的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;面对着萧炎的颓废，萧薰儿纤细的眉毛微微皱了皱，认真的道：“萧炎哥哥，虽然并不知道你究竟是怎么回事，不过，薰儿相信，你会重新站起来，取回属于你的荣耀与尊严…”话到此处，微顿了顿，少女白皙的俏脸，头一次露出淡淡的绯红：“当年的萧炎哥哥，的确很吸引人…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵…”面对着少女毫不掩饰的坦率话语，少年尴尬的笑了一声，可却未再说什么，人不风liu枉少年，可现在的他，实在没这资格与心情，落寞的回转过身，对着广场之外缓缓行去…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站在原地望着少年那恍如与世隔绝的孤独背影，萧薰儿踌躇了一会，然后在身后一干嫉妒的狼嚎声中，快步追了上去，与少年并肩而行…</p>',10,1),(12,'斗破苍穹','第二章 斗气大陆','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;月如银盘，漫天繁星。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山崖之颠，萧炎斜躺在草地之上，嘴中叼中一根青草，微微嚼动，任由那淡淡的苦涩在嘴中弥漫开来…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;举起有些白皙的手掌，挡在眼前，目光透过手指缝隙，遥望着天空上那轮巨大的银月。shuhaige.com</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉…”想起下午的测试，萧炎轻叹了一口气，懒懒的抽回手掌，双手枕着脑袋，眼神有些恍惚…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“十五年了呢…”低低的自喃声，忽然毫无边际的从少年嘴中轻吐了出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在萧炎的心中，有一个仅有他自己知道的秘密：他并不是这个世界的人，或者说，萧炎的灵魂，并不属于这个世界，他来自一个名叫地球的蔚蓝星球，至于为什么会来到这里，这种离奇经过，他也无法解释，不过在生活了一段时间之后，他还是后知后觉的明白了过来：他穿越了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着年龄的增长，对这块大陆，萧炎也是有了些模糊的了解…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大陆名为斗气大陆，大陆上并没有小说中常见的各系魔法，而斗气，才是大陆的唯一主调！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这片大陆上，斗气的修炼，几乎已经在无数代人的努力之下，发展到了巅峰地步，而且由于斗气的不断繁衍，最后甚至扩散到了民间之中，这也导致，斗气，与人类的日常生活，变得息息相关，如此，斗气在大陆中的重要性，更是变得无可替代！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为斗气的极端繁衍，同时也导致从这条主线中分化出了无数条斗气修炼之法，所谓手有长短，分化出来的斗气修炼之法，自然也是有强有弱。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;经过归纳统计，斗气大陆将斗气功法的等级，由高到低分为四阶十二级：天.地.玄.黄！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而每一阶，又分初，中，高三级！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修炼的斗气功法等级的高低，也是决定日后成就高低的关键，比如修炼玄阶中级功法的人，自然要比修炼黄阶高级功法的同等级的人要强上几分。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;斗气大陆，分辩强弱，取决于三种条件。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;首先，最重要的，当然是自身的实力，如果本身实力只有一星斗者级别，那就算你修炼的是天阶高级的稀世功法，那也难以战胜一名修炼黄阶功法的斗师。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其次，便是功法！同等级的强者，如果你的功法等级较之对方要高级许多，那么在比试之时，种种优势，一触既知。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最后一种，名叫斗技！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;顾名思义，这是一种发挥斗气的特殊技能，斗技在大陆之上，也有着等级之分，总的说来，同样也是分为天地玄黄四级。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;斗气大陆斗技数不胜数，不过一般流传出来的大众斗技，大多都只是黄级左右，想要获得更高深的斗技，便必须加入宗派，或者大陆上的斗气学院。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然，一些依靠奇遇所得到前人遗留而下的功法，或者有着自己相配套的斗技，这种由功法衍变而出的斗技，互相配合起来，威力要更强上一些。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;依靠这三种条件，方才能判出究竟孰强孰弱，总的说来，如果能够拥有等级偏高的斗气功法，日后的好处，不言而喻…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过高级斗气修炼功法常人很难得到，流传在普通阶层的功法，顶多只是黄阶功法，一些比较强大的家族或者中小宗派，应该有玄阶的修炼之法，比如萧炎所在的家族，最为顶层的功法，便是只有族长才有资格修炼的：狂狮怒罡，这是一种风属性，并且是玄阶中级的斗气功法。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;玄阶之上，便是地阶了，不过这种高深功法，或许便只有那些超然势力与大帝国，方才可能拥有…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;至于天阶…已经几百年未曾出现了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从理论上来说，常人想要获得高级功法，基本上是难如登天，然而事无绝对，斗气大陆地域辽阔，万族林立，大陆之北，有号称力大无穷，可与兽魂合体的蛮族，大陆之南，也有各种智商奇高的高级魔兽家族，更有那以诡异阴狠而著名的黑暗种族等等…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;由于地域的辽阔，也有很多不为人知的无名隐士，在生命走到尽头之后，性子孤僻的他们，或许会将平生所创功法隐于某处，等待有缘人取之，在斗气大陆上，流传一句话：如果某日，你摔落悬崖，掉落山洞，不要惊慌，往前走两步，或许，你，将成为强者！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此话，并不属假，大陆近千年历史中，并不泛这种依靠奇遇而成为强者的故事.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个故事所造成的后果，便是造就了大批每天等在悬崖边，准备跳崖得绝世功法的怀梦之人，当然了，这些人大多都是以断胳膊断腿归来…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;总之，这是一片充满奇迹，以及创造奇迹的大陆！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然，想要修炼斗气秘籍，至少需要成为一名真正的斗者之后，方才够资格，而现在的萧炎隔那段距离，似乎还很是遥远…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呸。”吐出嘴中的草根，萧炎忽然跳起身来，脸庞狰狞，对着夜空失态的咆哮道：“我草你奶奶的，把劳资穿过来当废物玩吗？草！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在前世，萧炎只是庸碌众生中极其平凡的一员，金钱，美人，这些东西与他根本就是两条平行线，永远没有交叉点，然而，当来到这片斗气大陆之后，萧炎却是惊喜的发现，因为两世的经验，他的灵魂，竟然比常人要强上许多！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;要知道，在斗气大陆，灵魂是天生的，或许它能随着年龄的增长而稍稍变强，可却从没有什么功法能够单独修炼灵魂，就算是天阶功法，也不可能！这是斗气大陆的常识。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;灵魂的强化，也造就出萧炎的修炼天赋，同样，也造就了他的天才之名。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当一个平凡庸碌之人，在知道他有成为无数人瞩目的本钱之后，若是没有足够的定力，很难能够把握本心，很显然的，前世仅仅是普通人的萧炎，并没有这种超人般的定力，所以，在他开始修炼斗之气后，他选择了成为受人瞩目的天才之路，而并非是在安静中逐渐成长！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是没有意外发生的话，萧炎或许还真能够顶着天才的名头越长越大，不过，很可惜，在十一岁那年，天才之名，逐渐被突如其来的变故剥夺而去，而天才，也是在一夜间，沦落成了路人口中嘲笑的废物！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在咆哮了几嗓子之后，萧炎的情绪也是缓缓的平息了下来，脸庞再次回复了平日的落寞，事与至此，不管他如何暴怒，也是挽不回辛苦修炼而来的斗之气旋。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苦涩的摇了摇头，萧炎心中其实有些委屈，毕竟他对自己身体究竟发生了什么事，也是一概不知，平日检查，却没有发现丝毫不对劲的地方，灵魂，随着年龄的增加，也是越来越强大，而且吸收斗之气的速度，比几年前最巅峰的状态还要强盛上几分，这种种条件，都说明自己的天赋从不曾减弱，可那些进入体内的斗之气，却都是无一例外的消失得干干净净，诡异的情形，让得萧炎黯然神伤…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黯然的叹了口气，萧炎抬起手掌，手指上有一颗黑色戒指，戒指很是古朴，不知是何材料所铸，其上还绘有些模糊的纹路，这是母亲临死前送给他的唯一礼物，从四岁开始，他已经佩戴了十年，母亲的遗物，让得萧炎对它也是有着一份眷恋，手指轻轻的抚mo着戒指，萧炎苦笑道：“这几年，还真是辜负母亲的期望了…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;深深的吐了一口气，萧炎忽然回转过头，对着漆黑的树林温暖的笑道：“父亲，您来了？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然斗之气只有三段，不过萧炎的灵魂感知，却是比一名五星斗者都要敏锐许多，在先前说起母亲的时候，他便察觉到了树林中的一丝动静。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，炎儿，这么晚了，怎么还待在这上面呢？”树林中，在静了片刻后，传出男子的关切笑声。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;树枝一阵摇摆，一位中年人跃了出来，脸庞上带着笑意，凝视着自己那站在月光下的儿子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中年人身着华贵的灰色衣衫，龙行虎步间颇有几分威严，脸上一对粗眉更是为其添了几分豪气，他便是萧家现任族长，同时也是萧炎的父亲，五星大斗师，萧战！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“父亲，您不也还没休息么？”望着中年男子，萧炎脸庞上的笑容更浓了一分，虽然自己有着前世的记忆，不过自出生以来，面前这位父亲便是对自己百般宠爱，在自己落魄之后，宠爱不减反增，如此行径，却是让得萧炎甘心叫他一声父亲。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“炎儿，还在想下午测验的事呢？”大步上前，萧战笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，有什么好想的，意料之中而已。”萧炎少年老成的摇了摇头，笑容却是有些勉强。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉…”望着萧炎那依旧有些稚嫩的清秀脸庞，萧战叹了一口气，沉默了片刻，忽然道：“炎儿，你十五岁了吧？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯，父亲。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“再有一年，似乎…就该进行成年仪式了…”萧战苦笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是的，父亲，还有一年！”手掌微微一紧，萧炎平静的回道，成年仪式代表什么，他自然非常清楚，只要度过了成年仪式，那么没有修炼潜力的他，便将会被取消进入斗气阁寻找斗气功法的资格，从而被分配到家族的各处产业之中，为家族打理一些普通事物，这是家族的族规，就算他的父亲是族长，那也不可能改变！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;毕竟，若是在二十五岁之前没有成为一名斗者，那将不会被家族所认可！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“对不起了，炎儿，如果在一年后你的斗之气达不到七段，那么父亲也只得忍痛把你分配到家族的产业中去，毕竟，这个家族，还并不是父亲一人说了算，那几个老家伙，可随时等着父亲犯错呢…”望着平静的萧炎，萧战有些歉疚的叹道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“父亲，我会努力的，一年后，我一定会到达七段斗之气的！”萧炎微笑着安慰道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“一年，四段？呵呵，如果是以前，或许还有可能吧，不过现在…基本没半点机会…”虽然口中在安慰着父亲，不过萧炎心中却是自嘲的苦笑了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同样非常清楚萧炎底细的萧战，也只得叹息着应了一声，他知道一年修炼四段斗之气有多困难，轻拍了拍他的脑袋，忽然笑道：“不早了，回去休息吧，明天，家族中有贵客，你可别失了礼。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“贵客？谁啊？”萧炎好奇的问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“明天就知道了.”对着萧炎挤了挤眼睛，萧战大笑而去，留下无奈的萧炎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“放心吧，父亲，我会尽力的！”抚mo着手指上的古朴戒指，萧炎抬头喃喃道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在萧炎抬头的那一刹，手指中的黑色古戒，却是忽然亮起了一抹极其微弱的诡异毫光，毫光眨眼便逝，没有引起任何人的察觉…</p>',10,2),(13,'斗破苍穹','第三章 客人','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;床榻之上，少年闭目盘腿而坐，双手在身前摆出奇异的手印，胸膛轻微起伏，一呼一吸间，形成完美的循环，而在气息循环间，有着淡淡的白色气流顺着口鼻，钻入了体内，温养着骨骼与肉体。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在少年闭目修炼之时，手指上那古朴的黑色戒指，再次诡异的微微发光，旋即沉寂…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呼…”缓缓的吐出一口浊气，少年双眼乍然睁开，一抹淡淡的白芒在漆黑的眼中闪过，那是刚刚被吸收，而又未被完全炼化的斗之气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好不容易修炼而来的斗之气，又在消失…我，我草！”沉神感应了一下体内，少年脸庞猛然的愤怒了起来，声音有些尖锐的骂道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;拳头死死的捏在了一起，半晌后，少年苦笑着摇了摇头，身心疲惫的爬下了床，舒展了一下有些发麻的脚腕与大腿，仅仅拥有三段斗之气的他，可没有能力无视各种疲累。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;简单的在房间中活动了下身体，房间外传来苍老的声音：“三少爷，族长请你去大厅！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三少爷，萧炎在家中排行老三，上面还有两位哥哥，不过他们早已经外出历练，只有年终，才会偶尔回家，总的说来，两位哥哥对萧炎这位亲弟弟，也很是不错。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦。”随口的应了下来，换了一身衣衫，萧炎走出房间，对着房外的一名青衫老者微笑道：“走吧，墨管家。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着少年稚嫩的脸庞，青衫老者和善的点了点头，转身的霎那，浑浊的老眼，掠过一抹不易察觉的惋惜，唉，以三少爷以前的天赋，恐怕早该成为一名出色的斗者了吧，可惜…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;跟着老管家从后院穿过，最后在肃穆的迎客大厅外停了下来，恭敬的敲了门，方才轻轻的推门而入。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大厅很是宽敞，其中的人数也是不少，坐于最上方的几位，便是萧战与三位脸色淡漠的老者，他们是族中的长老，权利不比族长小。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在四人的左手下方，坐着家族中一些有话语权且实力不弱的长辈，在他们的身旁，也有一些在家族中表现杰出的年轻一辈。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另外一边，坐着三位陌生人，想必他们便是昨夜萧战口中所说的贵客。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有些疑惑的目光在陌生的三人身上扫过，三人之中，有一位身穿月白衣袍的老者，老者满脸笑容，神采奕奕，一双有些细小的双眼，却是精光偶闪，萧炎的视线微微下移，最后停在了老者胸口上，心头猛然一凛，在老者的衣袍胸口处，赫然绘有一弯银色浅月，在浅月周围，还有点缀着七颗金光闪闪的星辰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“七星大斗师！这老人竟然是一位七星大斗师？真是人不可貌相！”萧炎心中大感惊异，这老者的实力，竟然比自己的父亲，还要高出两星。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;能够成为大斗师的人，至少都是名动一方的强者，那样的实力，将会让得任何势力趋之若鹜，而忽然间看见一位如此等级的强者，也难怪萧炎会感到诧异。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;老者身旁，坐有一对年轻的男女，他们的身上同样穿着相同的月白袍服，男子年龄在二十左右，英俊的相貌，配上挺拔的身材，很是具有魅力，当然，最重要的，还是其胸口处所绘的五颗金星，这代表着青年的实力：五星斗者！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;能够以二十岁左右的年龄成为一名五星斗者，这说明青年的修炼天赋，也很是不一般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;英俊的相貌，加上不俗的实力，这位青年，不仅将家族中的一些无知少女迷得神魂颠倒，就是连那坐在一旁的萧媚，美眸中在移向这边之时，也是微放着异彩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女虽然暗送秋波，不过这似乎对青年并没有什么吸引力，此时，这位青年正将所有的注意力，集中在自己身旁的美丽少女身上…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这位少女年龄和萧炎相仿，让萧炎有些意外的，她的容貌，竟然比萧媚还要美上几分，在这家族之中，恐怕也只有那犹如青莲一般的萧熏儿能够与之相比，难怪这男子对族中的这些胭脂俗粉不屑一顾。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女娇嫩的耳垂上吊有着绿色的玉坠，微微摇动间，发出清脆的玉响，突兀的现出一抹娇贵…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另外，在少女那已经开始发育的玲珑小胸脯旁，绘有三颗金星。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“三星斗者，这女孩…如果没有靠外物激发的话，那便是一个绝顶天才！”心头轻轻的吸了一口凉气，萧炎的目光却只是在少女冷艳的小脸上停留了瞬间便是移了开去，不管如何说，在他幼稚的外貌下，也是拥有一个成熟的灵魂，虽然少女很美丽，不过他也没闲心露出流口水的猪哥状来讨人嫌。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧炎的这举动似乎有些让得少女略感诧异，虽然她并不是那种以为世界围着自己转的女孩，不过自己的美貌与气质如何，她再清楚不过，萧炎的这番随意动作，倒真让她有点意外，当然，也仅此而已！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“父亲，三位长老！”快步上前，对着上位的萧战四人恭敬的行了一礼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，炎儿，来了啊，快坐下吧。”望着萧炎的到来，萧战止住了与客人的笑谈，冲着他点了点头，挥手道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;微笑点头，萧炎只当做没有看见一旁三位长老射来的不耐以及淡淡的不屑，回头在厅中扫了扫，却是愕然发现，竟然没自己的位置…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉，自己在这家族中的地位，看来还真是越来越低啊，往日倒好，现在竟然是当着客人的面给我难堪，这三个老不死的啊…”心头自嘲的一笑，萧炎暗自摇头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着站在原地不动的萧炎，周围的族中年轻人，都是忍不住的发出讥笑之声，显然很是喜欢看他出丑的模样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此时，上面的萧战也是发现了萧炎的尴尬，脸庞上闪过一抹怒气，对着身旁的老者皱眉道：“二长老，你…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咳，实在抱歉，竟然把三少爷搞忘记了，呵呵，我马上叫人准备!”被萧战瞪住的黄袍老者，淡淡的笑了笑，“自责”的拍了拍额头，只是其眼中的那抹讥讽，却并未有多少遮掩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎哥哥，坐这里吧！”少女淡淡的笑声，忽然的在大厅中响了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三位长老微愣，目光移向角落中安静的萧熏儿，嘴巴蠕了蠕，竟然是都没有敢再说话…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在大厅的角落处，萧熏儿微笑着合拢了手中厚厚的书籍，气质淡雅从容，对着萧炎可爱的眨了眨眼睛。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着萧熏儿那微笑的小脸，萧炎迟疑了一下，摸着鼻子点了点头，然后在众多少年那嫉妒的目光中，走了过去，挨着她坐了下去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你又帮我解围了。”嗅着身旁少女的淡淡体香，萧炎低笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧熏儿浅浅一笑，小脸上露出可爱的小酒窝，纤细的指尖再次翻开手中那本古朴的书籍，小小年纪，却有一种知性的美感，眨动着修长的睫毛在书中徘徊了片刻，忽然有些幽幽的道：“萧炎哥哥有三年没和熏儿单独坐一起了吧？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呃…现在熏儿可是家族中的天才了，想要朋友还不简单么？”瞧得少女有些幽怨的光洁侧脸，萧炎干笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“在熏儿四岁到六岁的时候，每天晚上都有人溜进我的房间，然后用一种很是笨拙的手法以及并不雄厚的斗之气，温养我的骨骼与经脉，每次都要弄得自己大汗淋漓后，方才疲惫离开，萧炎哥哥，你说，他会是谁？”熏儿沉默了半晌，忽然的偏过头，对着萧炎嫣然一笑，少女独有的风情，让得周围的少年眼睛有些放光。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咳…我，我怎么知道？那么小，我们都还在地上爬呢，我哪知道。”心头猛的一跳，萧炎讪笑了两声，旋即有些心虚的将目光转向大厅内。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嘻嘻…”望着萧炎的反映，萧熏儿小嘴泛起了柔和的笑意，目光转移到书籍之上，口中似乎是自喃般的淡淡道：“虽然知道他是好意，可熏儿不管怎么说也是女孩子吧？哪有偷偷摸女孩子身体的道理，若是熏儿寻出了那人，哼…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嘴角裂了裂，萧炎心头有些心虚，眼观鼻，鼻观心，不言不语…</p>',10,3),(14,'斗破苍穹','第四章 云岚宗','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大厅中,萧战以及三位长老,正在颇为热切的与那位陌生老者交谈着,不过这位老者似乎有什么难以启齿的事情一般,每每到口的话语,都将会有些无奈的咽了回去,而每当这个时候,一旁的娇贵少女,都是忍不住的横了老者一眼…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;倾耳听了一会，萧炎便是有些无聊的摇了摇头…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎哥哥，你知道他们的身份吗？”就在萧炎无聊得想要打瞌睡之时，身旁的熏儿，纤指再次翻开古朴的书页，目不斜视的微笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你知道？”好奇的转过头来，萧炎惊诧的问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“看见他们袍服袖口处的云彩银剑了么？”微微一笑，熏儿道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦？”心头一动，萧炎目光转向三人袖口，果然是发现了一道云彩形状的银剑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“他们是云岚宗的人？”萧炎惊讶的低声道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然并没有外出历练，不过萧炎在一些书籍中却看过有关这剑派的资料，萧家所在的城市名为乌坦城，乌坦城隶属于加玛帝国，虽然此城因为背靠魔兽山脉的地利，而跻身进入帝国的大城市之列，不过也仅仅只是居于末座。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧炎的家族，在乌坦城颇有份量，不过却也并不是唯一，城市中，还有另外两大家族实力与萧家相差无几，三方彼此明争暗斗了几十年，也未曾分出胜负…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果说萧家是乌坦城的一霸，那么萧炎口中所说的云岚宗，或许便应该说是整个加玛帝国的一霸！这之间的差距，犹如鸿沟，也难怪连平日严肃的父亲，在言语上很是敬畏。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“他们来我们家族做什么？”萧炎有些疑惑的低声询问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;移动的纤细指尖微微一顿，熏儿沉默了一会，方才道：“或许和萧炎哥哥有关…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我？我可没和他们有过什么交集啊？”闻言，萧炎一怔，摇头否认。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“知道那少女叫什么名字吗？”熏儿淡淡的扫了一眼对面的娇贵少女。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“什么？”眉头一皱，萧炎追问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“纳兰嫣然！”熏儿小脸浮现点点古怪之意，斜瞥着身子有些僵硬的萧炎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“纳兰嫣然？加玛帝国狮心元帅纳兰桀的孙女纳兰嫣然？那位…那位与我指腹为婚的未婚妻？”萧炎脸色僵硬的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嘻嘻，爷爷当年与纳兰桀是生死好友，而当时恰逢你与纳兰嫣然同时出生，所以，两位老爷子便定了这门亲事，不过，可惜，在你出生后的第三年，爷爷便因与仇人交战重伤而亡，而随着时间的流逝，萧家与纳兰家的关系也是逐渐的浅了下来…”熏儿微微顿了顿，望着萧炎那瞪大的眼睛，不由得轻笑了一声，接着道：“纳兰桀这老头不仅性子桀骜，而且为人又极其在乎承喏，当年的婚事，是他亲口应下来的，所以就算萧炎哥哥最近几年名声极差，他也未曾派人过来悔婚…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这老头还的确桀得可爱…”听到此处，萧炎也是忍不住笑着摇了摇头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“纳兰桀在家族中拥有绝对的话语权，他说的话，一般都没人敢反对，虽然他也很疼爱纳兰嫣然这孙女，不过想要他开口解除婚约，却是有些困难…”熏儿美丽的眼睛微弯，戏谑道：“可五年之前，纳兰嫣然被云岚宗宗主云韵亲自收做弟子，五年间，纳兰嫣然表现出了绝佳的修炼天赋，更是让得云韵对其宠爱不已…当一个人拥有了改变自己命运的力量时候，那么她会想尽办法将自己不喜欢的事，解决掉…很不幸的，萧炎哥哥与她的婚事，便是让她最不满意的地方！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你是说，她此次是来解除婚约的？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;脸色一变，萧炎心头猛的涌出一阵怒气，这怒气并不是因为纳兰嫣然对他的歧视，说实在的，对面的少女虽然美丽，可他萧炎也不是一个被下半身支配心智的色狼，就算与她结不成秦晋之好，那萧炎也顶多只是有些男人惯性的遗憾而已，可如果她真的在大庭广众下对自己的父亲提出了解除婚约的请求，那么父亲这族长的脸，可就算是丢尽了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;纳兰嫣然不仅美丽娇俏，地位显赫，而且天赋绝佳，任何人在说起此事时，都将会认为他萧炎是癞蛤蟆想吃天鹅肉不成，却反被天鹅踏在了脚下…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如此的话，日后不仅萧炎，就算是他的父亲，也将会沦落为他人笑柄，威严大失。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;轻轻的吸了一口冰凉的空气，萧炎那藏在袖间的手掌，却已是紧紧的握拢了起来：“如果自己现在是一名斗师，谁又敢如此践踏于我？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;的确，如果萧炎此时拥有斗师实力，那么，就算纳兰嫣然有着云岚宗撑腰，那也不可能做出如此行径，年仅十五岁的斗师，嘿，在斗气大陆这么多年的历史中，可唯有那寥寥数人而已，而且这几人，都早已经成为了斗气修炼界中的泰山北斗！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一只娇嫩的小手，悄悄的穿过衣袖，轻轻的按着萧炎紧握的手掌，熏儿柔声道：“萧炎哥哥，她若真如此行事，只是她的损失而已，熏儿相信，日后，她会为今日的短浅目光后悔！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“后悔？”嗤笑了一声，萧炎脸庞满是自嘲：“现在的自己，有那资格？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“熏儿，你对他们似乎知道得很清楚？你先前所说的一些东西中，或许就是连我父亲，也不知道吧？你是如何得知的？”轻摆了摆手，萧炎话音忽然一转，问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;熏儿一怔，却是含笑不语。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着熏儿的躲避态势，萧炎只得无奈的撇了撇嘴，熏儿虽然也姓萧，不过与他却没有半点血缘关系，而且熏儿的父母，萧炎也从未见过，每当他询问自己的父亲时，满脸笑容的父亲便会立刻闭口不语，显然对熏儿的父母很是忌讳，甚至…惧怕！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在萧炎心中，熏儿的身份，极为神秘，可不管他如何侧面询问，这小妮子都会机灵的以沉默应对，让得萧炎就算有计也是无处可施。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉，算了，懒得管你，不说就不说吧…”摇了摇头，萧炎的脸色忽然阴沉了下来，因为对面那在纳兰嫣然不断示意的眼色下，那位老者，终于是站起来了…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，借助着云岚宗向父亲施威么？这纳兰嫣然，真是好手段呐…”萧炎的心头，响起了愤怒的冷笑。</p>',10,4),(15,'斗破苍穹','第五章 聚气散','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咳。”白袍老者轻咳了一声，站起身来对着萧战拱了拱手，微笑道：“萧族长，此次前来贵家族，主要是有事相求！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，葛叶先生，有事请说便是，如果力所能及，萧家应该不会推辞。”对于这位老者，萧战可不敢怠慢，连忙站起来客气的道，不过由于不知道对方到底所求何事，所以也不敢把话说得太满。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，萧族长，你可认识她么？”葛叶微微一笑，指着身旁的少女含笑问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呃…恕萧战眼拙，这位小姐…”闻言，萧战一愣，上下打量了一下少女，略微有些尴尬的摇了摇头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当年纳兰嫣然被云韵收为弟子之时，年仅十岁，在云岚宗中修炼了五年时间，所谓女大十八变，好多年未见，萧战自然不知道面前的少女，便是自己名义上的儿媳妇。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咳…她的名字叫纳兰嫣然。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“纳兰嫣然？纳兰老爷子的孙女纳兰嫣然？”萧战先是一怔，紧接着满脸大喜，想必是记起了当年的那事，当下，急忙对着少女露出温和的笑容：“原来是纳兰侄女，萧叔叔可有好多年未曾与你见面了，可别怪罪叔叔眼拙。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;忽然出现的一幕，让得众人也是略微一愣，三位长老互相对视了一眼，眉头不由得皱了皱…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧叔叔，侄女一直未曾前来拜见，该赔罪的，可是我呢，哪敢怪罪萧叔叔。”纳兰嫣然甜甜的笑道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，纳兰侄女，以前便听说了你被云韵大人收入门下，当时还以为是流言，没想到，竟然是真的，侄女真是好天赋啊…”萧战笑着赞叹道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嫣然只是好运罢了…”浅浅一笑，纳兰嫣然有些吃不消萧战的热情，桌下的手掌，轻轻扯了扯身旁的葛叶。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，萧族长，在下今日所请求之事，便与嫣然有关，而且此事，还是宗主大人亲自开口…”葛叶轻笑了一声，在提到宗主二字时，脸庞上的表情，略微郑重。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;脸色微微一变，萧战也是收敛了笑容，云岚宗宗主云韵可是加玛帝国的大人物，他这小小的一族之长，可是半点都遭惹不起，可以她的实力与势力，又有何事需要萧家帮忙？葛叶说是与纳兰侄女有关，难道？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想到某种可能，萧战的嘴角忍不住的抽搐了几下，硕大的手掌微微颤抖，不过好在有着袖子的遮掩，所以也未曾被发现，强行压下心头的怒火，声音有些发颤的凝声道：“葛叶先生，请说！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咳…”葛叶脸色忽然出现了一抹尴尬，不过想起宗主对纳兰嫣然的疼爱，又只得咬了咬牙，笑道：“萧族长，您也知道，云岚宗门风严厉，而且宗主大人对嫣然的期望也是很高，现在基本上已经是把她当做云岚宗下一任的宗主在培养…而因为一些特殊的规矩，宗主传人在未成为正式宗主之前，都不可与男子有纠葛…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“宗主大人在询问过嫣然之后，知道她与萧家还有一门亲事，所以…所以宗主大人想请萧族长，能够…解除了这婚约。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咔！”萧战手中的玉石杯，轰然间化为了一蓬粉末。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大厅之中，气氛有些寂静，上方的三位长老也是被葛叶的话震了了震，不过片刻之后，他们望向萧战的目光中，已经多出了一抹讥讽与嘲笑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嘿嘿，被人上门强行解除婚约，看你这族长，以后还有什么威望管理家族！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一些年轻一辈的少年少女并不知晓萧炎与纳兰嫣然的婚约，不过在向身旁的父母打听了一下之后，他们的脸色，顿时变得精彩了起来，讥诮的嘲讽目光，投向了角落处的萧炎…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着萧战那阴沉至极的脸色，纳兰嫣然也是不敢抬头，将头埋下，手指紧张的绞在了一起。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧族长，我知道这要求有些强人所难，不过还请看在宗主大人的面上，解除了婚约吧…”无奈的叹了一口气，葛叶淡淡的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧战拳头紧握，淡淡的青色斗气，逐渐的覆盖了身躯，最后竟然隐隐约约的在脸庞处汇聚成了一个虚幻的狮头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧家顶级功法：狂狮怒罡！等级：玄阶中级！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着萧战的反映，葛叶脸庞也顿时凝重了起来，身体挡在纳兰嫣然身前，鹰爪般的双手猛的曲拢，青色斗气在鹰爪中汇聚而起，散发着细小而凌厉的剑气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云岚宗高深功法，青木剑诀！等级：玄阶低级！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着两人气息的喷发，大厅之中，实力较弱的少年们，脸色猛的一白，旋即胸口有些发闷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在萧战的呼吸越加急促之声，三位长老的厉喝声，却是宛如惊雷般的在大厅中响起：“萧战，还不住手！你可不要忘记，你是萧家的族长！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身子猛的一僵，萧战身体上的斗气缓缓的收敛，最后完全消失。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一屁股坐回椅子上，萧战脸色淡漠的望着低头不言的纳兰嫣然，声音有些嘶哑的道：“纳兰侄女呐，好魄力啊，纳兰肃有你这女儿，真是很让人羡慕啊！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;娇躯微微一颤，纳兰嫣然呐呐的道：“萧叔叔…”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，叫我萧族长就好，叔叔这称谓，我担不起，你是未来云岚宗的宗主，日后也是斗气大陆的风云人物，我家炎儿不过是资质平庸之辈，也的确是配不上你…”淡淡的挥了挥手，萧战语气冷漠的道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“多谢萧族长体谅了。”闻言，一旁的葛叶大喜，对着萧战赔笑道:“萧族长，宗主大人知道今天这要求很是有些不礼貌，所以特地让在下带来一物，就当做是赔礼！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说着，葛叶伸手抹了抹手指上的一枚戒指，一只通体泛绿的古玉盒子在手中凭空出现…</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;小心的打开盒子，一股异香顿时弥漫了大厅，闻者皆都是精神为之一畅。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三位长老好奇的伸过头，望着玉匣子内，身体猛的一震，惊声道：“聚气散？”</p>',10,5),(21,'九星毒奶','第一章  划重点？','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;烈日当空，灼烧大地，尽管已经是八月末了，但炎热的夏季依旧在散发着阵阵余威。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;微风席卷，却不能给人们带来多少清凉，它吹拂着喧嚣的江滨市，也吹进了东城区的一座民宅之中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呃......”趴在电脑桌前的青年痛苦的揉了揉脑袋，睁开了浑噩的双眼，迷茫的看向了四周。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此时此刻，青年的脑海中冒出了标准的哲学三连击：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我是谁？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我在哪？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我要干什么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;奥，对，我叫江晓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓揉着自己的脑袋，又是一阵龇牙咧嘴，诶，脑袋好疼......</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;放眼四周，这是谁的房间？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是猪窝吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;到处都是随意丢弃的衣物？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;到处都是...嗯，纸团？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;乖乖，可了不得。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青年目光所及之处，在一旁的纸篓里，更是看到了堆积如山的纸团。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不管这间房是谁的，不愧是吾辈楷模！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;然而，江晓心中那戏谑的心思在下一刻便消失无踪了，因为在不经意间，他看到了面前电脑屏幕上那微微晃动的人影。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;漆黑的电脑屏保所映射出来的人影，当然就是江晓本人了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓也知道这黑屏里呈现的就是自己，但是......但是这不算模糊的影像竟然是个孩子？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓不相信的将脸蛋向前探去，而黑色屏保中的人影也做着相同的动作。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咕嘟。”江晓喉结一阵蠕动，咽了口唾沫，小心翼翼的碰了碰鼠标。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;电脑屏保消失不见，电脑屏幕上，一个青年正一副见鬼了的模样，眼睛傻傻的盯着电脑屏幕，这模样正是江晓此时的样子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是啥视频软件？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓眨了眨眼睛，这似乎并不是在视频聊天，而是在录制？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓向上看去，发现了一个摄像头，这具身体的本尊刚才在干嘛？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自己撸小视频？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;呃，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;录小视频？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;显然，视频依旧在录制的过程中，这倒是让江晓有些意动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果这具身体的主人一直在录制视频的话，那么自己穿越的全过程是不是就录制下来了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;还有这种福利？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓急忙按下了停止录制，选择了文件播放。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;几秒钟的等待过后，电脑屏幕中，一个稍显青涩的面庞出现在江晓的面前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;格衬衫，圆寸头，大概15、6岁的年纪，模样还挺清秀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这干干净净的模样不像是满地扔纸团的家伙啊？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“大家好，我叫江小皮。”视频里的男孩终于说话了，“可惜，我最近有点感冒，你们听不到我充满磁性的男神嗓音了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓：？？？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;视频中，江小皮随手抽出了一张面巾纸，擤了一下鼻涕，又团成一团，随手仍在了地上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;原来这些纸团是这么来的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嗯，其实江晓也是这么认为的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;废话，面巾纸还能用来干啥？不就是擤鼻涕么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓伸手也从桌上的纸抽里抽出了一张面巾纸，因为他终于意识到“自己”感冒了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;视频中，自称江小皮的青年继续开口说道：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我再说一遍，我叫江小皮！记住这个终将让你们敬仰一生的名字！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是我首次开通微薄，因为我发现，我的强大实力已经不允许我再低调下去了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓急忙按下了暂停键，看着屏幕中的“自己”，江晓突然有种想要打死自己的冲动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这孩子是怎么长大的？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他是怎么活了这么多年的？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓缓了缓心神，镇定了好一会儿，继续按下了播放键。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;屏幕中，江小皮却没有再继续吹嘘下去，而是话锋一转：“简单的介绍一下我自己吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯，孩子们总是问自己的父母，他们是从哪里来的。而父母们的回答多种多样，有从垃圾箱里捡来的，有充话费送的，也有过节收礼收到的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江小皮突然极为认真的看着摄像头，开口道：“而我就厉害了，我真的是从垃圾箱里捡来的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我的老妈是个大美女，温柔美丽，她叫江红叶，听听这名字，多美！这才是人的名字！再看看我的名，哎......”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;屏幕中，江小皮憋着嘴，一脸不开心的碎碎念道：“捡到我的那一天，老妈养的狗狗死了，那狗的名字叫小皮，我就被叫做......嗯。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江小皮一脸的不开心，突然眼前一亮，看着摄像头说道：“万幸，那狗狗还挺可爱的，是个爱笑的金毛。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓：“......”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;视频中，江小皮托着下巴，沉默了一会儿，道：“行吧，我想你们已经记住了我的名字了，那么是时候表达我的态度了，我即将进入滨城高中就读，现在，我就要告诉高一的所有同学！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓稍显期待的看着屏幕，不知道这江小皮到底会说出什么话来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;视频中，江小皮说道：“从今天起，我就是高一的大哥了！有不服的，放学之后小树林约一下！我江小皮告诉你们，高一，我最大！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“另外！”江小皮指着摄像头，继续说道，“那些高二高三的人，别以为你们年纪大，早觉醒能力一两年就可以为所欲为，那个姓夏的校花我已经预定了，谁都别跟我抢！不服的一样，放学之后操场、小树林约一下，我江小皮没怕过谁！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江小皮恶狠狠的看着摄像头，奶凶奶凶的：“我要让你们知道，美人，只配强者拥有！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓看着屏幕里那个中二病晚期的“自己”，此时此刻的他是懵逼的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;咔嚓！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;视频中，江小皮身后的房门突然打开了，一道倩影迈步走了进来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓眉毛一挑，啧啧，这女孩可真美。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;清水出芙蓉，天然去雕饰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只见她身穿着洁白的睡裙，这显然是居家服饰，她应该也在这民宅中居住。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她大概17、8岁的样子，身材高挑，面容精致，此时那精美的容颜上却布满了寒霜，一头漆黑的长发随着那怒气冲冲的步伐飘荡着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;屏幕中，面容极冷的女孩踢开了满地的纸团，快步走到了江小皮的身后，而江小皮还没有察觉，依旧在吹牛口嗨。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只见女孩扬起一只手，一巴掌打在江小皮的后脑勺上！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;咚！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大快人心！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一声闷响，江小皮一头撞在了电脑桌上，而面容冰冷的女孩气还没消，狠狠的瞪了一眼“装死”的江小皮，道：“起来！做点正事！修炼星力，或者收拾屋子！别拍这种恶心的视频。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江小皮趴在电脑桌上，一动不动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那你就别吃饭了！”女孩寒声说着，转过身，白色的睡裙轻轻飘荡，厌恶的踢开满地的纸团，走出了房间，狠狠的将门甩上了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而屏幕中，那个“装死”的江小皮，似乎没有装？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓坐在电脑桌前，看着屏幕上趴着的江小皮，足足5分钟后，看到了“江小皮”揉着脑袋，迷茫的看着四周的模样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是在这个节点上，江晓穿了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;哇！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;厉害了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自己竟然是被一巴掌扇进了这个世界的？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓揉了揉脑袋，继承了江小皮的身体，努力的回想着脑海中的记忆。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个女孩似乎是江小皮的姐姐？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;父亲姓韩，女儿当然跟父姓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而江小皮是母亲捡来的，在母亲江红叶的要求下，所以跟了母姓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在江小皮的记忆中，父母二人似乎是在3年前的一次任务中一去不复返的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嗯？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;姐弟俩相依为命？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;小姐姐美若天仙？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两人无血缘关系？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嗯，是时候划重点了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;划...个屁划，此时的江晓已经彻底懵了，那里还有心思划这方面的重点？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;重点是，江晓从一个25岁的青年变成了一个16岁的青年，从一个步入社会的菜鸟变成了高中生？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而且，这个世界......</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓将鼠标指针移动到电脑的左下角，看着年月：2015年8月20日。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嗯，时间上倒是没什么差别，和江晓来的那个世界一样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可是刚才这姐弟俩口中的“觉醒”“星力”是什么意思？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓皱着眉头，仔细的回想了一下江小皮的记忆，这才惊讶的发现，这个世界竟然不是自己的地球世界？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是一个充满了神奇能力、神奇觉醒者、以及布满了异次元空间的世界。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;由于异次元空间门的存在，这个世界与江晓原本所处的地球有着巨大的差别。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在那无数异次元空间门的背后，存在着各种各样光怪陆离的世界，同样有着千奇百怪的生物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;让人感到难过的是，绝大多数的空间传送门内部的生物并不友好。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在人类坎坷的历史长河中，人类不仅面对着内部的内战，更面临着外部异维度环境生物的入侵和侵扰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力，是一种神奇的能量，它随着异次元空间大门的出现相伴而生，这种神奇的能量改变着人们的身体素质，同样改变了人们的生活方式。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着第一批星力觉醒者的出现，曾经只能够用“坚船利炮”来对抗异次元生物的人类们，终于有了新的选择。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然，虽然新的能量涌入了地球，改变着人类的命运，但却没有改变所有人的命运。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;事实上，绝大多数的人们依旧是普通人，他们的身体无法承载这种神奇的力量。贸然修行不仅不会强身健体，反而会造成身体的损伤。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着时间的推移，人类对星力的认知不断加深，经历了不断的验证过后，“16岁”成为了国际公认的觉醒阶段。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;16岁是一个门槛，在这个年纪里，人类觉醒的几率最大，是人为干预觉醒的最佳时间，过早或过晚都可能会对实验者造成身体上的损伤。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然，总有一些狗贼！他们会做出一些人神共愤的事情，我们将这类人称之为“天才”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他们不需要做任何事情，只是突然某一天，他们感受到了地球上那无处不在的星力，被称之为自然觉醒，前途不可限量。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他们甚至可能在10岁、8岁、甚至6岁就开启了觉醒之路，足足比正常觉醒者早修炼了数年之久。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然，这个世界上哪有所谓的公平呢？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;除了极少部分天之骄子之外，更多的人是经由外力干预而觉醒的，占据着觉醒者团队的绝大部分比重，江小皮显然就属于这一类觉醒者。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在16岁之后，经由觉醒者的帮助，将星力引导进入身体之中，从而开启觉醒者的职业生涯。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是这必须是在严格且正规的过程中实现的，把握好分寸是一个合格引路人的必要条件。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有太多太多无法觉醒的人，他们不甘心，不愿意接受这样的结果，从而想方设法来达到自身的目的，而最终的结果都不太好。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这样强扭瓜的人会在这条路上受伤、死亡，更多的情况就是其不仅没有觉醒，反而对自身身体遭受了巨大的创伤，从此一蹶不振，变成了一个脆弱的病秧子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在两个月前，在初中三年级最后的毕业典礼上，十数名觉醒者站在方阵之中，帮助这一批孩子完成了最后的毕业考核。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;校长大人早已经习惯了在致辞的时候享受阵阵的欢呼声，当然，傻子都知道，那欢呼声不是给校长那冗长的发言稿的，而是给那些一个又一个觉醒的孩子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那场毕业典礼，终于开启了江小皮的职业生涯。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江小皮的身体适合修炼星力，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他成功的开启了星图！</p>',16,1),(22,'九星毒奶','第二章  既治病，也要命！','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有点意思。”江晓仔仔细细的回想着脑海中的记忆，虽然他初来乍到这个世界，但是却已经被这个神奇的世界深深的吸引住了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在江晓的原本世界中，他无亲无故，了无牵挂，倒也没什么放心不下的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个世界竟然有着各式各样千奇百怪的能力？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;乖乖，可了不得。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓站起身子，环视着自己的“猪窝”，实在是有点不堪入目，江晓想了又想，他还是认命的捡起一件件衣服，按照记忆，轻车熟路的将衣服挂回衣柜之中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;满地的纸团让江晓又有一种擤鼻涕的冲动了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可恶，既然成为了觉醒者，为什么还会感冒？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;还真是为你感到羞愧呢，江小皮！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓一边吐槽着江小皮，一边将满地的纸团收进垃圾袋中，足足收拾出了两大袋子垃圾，江晓的内心都是崩溃的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这么一个长相清秀，衣着干净的阳光青年，怎么如此邋遢？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;难道你这身打扮是你姐给你穿的？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓一边收拾着，一边皱起了眉头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;记忆之中的江小皮似乎一直是这样“骚浪贱”的，尤其是在三年前，父母离开之后，江小皮的性情大变，缺乏管教的他，一天天的得寸进尺。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是这样的得寸进尺也是在可以承受的范围之内的，然而在两个月前，当江小皮在毕业典礼上觉醒了之后，他这“狂拽炫酷吊炸天”的性格便一发不可收拾了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;估计是开启了觉醒者生涯之后，心态失衡了吧？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这样的心态失衡在三天之前达到了巅峰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嗯？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓愣了一下，将垃圾袋缓缓的放在门口，他在记忆中找到了不得了的东西。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;系统！？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内视星图！？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江小皮怎么会有系统加身？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;难道...江小皮真的成为了天选之子，直接开了金手指，所以才导致心态彻底变化，觉得老子天下第一。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;结果“天下第一”刚刚开启吹牛皮的生涯，就被自己的小姐姐一巴掌给扇死了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓小心翼翼的打开了内视图，一个虚幻的江晓身影出现在了正中央。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虚幻的江晓只是背景图，三大板块赫然在列：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一、星力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;二、星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三、基础技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在正上方的星力板块中，一个星座图呈现了出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;九颗暗淡的星辰拼凑出了勺子的形状，非常像是“北斗七星”的布局，但是这个勺子柄部有点长，多出来两颗暗淡的星辰，一共九颗星星。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;九颗星星由似有似无的点点星芒连接起来，萦绕在“勺子”星座图的周围，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星座图上方，一行字迹非常的清晰：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力：星尘期LV.2。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星尘期？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓的面色有些古怪，缓缓的坐在单人小床上，仔仔细细的回想了一下，这才恍然大悟，所谓的星尘期是这个世界对觉醒者体内所蕴含星力多少一种等级分类。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星尘期之上，还有星云期，星河期，星海期，星空期。似乎人类社会的分类只达到了星空期，再往上恐怕还会有，但那只是传说，无从考证。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓习惯性的揉了揉头发，看着自己的星图，江晓的心中慢慢的兴奋了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第二板块，两个字迹简单明了：星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;让江晓有些失望的是，星技里面竟然啥都没有，空空如也。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一个技能也没有？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这么贫穷么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓摇了摇头，继续向第三个板块看去，这一次终于有新东西了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第三板块基础技：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;基础格斗（黄铜品质Lv.1，可升级）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力充沛（黄铜品质Lv.1，可升级）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;然后就没了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓懂得基础格斗的意思，但是这个黄铜品质是啥意思？最低级的品质么？而且，这个星力充沛是什么鬼？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓心念一动，仔仔细细的看了看，星力充沛：凝聚、及恢复星力的速度（黄铜品质Lv.1，可升级）。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;乖乖，可了不得。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第一板块给我来了个“北斗九星”的星座图。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第二板块星技空空如也。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第三板块来了两个基础技能，而且看起来都非常有用，甚至可以升级？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江小皮就是因为这个，所以才放肆口嗨了么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“江！小！皮！”就在这时，门外传来了一道冰冷的声音，听得出来，对方有些恼怒，那每一个字似乎都是从牙缝里挤出来的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;下一刻，房门猛地被打开，那个白裙胜雪、肤白貌美的小姐姐再次出现了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你真不吃...嗯？”白裙小姐姐愣了一下，刚刚还是满地纸团、乱七八糟的猪窝，现在竟然收拾的如此干净？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪那一双漆黑的眼眸左右打量了一下，继而看到了坐在床上发呆的江晓，那一脸的愠怒渐渐消解，声音清冷，道：“吃饭。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说着，便转身离去了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓眨了眨眼睛，刚才不还说“你别吃了”么？而且还扇了江小皮一巴掌？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这才多长时间？半个小时？就屁颠屁颠来叫吃饭了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然江小皮才16岁，是韩江雪的小弟弟，但是此时的江小皮已经被她一巴掌扇“没”了，也许是被扇到其他世界去了吧？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;初来乍到的江晓已经是个25岁的成年人了，通过女孩的行为，也看出了她有点小傲娇的属性。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过，话说回来，这女孩的外形条件还真是不错。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;浑身上下唯一的毛病，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是平了点，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有点飞机场的意思......</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;......</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓飞快的整理好床铺，按照脑海中的记忆，轻车熟路的在家中行走，一百二十平米，三室一厅，条件还算不错，除了江小皮的猪窝之外，其他的地方都很干净，装潢典雅，看得出来居家主人的品味。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;将两大袋垃圾放到家门口，江晓又返回洗手间中，仔细的洗了洗手，这才进入了厨房中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而韩江雪正将那一头漆黑的长发束成马尾，对江晓依旧没有什么好脸色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然她的面部依旧寒霜密布，但是内心中却也在暗暗疑惑，这江小皮怎么了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;真的被自己一巴掌给扇醒了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;事实上，韩江雪并不是这么暴力的人，她从未打过江小皮，对于自己的弟弟，她清冷的性格虽然很难做出一些温暖的表情与话语，但是她的行为却是温暖的，默默的照顾着江小皮的日常起居。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两个月前，她还在为江小皮觉醒而暗暗高兴，但也就是在江小皮觉醒之后，灾难降临了，虽然在三年前父母离去之后，江小皮就越来越难以相处，但是两个月前的那次觉醒之后，江小皮的性格发生了更为巨大的变化，尤其是在三天前，江小皮简直就是变了一个人，变得特别张狂。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪一直忍受着江小皮幼稚且好笑的言论与行为，正当她不知道该如何教导江小皮的时候，今天的这一段视频让韩江雪真的是忍无可忍。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于韩江雪这种性格清冷的人来说，怒火的爆发从来不是一蹴而就的，而是日积月累形成的，显然，今天江小皮那猖狂无耻的模样彻底惹怒了韩江雪。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;让韩江雪万万没想到的是，这一巴掌的疗效简直惊人！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这小子收拾衣服了，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;打扫房间了，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不作不闹，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;安安静静的来吃饭了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;你看他现在的模样，多乖巧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪看着低头吃饭的江晓，她忍不住伸出右手，看了看自己白嫩的手掌。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;难道自己觉醒了新的星技？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓悄悄打量着韩江雪，看到她正在打量着手掌，江晓忍不住打了个哆嗦。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然那一巴掌是打在江小皮身上的，但是江晓此时的后脑勺依旧在隐隐作痛，不可抑止的回想起了刚刚视频中的画面。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;你可见过一招从天而降的掌法？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;专拍后脑勺？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;既治病，也要命！</p>',16,2),(23,'九星毒奶','第三章  我都要','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两人默默无言的吃饭，尴尬的气氛终于还是被韩江雪打破了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪一边夹着菜，装作漫不经心的询问道：“还疼么？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呃......”江晓幸福的吃着红烧肉，正在赞叹着小姐姐美妙的厨艺，突然听到了韩江雪的问话，迟疑了一下，回应道，“有点。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪面无表情的脸蛋上终于有些变化，似乎是有些心疼，但又不愿意表现出来，张了张小嘴，半晌之后，恢复了漫不经心的模样，道：“以后别拍那样的视频了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好的。”江晓又吃了一块红烧肉，吃了一大口米饭，幸福的都快哭出来了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;真是想不到，如此年纪轻轻的女孩竟然厨艺惊人，难道这是她的星技？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这红烧肉真是肥而不腻，美味绝伦。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓埋头吃饭的模样，让韩江雪有些心疼，弟弟怕是被自己打怕了，刚才在气头上，她的确下手没轻没重。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想到这里，韩江雪放下了碗筷，起身走出了厨房。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓的动作停了一下，目送着白裙飘飘的小姐姐离去，这就吃完了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;吃的这么少？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;多浪费啊？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;你不为自己考虑，也得为那片飞机场考虑考虑啊？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不准备在机场上建个C字楼什么的啊？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓吃完了一碗饭，看着餐桌对面韩江雪剩下的一小碗米饭，犹豫了一下，拿过来继续胡吃海塞。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着时间的推移，他脑海中的记忆慢慢融合，倒也变得越来越自在了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;理智上，他知道自己与这个家庭没有半点关系。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是在情感上，江晓已经逐渐进入角色了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;进入角色的结果就是......吃完饭扔下碗筷就走。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;至于洗碗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;呵呵，不存在的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓打消了自己放下碗筷就走的念头，他毕竟不是江小皮，性格与行为、语言上的差异终究会被发现的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;既然如此，那何不从小事上做出改变？倒也能让韩江雪更好的接受。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那就从洗碗开始吧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我的饭呢？”背后，突然传来了一道清冷的声线。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这样的声音总让江晓觉得很违和，这种不食人间烟火的气质与声音，说出来的话却是柴米油盐。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我以为你不吃了。”江晓收拾饭碗的动作停了一下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯。”韩江雪似乎性格就是如此，也没辩解什么，她对很多事情都不怎么关心，只是示意了一下江晓，道，“坐下。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦。”江晓坐了下来，这是要开会？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;本着既来之则安之的心理，江晓准备尽可能的融入角色，倒也听话的坐了下来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“喏。”韩江雪摊开了白嫩的手掌，一枚闪烁着黄铜色光芒的珠子呈现在江晓的眼前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是...星珠？”江晓眼前一亮，星珠这种东西他并不陌生，在江小皮的记忆中，韩江雪曾经给过他3枚星珠，这也是江小皮的星力能够达到星尘期Lv.2的原因。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可惜的是，江小皮资质很差，三枚黄铜品质的星珠里蕴含的大部分能量，都被江小皮浪费了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然，韩江雪给予江小皮星珠的目的，却并非只是增强江小皮的星力，而是想要让江小皮获得星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星珠，出产于异次元生物，但并非每一只异次元生物都会形成星珠。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过吸收异次元生物的星珠，从而获得其能力，这也是星技获取的唯一方式。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;让人们感到难过的是，哪怕你获取到了星珠，也明知道这一枚星珠中蕴含着异次元生物的能力，人们从星珠中吸取到星技的可能性也是低的令人发指。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所以，星技的获取难度并不低。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而且，比较重要的一点是，随着星力的提升，觉醒者才能吸收星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力等级分为：星尘期，星云期，星河期，星海期，星空期以及可能存在的更高级别。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;哪怕是一个人的星槽再多，资质再强，在他星力为星尘期的时候，最多也只能在星图中镶嵌4个星珠，也就是只能拥有4个星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当星力总量提升到星云期之后，可以再吸收4个星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而当星力总量达到星河期之后，会发生质变，可以再增加8个星技，而后以此类推。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这些对于江晓来说，还是太过遥远。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;已经吸收了三枚星珠的江小皮，自然而然的成为了芸芸众生中的一员，三枚星珠，没有吸收到任何一项星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“昨天我在雪原里碰到了一群白鬼，这枚星珠是白鬼巫的。”韩江雪非常难得的说了一整句话。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;解释了一句之后，便不再开口，而是将这枚小指甲大小的星珠放在了江晓的桌前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白鬼种群？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而且这枚星珠不是来自白鬼？而是来自更稀有的物种——白鬼巫？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“吸收了吧，人们的体质毕竟不同，有些人哪怕是开启了体内的星图，但也许一辈子都无法获得星技。”韩江雪再次开口说了一大段话。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;你这句话是在安慰我么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓的面色有些古怪，结合了记忆之后，知晓了眼前这枚星珠的珍贵程度之后，心中暗暗的叹了口气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪看着江晓没有动作，也感受到了江晓的压力，继续安慰道：“你的资质并不高，星图只有9颗星，这已经注定了你的上限，哪怕是吸收不到星技，你也不用太难过，没关系的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓：？？？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓终于抬起头，一脸无奈的看着韩江雪，他知道这妞儿是出于好心，怎么说出来的话这么让人恼火呢？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;的确，江晓的星图是“北斗九星”，九颗暗淡的星星代表了九个星技，同样，这也代表了成长的上限。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说白了，九颗星星就代表了九个技能槽，江晓哪怕是玩出个花来，最多也只能获得九项星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通常意义上来讲，星星凹槽的数量就代表了一个人的资质，不仅仅能够区分出觉醒者获取星技的数量，更是可以直观的让人们看到发展空间。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是人类社会在经过了无数实验检验之后所形成的标准。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;拥有20个星槽的觉醒者和拥有1个星槽的觉醒者，在面对着同样品质的星珠时，20星槽的觉醒者能够更加轻易的获取星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓的星图是“北斗九星”，而韩江雪的星图，却是熊熊燃烧的白色焰火。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过星槽的连线以及星力的分布、渲染，在战斗的时候，韩江雪所呈现出来的星图，简直可怕。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;足足三十个星槽！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这代表了她能够获取星技的数量，以及她的成长资质！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓拿起了餐桌上的星珠，还未等吸收，脑海中突然蹦出了一个画面。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“白鬼巫星珠（黄铜品质）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;拥有星技：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1、祝福：缓慢的恢复目标生命力。（黄铜品质，可升级）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2、诱饵：制造一个幻象迷惑敌人。（黄铜品质，可升级）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;是否合并吸收？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是什么意思？是否吸收？而且还是“合并”吸收？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这代表了什么？一个星槽里面放两个技能？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;别人就算是有星珠，也可能一辈子都吸收不到一个星技，而自己却可以直接吸收？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;包括后面的“可升级”标识，要知道，其他人哪怕是吸收了星技，就会定格在技能吸收时候的品质上，无法再提高了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是江晓却可以将一项技能提升品质？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是何等的金手指啊！？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;是不是三天前系统给江小皮介绍了之后，他的心态彻底大崩，尼玛这还真的是个天选之子啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不用可惜，吸收了吧，我可以再去打。”韩江雪有些不耐烦的说道，无所谓的挥了挥手，她也知道江晓的资质，也知道自己的小弟弟是个大坑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪继续道：“白鬼巫有两项星技，一个是治愈队友的伤势，另一个是创造幻象，能力都很不错......”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说话间，韩江雪看到一直犹豫不决的江晓眼眸一亮，手中的星珠化作点点星芒，钻进了他的胸膛。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;哪怕是韩江雪性格再怎么淡漠，此时的她，都有了一丝期待，歪着脑袋询问道：“有没有收获星技？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓感受着体内涌入的点点星芒，睁开双眼，点了点头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪美眸一亮，随着那冰冷的容颜稍稍缓解，仿佛整个世界都温暖了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓望着眼前明眸善睐的女孩，心情也好了不少。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪轻声道：“收获了哪种星技？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;哪种星技？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;曾经的江小皮没得选，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我，江晓，都要！</p>',16,3),(24,'九星毒奶','第四章  我祝福你','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪与江小皮生活在江滨市，通过江小皮的记忆得知，这里的华夏与江晓原本世界的华夏地域没有多少改变。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江滨市，勉强算得上是二线城市，虽然经济不算发达，但起码还占了个省会的名额。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江滨市是北江省的省会，北江省伫立在华夏大地的最北端，这里盛产“白鬼”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;众所周知，神州大地的地域无比辽阔，从南到北，从东到西，地理环境天差地别。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而在这华夏大地的最北部省份，其内所开启的异次元空间门颇为有趣，呈现出了鲜明的地域特征，也不知道是不是造物主的玩笑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在北江省的异次元传送门背后，通常都是白雪皑皑、环境恶劣且严寒的雪原。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;雪原之中，最普遍、最大量的种群就是白鬼一族了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他们浑身上下包裹着白毛，呈人形，直立行走，更像是猿类，身高一米五~两米不等，身强体健，非常凶猛。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它们身披着厚厚且洁白的绒毛，但是那一张脸却是黑的可怕，像是恶鬼一样，让人看着毛骨悚然。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江小皮曾经吸收的三枚星珠，统统都是最低等级的白鬼星珠。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而今天，不知道韩江雪是不是有意道歉，竟然拿出了白鬼巫的星珠。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;相比于白鬼来说，白鬼巫的数量相对更少，在一支白鬼种群中，最多只会有一只白鬼巫的存在。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;普通的白鬼凭借着尖牙利爪欺行霸市，而白鬼巫就高级了一些，它们拥有着神奇的“魔法”，对于地球人来说，这类“魔法”统一被称之为“星技”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而此时，星技板块上一无所有的江晓，终于拥有了人生中的第一个星技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不，是人生中的第一和第二个星技：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;祝福和诱饵。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓开启了自己的星图，下一刻，一个虚幻的江晓突兀出现，成为了大背景。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而在虚幻人物的胸膛出，一个“北斗九星”的星图呈现了出来，那原本还阴暗着的第一颗星星，此时已经被点亮了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无数星星点点的光芒萦绕在星辰周围，像是暗夜中的萤火虫一样，肆意飞舞，煞是美丽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力：星尘期Lv.2。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力依旧是Lv.2么？吸收星珠可是提高星力的最有效途径，自己吸收了一颗星珠，怎么星力没有半点长进？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这里就不得不要说明一下了，星珠的确可以巩固人体内星技，甚至可以提高星力总量的上限。但是，星力与身体素质也是挂钩的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力的提高可以促进人类身体素质的增长，让人们身强体壮，力量更强、速度更快，全方位的提升身体素质。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而人类的身体就像是一个水桶，只有通过不断的提高身体素质，才能装载更多的星力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;二者就是相辅相成的关系，星力促进身体素质提高，而身体素质提高就可以吸收更多星力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此时此刻的江晓却在身体素质上出现了问题，如果把他的身体比喻成一个小水桶的话，此时的江晓这个水桶里已经装满了星力，无法再承载更多了，这也是导致内视星图上星力没有提升的原因。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;现在的江晓最应该去锻炼身体，消化吸收星力，提高自身的身体强度，成为一个更大的水桶。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天天在家混吃等死，等着已经溢满的星力自动改造人体素质？而不去锻炼，消化、提升，江晓恐怕十年才能升一级。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这些暂且不提。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓已经将目光向第二板块看去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星技：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1、祝福：缓慢的恢复目标生命力。（黄铜品质Lv.1，可升级）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2、诱饵：制造一个幻象迷惑敌人。（黄铜品质Lv.1，可升级）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;果然，点亮一颗星星，但是却获得了两个星技！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;奶腿的，谁说我“北斗九星”就只能有九个星技？我不仅能有9个，我恐怕还能再多来9个！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最最关键的是，当一个人千辛万苦终于吸收到了一个星技，但却只能在“黄铜品质”上固定一生的时候，江晓却可以将星技的品质提高！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个世界有一项常识：一旦星槽被点亮，那么此星技便无法更换，无法升级，无法消除。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;也就是说，哪怕是一个神级人物，星图里拥有一万个星槽，你最初吸收的那个黄铜级别的星技，永远都是黄铜。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黄铜，就这么倔强！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓意念一动，看向星技后方的“可升级”选项上：“升级需求：白鬼巫星珠（10）或技能点（10）。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;哇，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这样一来，自己就有动力了呀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓再次看向了自己的第二个星技：诱饵。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同样需要同类型的星珠或者技能点。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那么现在问题来了，这个技能点怎么获取？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓有点摸不着头脑，当初这个内视星图挂在江小皮身上的时候，应该是给江小皮解释过了吧？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“系统？”江晓尝试着喊了一句。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯？”韩江雪静静的看着江晓，这个只知道胡闹的弟弟今天真的是有些异常，姑且算是被一巴掌扇清醒了吧，但是怎么又开始胡言乱语了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓急忙收敛心神，继续向第三板块看去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;基础技：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;基础格斗（黄铜品质Lv.1，可升级）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力充沛（黄铜品质Lv.1，可升级）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓意念一动，终于发现了有些不同。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;基础格斗（黄铜品质Lv.1，可升级），升级需求：自主提升或10技能点。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力充沛（黄铜品质Lv.1，可升级），升级需求：自主提升或10技能点。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果说星技可以用同类型的星珠升级的话，那么基础技却只能用自主训练或者用技能点来升级了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓刚要关闭内视星图，却在右下角看到了一行小字：“技能点：1。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;乖乖，可了不得。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这么坑人的么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;字这么小？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这不是跟电视广告里卖药的一样么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大吹特吹本药物治疗效果有多好，结果右下角有一行半透明的小字：本产品为保健食品，不能替代药品。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这谁能看得到啊？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;......</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三大板块中，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第一板块星力和第三板块基础技，都可以通过自身修炼和技能点来升级。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而第二板块星技可以用同类别星珠和技能点升级。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;傻子都能看出来技能点的重要性，只不过，江晓现在还不知道技能点到底该怎么获取？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想了又想，江晓还是没有擅自动用那仅有的一个技能点，毕竟他不着急，可以再等一等，彻底摸清楚状况之后再进行升级，反正他现在也没什么危险。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我问你话呢。”面前，那清冷的声线再次传来，声音中带着一丝严厉，那漆黑的美眸也凌厉了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓回过神来，看向了对面的女孩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;啧啧，这女孩气势不错啊，虽然刚刚年满十八，但可能是因为这个世界存在星力的原因，让这样的花季少女接触到了普通人无法触及到的战斗，所以她的气势与气质是真的不错。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可惜，小姐姐的威严并不能吓到江晓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“给我看看你的星图。”韩江雪皱着眉头说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓耸了耸肩，体内能量流转，胸前，“北斗九星”的星座图悄然出现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯。”韩江雪轻轻的点了点头，别看她冷着一张脸，但是心中却已经激动万分了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第一颗星星果然亮了，呈现出了应有的黄铜颜色！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哪个星技？”韩江雪一副镇定自若的模样，随口问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓催动着体内的能量，可怜巴巴的几缕星尘缓缓流转，随着江晓右手一挥，一道白光照耀在了韩江雪的身上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星技：祝福！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;下一刻，白光沐浴中，那白裙胜雪、不食人间烟火的高冷小姐姐，突然发出了一道极致诱惑的鼻音：“嗯~”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓：？？？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;5秒钟后，韩江雪朦胧的双眼渐渐恢复了清明，一双美眸中眼波荡漾，白嫩的俏脸已经升起了两团红晕，像一只诱人的水蜜桃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓有点摸不着头脑，这是什么福利？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这技能...这么舒服么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;好吧，小姐姐，让我继续祝福你！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓再次调动着星力，手中白光一闪，一道白光再次落在了韩江雪的身上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪：“啊~”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓：“......”</p>',16,4),(25,'九星毒奶','第五章  我再祝福你','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“停，停一下，小，小皮......”韩江雪双肘拄在餐桌上，双手扶着额头，轻声细语道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;哇，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这么说话多温柔，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;软软糯糯的，多悦耳，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;平日里那么冷冰冰的干什么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓适时的收手，突然发现，自己获得的星技似乎不是什么正经能力？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说好的恢复队友生命力呢？治愈队友伤口呢？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;怎么撩妹效果这么好？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;厨房中的气氛尴尬无比，一时间，房间里安静极了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓也知道女孩面子薄，他开玩笑似的打破了沉默，道：“看来你收到了我对你的祝福。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪猛地抬起头，恶狠狠的白了江晓一眼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果是在10分钟之前，江晓可以保证，这样的白眼会是冷冷的一瞥，带着鄙夷和不屑，会刺痛江晓的神经。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是现在，韩江雪这样的状态之下，她的白眼却是奶凶奶凶的，像是一个毫无战斗能力，却依旧要在弟弟面前保持威严的小姐姐。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黄铜品质的“祝福”威力竟然如此之大？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那升级品质之后的“祝福”呢？怕不是要上天？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓仔细的回想了一下，按照江小皮的记忆，星技与星宠的品质都是用这样的品质划分的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黄铜品质上面还有白银品质，然后是黄金品质、铂金品质、以及已知最高的钻石品质。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在江小皮的记忆中，大抵可分为五个等级的品质，黄铜最低，钻石最高，根据网上资料来说，据说钻石品质之上还有更高级别的品质，只不过世人并不知晓，估计那些神级的人物才拥有更高品质的星技和星宠。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;世人并不知晓？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是穿越来的江晓知道啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这还用问么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;钻石上面是大师！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大师上面是王者啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;......</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“真是幸运，竟然是医疗技能。”韩江雪收获了满满的祝福，那清冷的声音也柔和了不少，“北江大地盛产白鬼一族，获得星技的觉醒者中，真正吸收到‘祝福技能’的却是凤毛麟角。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯。”江晓知道刚才自己有点调皮，女孩身为一家之主，又要强装威严，所以，此时的江晓特别乖巧的点了点头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不，你并不了解医疗技能的珍贵程度。”韩江雪轻轻的叹了口气，看待江晓的眼神，像极了看待地主家的傻儿子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“一百个觉醒者里面，拥有医疗技能的觉醒者，恐怕只有一个。这种特殊类别的星技很难获取，全世界充满了暴力输出的法系觉醒者、近战系觉醒者，但医疗系觉醒者真的是少之又少。”韩江雪似乎是松了口气，打开了话匣子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你知道，一个辅助系、尤其是拥有医疗系星技的觉醒者有多么吃香么？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪继续道：“我们毕业之后，会加入到国家各种各样的机构之中，绝大多数机构都存在着外勤人员，四人小组是外勤人员的基本配置，任何觉醒者都希望这四人团队中，拥有一个医疗系觉醒者。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“医疗系觉醒者代表了队伍的续航能力、平稳的战斗力，甚至会在关键时刻拯救团队的性命。哪怕在这个医疗系觉醒者的星图之中，只拥有一个医疗系技能就足够了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪赞叹的看向了江晓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是江晓睁开眼睛一来，第一次看到这个高冷的小姐姐给予自己肯定的眼神。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“可惜，只是黄铜品质的祝福。”韩江雪话锋一转，虽然嘴上说这可惜，但是表情上却没有多么大的变化，道，“我不用再担心你的未来了，正常上学，毕业之后我帮你找家医院，当个医生，也算是一条出路。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓：？？？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我的命运就这么被你安排了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这具身体可是刚刚初三毕业啊？那句话怎么说来着？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我现在可是祖国的花朵啊？只要我努力，我可是拥有无限可能的！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江小皮啊江小皮，你到底是让你姐姐有多失望呢。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我本来还想要走出一条成神之路呢，结果韩江雪直接给我把路铺进医院去了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;诶？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;等等。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;话说回来，当个医生其实也挺好，正常医疗手段解决不了的事情，江晓通过星力和星技就可以解决。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;以后，随着江晓将“祝福”技能的品质提高之后，说不定还能够拯救一些濒死垂危的病人，倒也是功德无量。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这不也是真正有意义的事情么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想到这里，江晓不由得陷入了沉思，他才来这里不到两个小时，经历了翻天覆地的变化，接受了大量的新鲜事物，的确需要一段时间，静下心来思考一下自己的未来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;毕竟此时的江晓不是一个16岁的懵懂少年，而是一个拥有着成年心智的25岁成年人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;接受、融入这个光怪陆离的世界之后，对自身进行人生规划，寻找自己真正的志向，成为了江晓的第一要务。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;医生这样的职业的确高尚，也的确对社会有益。但如果只是这样的职业的话，是否太过正规了一些？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;既然不小心进入了这个光怪陆离的世界，那么就要开启一段光怪陆离的旅程，否则都对不起自己穿越过来啊？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;呃...</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;否则都对不起小姐姐一巴掌把自己扇过来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;星力、星技、星宠，再加上网络上叙述的次元世界，花花万物等待着江晓去尝试。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而这具身体还附加的一个内视星图，如果江晓真的选择了一个常规职业的话，那可真的叫做暴殄天物了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪看着不动声色的江晓，心中微微诧异，如果实在平时，弟弟早就已经“揭竿而起”了，大声叫嚷着不当医生，不工作，要出去玩，要出去浪迹天涯......</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此时的江晓，却安静的出奇。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;也许是因为刚刚获得星技，尚未消化这一结果吧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪起身走到江晓面前，伸出一只手，摸向了江晓的脑袋，却在半空中停顿了一下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那只纤白的手掌停滞了一下，到底还是缩了回去，道：“不要四处招摇，也不要拍摄那些乱七八糟的视频，好好的熟练星技，吸收星力。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说着，韩江雪走向了厨房门口。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唰......</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一个身高172cm，有些瘦弱的男孩突兀的出现在了厨房门口，挡住了韩江雪的去路。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪愣了一下，一双美眸直视着眼前江晓的漆黑眼眸，却看到青年开口说话了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然她面前的青年开口了，但是那声音却来自她身后的餐椅。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我的确是刚刚觉醒，但我知道，觉醒者也是分类别的，获得了低品质医疗星技的觉醒者，吸收其他高品质医疗类型的星技会有更高的成功率。”江晓继续说道，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“而那些暴力输出类型的觉醒者，哪怕是天赋异禀，资质再高、星槽再多，恐怕一生也与医疗系星技无缘，他们的体质不适合吸收这类技能。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪的眼眸微微瞪大，低头看着眼前的江晓，缓缓转过身，看向了身后坐在餐椅上的江晓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;幻象！？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白鬼巫第二星技：诱饵！？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪怔怔的低头看着江晓，一星双技！？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这样的情况在人类历史上也出现过，不过这样幸运的觉醒者简直就是凤毛麟角！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你是怕我太抢手了。”江晓转过身，看着沉默的韩江雪，分析道，“你知道我的资质不高，星槽更是少得可怜，但偏偏却是个医疗系觉醒者。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你知道在医疗系觉醒者如此缺少的情况下，我就算是实力再不济，也可能会被招入一些机构中，参加相对危险的任务。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪微微俯下身，极为严肃：“在你的星图中只有一颗星点亮的阶段里，不要在外人面前用出第二技能。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“没问题。”江晓果断的点了点头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“没问题？”韩江雪意外的挑了挑眉，她本以为按照弟弟的性格，恐怕会更加的自以为是，更加的无法无天，结果弟弟却一如既往的乖巧听话。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我也有一个条件。”江晓突然开口道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“条件？”韩江雪点了点头，难怪这小子这么听话，原来是有求于自己，“说。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我会努力修炼的。”江晓抬头看着韩江雪，继续道，“你再去雪原的时候，带上我。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;看着江晓极为认真的表情，韩江雪面无表情的回应道：“我凭什么答应你？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;江晓耸了耸肩：“作为回报，我可以送你几个祝福。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩江雪急忙开口制止道：“不，不用......”</p>',16,5),(26,'全职法师','第1章 世界大变','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;水兰中学外，小雨淅沥沥的下着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;校门外，挤满了撑着雨伞的家长，他们被雨水淋湿的脸上满是期望。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;今天可是他们孩子的升学大事啊，能不能上天澜魔法高中，能不能将来考上一个好大学就看孩子今日的表现了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;你没看错。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是魔法高中！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个社会，魔法为尊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有的孩子都需要接受九年义务魔法教育，学完九年魔法基础理论与魔法生活技能之后，都将接受和中考一样的考试，考试过的人，才能够进入魔法高中，成为一名真正的魔法师！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;桂花被雨水打落在校园笔直的道路上，铃声响起之后，学生们蜂拥而出，要么独行，要么寻找着自己的小伙伴，讨论着今日的考试内容。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“喂，慕白，元素魔法的七系，冰系、火系、土系、水系、风系、光系、雷系，这道题你们做出来了吗？”一名戴着小领结的白脸男生问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“完美解答！”那个无论是发型、身高、长相都堪称男神的慕白说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不愧是我们的尖子生，这么难的混系问题都能够做出来……咦，那不是模拟考上只靠了个6分的莫凡吗？”满脸麻子的赵坤三说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;男神慕白眼睛马上不屑的瞥了一眼那个独自一人打伞的少年，道：“这家伙马上要跟他老爸一样，当一个破皮卡车采购员了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那不就是你们穆家的仆人了吗，哈哈哈！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这三人说话很放肆，根本就不怕旁边的那个叫做莫凡的少年听见。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嘲笑完之后，这三人自顾大摇大摆的离开了，显然他们有足够的信心能够考上魔法高中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;打着一把黑色的黄飞鸿伞，莫凡自顾走出了校门外去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;回头望了一眼这个即将告别的校园，莫凡千言万语化作两个字：坑爹！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;能不坑爹吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡本来不属于这个世界，他是一个天才学生，对平日里的考试其实压根不屑，从来都是交白卷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;被同学、老师们认为是学渣很正常，就像一个大学生没有理由要为了那小红花而浪费一个多小时时间在那里干坐着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;原本，莫凡就打算中考的时候再稍稍认真点，随随便便拿个全校第一、第二，用最打脸的姿态踩着慕白那种傻x到全城最好的高中……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;谁知意外发生了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自己一个大好学生，在学校后山睡了一觉，尼玛忽然间跑到了这个世界来！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;魔法！！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身边的同学、老师、老爹、妹妹都没有变，尼玛变得是整个世界。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自己那崇尚科学的世界，在自己一觉醒来变成了魔法为尊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;神马数理化，变成了黑魔法、白魔法、异次元魔法！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;神马学系，变成了火系冰系大雷系！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;你变就变吧，莫凡其实一点都不抵触这魔法，甚至知道魔法真正存在，整个人都斯巴达了，偏偏就要在自己快考试的时候世界大变。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;搞得自己对魔法知识一窍不通！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;于是，从一个想要扮猪吃虎的学神一下子变成了真学渣，受尽了嘲讽、耻笑啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唯一值得庆幸的是：这九年义务魔法，学的都是一些杂七杂八的理论知识，只是一些魔法与生活息息相关的东西，真正的魔法要到高中才会接触。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;成为真正的魔法师，必须经过一个重要的步骤，那就是“魔法觉醒”！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;魔法觉醒会在高中开学典礼上，全校领导老师见证下开启，那个时候哪怕你前面的理论没有学，一样是有可能开启属于自己的系！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡其实是真的喜欢魔法，这对他来说无疑是一次完美的奇幻重生。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他也想要成为一个能够傲立高楼穹顶上方舞火唤雷的至高法师，为拯救世界做那么一点贡献。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;偏偏，这次考试……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这么说吧，经过他临考前不懈努力，他终于达到了看懂题目的境界，至于答案是否正确……呵呵，开心就好。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“莫凡，莫凡……”人群之中，一个脸色蜡黄的中年男子朝着莫凡高举着手。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡看着这个熟悉的面孔，有些意外，叫了一声：“爸，你怎么来了？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“来接你啊，考完试你也算毕业了，我给你找了一份在隔壁城区跑业务的活，你广丰叔叔会带你，跑个几年有经验了就可以自己干了，运气好一个月拿个四五千应该没问题，早点出来工作也好。”莫家兴笑容可掬的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;世界是变了，但爸还是这个爸，莫凡可以感觉得到那股子什么都无法改变的亲切。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;去跑业务这事莫家兴和莫凡提过了，本来想给老爹一个惊喜的莫凡只能够苦逼的接受自己确实是学渣的这个现实了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;问题是，现在他不甘心。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个社会汽车、手机、电脑、冰箱都有，科技的产品一点也没有因为魔法的取代而改变，假如成不了一个魔法师，那么你就会成为这些东西生产的工人。妈蛋，这和自己在原来的世界有什么区别，老子要学魔法！！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;按照这个世界，其他东西都不会改变的理论来看，自己的天赋应该也是不变的才对。高中只要多加努力，把过往的那些东西补起来，自己依然可以成为一个真正的学霸……不，魔法霸！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“爸，我想继续学。”莫凡沉默了良久，还是将自己心中的想法告诉了莫家兴。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你不是不喜欢学魔法吗？”莫家兴挑起了眉毛，一脸奇怪的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呃……”莫凡有点蛋疼，也不知道自己说实话老爹会不会信，唉，心塞。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫家兴看着自己马上就满十六岁的儿子，脸上又挂起了憨厚的笑容道：“没事的啦，爸又不会怪你没好好用功学魔法，人各有志。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不是，我是真的想学了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你考试能考上吗？”莫家兴问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不能。”莫凡很肯定的回答道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那不就得了，没事啦，虽然古人常说：万般皆下品，唯有魔法高，但也有行行出状元的说法……”莫家兴继续说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡听完，不自觉的吧唧了一下嘴。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;现在很多信息莫凡要自动转换，但转换的过程中就让莫凡特别无语，比如说莫凡记得有一天历史老师告诉同学们：光系的魔法鼻祖是“爱迪生”，当时莫凡内心就有亿万草泥马在奔腾！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;老爸莫家兴拍着莫凡的肩膀，宽慰宽慰着的时候，莫家兴突然发现自己儿子保持着沉默，神情显得不太对劲。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;知子莫若父，莫家兴慢慢的收起了笑容，声音转低沉了一些道：“你是认真的？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“恩，我想获得那个觉醒的机会，虽然我知道现在后悔晚了，但我真的想好好学，想成为一个魔法师。”莫凡很诚恳的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自己需要的仅仅是一个机会！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫家兴保持了片刻的沉默。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡也没有再说话。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“真的想学？”莫家兴再一次确认一遍。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“真的。”莫凡没有一点犹豫的点头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;起初莫凡也觉得是自己在做梦，世界和自己开了一个玩笑，可事实证明，世界真的变了，这不是在做梦！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那好吧，我想想办法。”莫家兴并没有再多说。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“爸，我在天澜魔法高中找了一份暑假临时工做，看管图书馆的，后天就去上班。”莫凡说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;既然决定了要学魔法，莫凡就得打起十二分精神。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;能不能让自己上魔法高中获得那次成为魔法师的觉醒机会就看老爸这边的了，而自己对知识的匮乏是需要靠自己努力去填补，所以在明知道自己没有一点希望考上魔法高中的情况下，莫凡提前找了这份差事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这份工作，工资几乎没有，管吃管住而已，但对莫凡来说却真的很重要，图书馆里他可以找到太多自己需要恶补的东西了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;还好，自己确实是个天才，短短时间，已经把小学一二年级的课程给补完了……妈蛋，这句话怎么都觉得自己是个弱智！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一旁，老爸莫家兴见自己儿子这么坚决，也心有所动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;儿子既然一心求魔，自己又有什么不值得高兴呢，毕竟这个社会终究是魔法师才有真正的地位，跑业务一辈子再有房有车，也抵不过一个从魔法学校毕业的魔法师来得有价值，来得让人尊敬。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“先回家吧，这事包在老爸身上了！”莫家兴点了点头，不再多说什么。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;------------</p>',4,1),(27,'全职法师','第2章 真实的阶级','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是一片围绕着一座半城山而建的一片住宅区，顺着那条有着铁栅栏的道路往前走，一直走到小路的尽头就是莫凡家了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;矮矮的一小栋，一层半高，外漆已经斑驳露出红砖，周围有很多杂物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;街坊邻居的房子基本上都有三层半了，经过了一番装修翻新之后显得更像是一个家，而本身就最角落又低矮的莫凡家就显得格外寒酸老旧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“莫凡哥，回来了啊……我给你带了个好消息咧。”刚到家门口，一个跟泥猴子一样的少年就跳了出来，满脸喜悦的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这泥猴子叫张候，也是这片老街区的孩子，算是和莫凡一起长大的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“什么好消息？”莫凡问了一句。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“小公主回来咯，我今天在山庄门口看见她了，哇塞，你不知道现在小公主长得多漂亮，简直就是小天使。”张候有些小激动的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡看了一眼这条街迎面着的山庄。山庄是一片精致到令整个城市都羡慕的绿化，每一寸土地的花草树木都经过了精心的雕琢，达到真正园林的级别。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过，这漂亮的城山园林此时是被高铁栏给围了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;记得小时候，铁栅栏是没有的，自己经常带着这条街的孩子们在这座山庄庄园内玩闹。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山庄最高处有好几栋非常精致的欧式别墅，在他们这群熊孩子眼中就跟童话故事里的城堡一样，而城堡里也确实住着一个美丽得让人忘记呼吸的公主，和他们年龄差不多，莫凡经常带着一群小孩把公主拐带出来一起到处玩耍……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可不知什么时候，山庄多了铁栅栏，街道的大人们都不允许孩子再闯到山庄里去，而那位原本和大家玩成一片的公主就真的成为了城堡里的公主，随着年龄的增长变得遥不可及，见到的次数越来越少了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你知道吗，我听说小公主现在是帝都名校魔法尖子，天生对冰系魔法有着别人无法媲美的能力，才15岁就已经可以释放冰系魔法了。”张候神神秘秘的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡愣了愣，假如张候跟自己说小公主获得什么全国奥赛奖这些，他估计毫无感觉，但如果是冰系魔法师的话，那就真的了不得了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;绝大多数人都是要到16岁，也就是高一的时候才接受觉醒，获得自己的第一系魔法。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;获得之后并不意味着你就是魔法师，你还需要经过非常漫长的修炼，还需要获得魔法书、勤加练习才能够释放出一个魔法来，这个小公主可真了不得，15岁就已经是真正的魔法师了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;难道这就是所谓的神童？魔法界的神童！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“莫凡哥，我好替你可惜啊，当年你再加把劲没准就把天真无邪的小公主给拿下了。才貌双全，啧啧啧……羡慕死我们了。”张候挑着眉毛说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那小时候的屁事，说个毛线。”莫凡没理会他。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫家兴听着这两个少年说这种话，咳嗽了一声，托着莫凡回家去了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刚回到家，爸爸莫家兴就道：“我出去一会，心夏在姨姨那住，应该不会回来。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好，我知道了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫家兴步伐匆忙的离开了，莫凡在家走了一圈，发现家里其实并没有任何的变化，一样的家徒四壁。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;世界都变了，但家庭的穷酸一点都没有变，怎么自己家庭没和山庄上那户人家调换一下，老天爷也真是的大费周章的将科学搞成魔法，难道这点小事都不顺便一下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唯一还值得庆幸的是：自己样貌没变，依然风流倜傥！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在家坐着也无聊，什么东西都没有，莫凡无聊下出去逛了一圈，想看看其他东西是否发生了变化。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;顺着很少人走的苔藓小道，刚要拐到大街，莫凡正好看到自己老爸的老皮卡车。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;老爸是司机，以前是给山庄上面的老爷开车的，后来不知道怎么的调到后勤去了，基本上去帮山庄上面的人采购东西，从那以后家庭和状况急剧下降。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“家兴啊，你这样的要求就有些无礼了。你该知道我以前也对你们不薄，你们家小子做出那样的事，我还留了一个采购给你做，换作是别人家我就直接让他卷铺盖走人了。”一个中年男子语气缓慢的声音传了出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“穆贺哥，就当我是最后一次找你帮忙，天澜魔法高中买进去的话真的要太多钱了，我家的情况您也是了解的，实在负担不起。”莫家兴显得有些低声下气的声音慢慢的传来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你啊，干嘛为你那个没出息的儿子这样，他自己没本事考上魔法高中，就让他自生自灭好了，也快16岁了。而且啊，就算我这次帮你，把他弄上魔法高中，以他的尿性，肯定还是不务正业，成不了真正的魔法师，想当魔法师不是那么容易，不仅要靠自身天赋，后天努力，除此之外，那些魔法书、魔器、魔具你们家买得起吗，没有这些辅佐，他连一个初阶魔法师都成为不了……”那个叫做穆贺的男子用一种语重心长的口吻说道，但是莫凡听到的尽是一股子趾高气昂。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“他这次是真的想学。穆贺哥，您这次愿意帮我的话，之前穆老爷说让我们搬出穆家庄的事，我们马上就搬出去，这样也让穆老爷安心一些，我也保证我那臭小子肯定不会再去找穆宁雪小姐了。”莫家兴的声音传了出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦，这倒是有的考虑考虑。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一听到他们愿意搬离这里，那名叫做穆贺的男子似乎有兴趣往下谈了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;墙边，一个少年依靠在墙壁上，听着这段对话心情却变得无比复杂了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;还以为世界变了，很多可怕的关系也会改变，原来……一点都没有变。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如同统治者一般的穆山庄的富人还是高高在上，在底层挣扎的爸爸仍旧在摸爬滚打，需要处处求人，这个穆贺是天澜魔法高中背后的董事长，其实他只要一句话就能够让自己进入魔法天澜高中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是在听到自己老爸莫家兴说愿意搬出这莫家庄的事之后，这个穆贺像是松了一口气，马上爽快的答应了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最终，这段对话还是以老爸莫家兴不停的感谢穆贺为结束，穆贺也驾着他的豪车离开了，留下了那辆在风尘中显得孤独老旧的皮卡，就如自己老爸莫家兴一样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这哪里是梦？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;残酷得跟现实一模一样，靠在墙边呼吸显得沉重的莫凡清楚的意识到自己的家庭状况没有一点点的改变，卑微的地位也没有得到改变。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只有旧社会才有的老爷、小姐称呼，事实上在当代也没有真正意义上的结束，某些拥有历史底蕴的家族、世家他们仍旧占据着上位者的身份，尽管为他们服务的人不再叫仆人，叫工人，尽管不需要再行礼下跪，但这群底层的人命运仍旧被这些富人、有身份的人死死的我在手心里，随意摆布着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自己就是出身在这样一个底层、被一个姓为穆的世家所支配着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;心底仿佛有什么在剧烈的涌动，拳头更是紧紧的握着，重重的砸向了泛青的墙壁。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你们穆家，莫欺我年少，莫欺我落魄！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我飞黄之时，一定要你们十倍、百倍偿还！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;――――――――――</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;乱语：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一定，一定，一定要记得投“推荐票”，一定要“加入书架”，新书《全职法师》拉开绚丽序幕！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;让你们的热情，来得更猛烈一下吧，让我知道你们有多爱争锋，有多爱乱叔，又会将多少炙热延续到《全职法师》！！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;乱盟的兄弟们，我在呼唤你们！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;轰炸吧！！！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;------------</p>',4,2),(28,'全职法师','第3章 开学大典','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡回到家，脑海里全是穆贺那番话，那番话看似漫不经心、语重心长，却已经透出了太多对莫家兴这个家庭以及对自己的那种不屑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;连一个初阶魔法师都成为不了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;老子呵呵你一脸！！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡承认自己在有一段时间和小公主穆宁走得很近很近，但年幼的他根本不知道这会给自己这个贫苦家庭带来一场灾难，从那一天起莫凡就知道这个社会依旧存在着三六九等。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“莫凡，哈哈哈，你的事情解决啦，我打电话给我一个老朋友，原来他现在可牛b了，是天澜高中的教导处主任，他说只要你好好修炼，他可以让你进天澜魔法高中，让你觉醒你命中的那一系魔法，这种机会可是一辈子才一次，这次你一定要努力，知道吗？”脸色蜡黄莫家兴满脸笑容的走了进来，手掌拍在莫凡的肩膀上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡看着老爸的喜悦笑容，换作平常莫凡也会高兴的和老爸喝一周白酒庆祝一下，可在知道真相后的他哪里还相信得了老爸这忠厚得爽朗大笑，更让莫凡不愿意去相信自己尊敬的老爸其实在这个社会从来就没有被人尊重过。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“真的吗，那太好了，不愧是我老爸，什么事情都能够搞定，哈哈。”终于，莫凡也换上了一张笑脸，跟自己老爹勾肩搭背了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那是当然，也不看你爸是谁？”莫家兴觉得这小子马屁拍的不错，脸上的笑容就更多了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两父子晚上还是喝了几杯，喝酒过程中莫家兴用委婉的说道：“莫凡啊，你就直接住校，我打算把家租出去，给我们生活多点补贴。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡其实也知道，这个家相当于卖给穆贺了，这个破屋子不值钱，但这块地在城区却价格很高。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“行啊，租出去吧，反正我也很少回来，就是心夏那边……”莫凡佯装不知的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“心夏就在你姨姨那了，说实话我们两个大男人也不好照顾心夏，这丫头懂事，不用我们担心太多。”莫家兴说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那行，你记得多来学校看我。”莫凡说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“知道了，困了困了，我先睡了，你明天就去学校吧。”莫家兴喝掉了最后一口白酒，有些摇摇晃晃的站了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡看着他转身，看着这个背影突然间想起了朱自清的那篇文章。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自己住校，心夏住姨姨家，两个孩子都安顿好了，那么这个中年人该住哪？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最后莫凡也没有说穿这件事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;事实上，莫家兴愿意做出这样的牺牲来对他来说是一种做父亲的职责和荣耀，他想让自己的儿子知道，你放心的去学，你解决不了的事情我会帮你解决。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而莫凡也知道，把事情说穿了，只会让莫家兴感到悲凉和无地自容，毕竟哪个男人不想做一个什么事情都可以为儿子摆平的牛b老爸？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡不能说穿，别人可以不尊重老爸，但自己不可以！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这小破屋，不住就不住了，最多三年，等自己魔法大乘，什么都会有的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个社会，魔法为尊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自己也一定会成为顶天立地的法师尊者！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;暑期两个月时间过得飞快，莫凡感觉整个人都掉进书海里了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天澜魔法高中的图书馆什么书都有，而有关魔法学的书更是跟科学一样复杂、众多，是真正意义上的学海无涯。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过，莫凡已经收获很大了，至少他已经不再是文盲……哦，魔法盲了，九年义务魔法教育教的东西莫凡基本上掌握，大有张无忌乾坤大挪移神功炼成脚踏光明鼎的气势了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;九月一号开学，开学这天对所有魔法学生来说都是无比重大的日子，因为这不仅关系到所有学生踏入魔法师的最重要一步，更关系到了学生们将成为哪一系的魔法师！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;觉醒，顾名思义，就是让身体里潜在的力量苏醒过来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“小鬼，祝你好运啊，最好觉醒一个火炎系，这会让你在魔法学生里面占很大的便宜，至于其他系虽然也还可以，但从初阶魔法师战斗力来说还是火系最好，哦，岩系也不错……风系也可以……”图书馆总管理顾老头说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡应了一声，没理会他了，这老头说起话来没完没了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说实话，莫凡昨晚一整夜没睡，对他来说今天的觉醒不单单是一次命运的改变，更是前所未有的尝试，在原来的世界里魔法这种东西是不可能存在的啊，今天自己就可以真心实意的握住了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;小心脏是活蹦乱跳的！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡在高一8班，学号最后一位48！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;没办法，他基本上是走后门进来的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;学号越前面就意味着魔法中考时分数越高，据说这种人一般觉醒了某系之后，修炼速度会比正常人快，因为他们对整个魔法知识融会贯通，修炼起来遂心应手。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很不巧，这个高一8班的1号，就是莫凡的老同学-穆白</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;穆白其实就是穆氏世家的人，貌似是一个旁系寄居在山庄里的，他之所以一直都看不起莫凡自然是因为在穆氏山庄里他得算是小少爷，莫凡就是仆人得儿子，也是仆人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可惜这家伙一个旁系子弟和穆宁雪这种小公主级别的不是一个层次的，穆白以前就各种讨穆宁雪欢心，穆宁雪甩都不甩他。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;穆白魔法成绩很好，在穆氏世家里应该算是重点培养对象了，据说这家伙若是觉醒了穆家的家族系寒冰系的话，他以后修炼速度更是提升好几倍，毕竟穆世家有太多的世家资源可以提供了，分到穆白这样一个旁系子弟上都和普通人没法比的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“同学们，欢迎你们入校，想必你们很多人都在等待今天这个令人激动的时刻了，在此之前我有很多话要跟大家说。”大魔法操场上，全校新生一千五百人分成二十个班级整整齐齐的形成方块站队，看上去无比的壮观！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“同学们，你们知道成为魔法师是为了什么吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你们无论是在魔法师的哪一个位置都不要忘记，我们魔法师以探索人类发展，以守护人类为最重要的使命，可别忘了在你们安逸的城市之外，还有无数妖魔虎视眈眈！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;校长说了很长很长的一通话，终于到了大家最想听的了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好，今日觉醒开始，希望你们能够成为未来朗朗升起的魔法之星！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;老校长的话音刚落，操场上学生们已经按捺不住内心的喜悦了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;谁不想操控烈焰充满摧毁之力，谁不想掌控寒冰冻住一切邪恶，谁不想驾驭大气驰骋天地，谁不想催动岩土抵御入侵？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在无数的故事、视频、电影之中有太多的英雄值得他们崇拜，并且渴望成为那样的魔法师，今天就是他们踏上魔法师旅程的第一步，无论是在城市之外的妖兽领域里骄狂，还是掌控魔法都市的至高权力……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（心情好，再更一章！开新书，就是任性！）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（全职法师的更新时间会很稳定~~~中午12点整，和晚上10点整，一般情况下都是2章送上，任性的时候3章，往后走自然是会多更新的~~~大家鼎力支持！推荐票，要记得投啊！！）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;------------</p>',4,3),(29,'全职法师','第4章 法系觉醒','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;觉醒是20个班级同时进行。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡是48号，他将是全班最后一个去接受觉醒恩赐的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;班主任，也就是大家的魔法导师-薛木生已经站在班级的最前列，觉醒会在大庭广众之下，学生们要做的就是在被叫到名字的时候走上前期，将双手放在魔法导师面前的那个觉醒石，静静的感应。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我说，走后门进来的，你不会觉醒失败吧，我听说某些真的很没能力的人会出现觉醒失败情况，像你这种渣渣感觉还是不要浪费昂贵的觉醒石的能量了。”另外一列队上，一个莫凡的老同学赵坤三说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;赵坤三是穆白的狗腿子，穆白自己觉得去找莫凡麻烦是自降身份，所以多半会让赵坤三出手。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;赵坤三说的觉醒失败的情况是有，其实莫凡也挺担心这个的。严格上来说他是……恩，算穿越过来的吧，要是体质和这里的人有差别，那这玩笑就开大了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“莫凡哥，别理这****，等等觉醒一个火系给他看看，叫他知道什么叫狗眼看人低！”和莫凡分在同班的泥猴子张候说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡没说话，换作以前自己一定用自己那骂人不带重样的口才喷死这赵坤三狗腿子，今天没那个心情。一方面是确实很紧张，另一方面不知道为什么自己脖子上戴着的那个黑色坠子一直发出奇怪的颤动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个黑色坠子是原来初中后门门卫嬴老头的遗物，莫凡很怀疑这世界巨变的罪魁祸首就是这货，那天莫凡就是戴着这坠子在学校后山睡觉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;今早上，这破坠子就跟中邪一样，不停的颤，颤的自己的手都不停的抖，抖个没完。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;妈蛋，你抖毛啊抖，你就一枚坠子能不能老老实实的做你的坠子！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“穆白！”班主任薛木生喊了一句。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呀，他就是穆白啊，长得可真帅，成绩还是第一哦。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是呢，是呢，今天一早我就看到他了，好喜欢，不知道他会觉醒什么系呢，估计不是水系、光系那种前期废系吧？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;穆白一站出来，马上就听见了班级里有几个女生在那里叽叽喳喳，同时引得别的班一些花痴在那里频频侧目。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;穆白一副毫不在意的样子，眼神却很享受这种被人议论，他走到了班主任面前，脸上带着谦逊和自信的笑容。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“穆白，是有名的寒冰世家穆氏世家的吧？”班主任薛木生微笑的问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“正是。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好，你如果觉醒的是寒冰系，你应该会比别人优越很多，但别忘了多加努力，天赋毕竟决定不了一切。”班主任薛木生说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;穆白点了点头，心里却暗暗道：是，天赋决定不了一切，但没有天赋和家底，就永远生活在社会的最底层！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在班主任的许可下穆白慢慢的将双手放在了那枚悬浮的觉醒石上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;觉醒石呈现厚星罗盘状，有一个明显掌印，穆白看似平静内心却一样紧张不安的将手放在觉醒石上面……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;以他的血统，觉醒寒冰系的可能性最大，但也有可能发生一些特殊情况，万一觉醒成凌水系或者圣光系，估计穆家那边就不会再那么重视他了，毕竟他们不可能浪费财力去专门给自己找相应属性的资源。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;右手放上……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;觉醒石上突然泛起了星辰光辉，这些光辉连成了一条条星痕如同特殊的血管血脉一样爬到了穆白的右手上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大家都是第一次亲眼目睹觉醒，同学们一个个踮起脚看着穆白。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;穆白站在那里一动不动，被醍醐灌顶一般，头颅微微的扬起……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咯吱，咯吱，咯吱~~~~”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，觉醒石周围，一股寒霜之气在那里缭绕。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这些寒霜越来越强，竟然是慢慢的冻结了觉醒石的边缘，结成了一片小冰霜！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“寒冰系，真是寒冰系啊！！！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，身后有人大叫了起来，其中还伴随着一些女生的尖叫声。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;穆白已经听到声响了，在欣喜中睁开了眼睛！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;果然是寒冰系，拥有了穆家本源系，自己就更容易被穆家接纳了，飞黄腾达之日不远了，收拾莫凡这种小社会渣渣不跟捏死一只蚂蚁似的……唉，不对，觉醒了寒冰系，自己算是穆氏世家正统弟子了，自己哪还有必要跟这小瘪三一般见识啊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;远见要高，恩，自己远见要高，说不好还有机会和穆宁雪一起修炼！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“很好，是很纯净的冰系，我想你的精神世界里应该已经出现了一片冰系星尘，多加修炼，让它更加壮大吧！”班主任薛木生眼中满是赞许。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;能够在觉醒的时候就冻结了觉醒石，说明这种寒冰系力量超越了常人，算是很有天赋的寒冰系学徒了，此子日后应该大有出息！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个，邱月英！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;班主任话音刚落，一个脸上带着几分英气的女生走了出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好，土系，你的星尘起步不错，看来和你之前的努力是分不开的！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个，许青林！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“也是寒冰系。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个时候莫凡有特意踮起脚去看，他发现同样是寒冰系的觉醒，那个叫做许青林的家伙在觉醒的时候只是出现了一层淡淡的霜雾，并没有让觉醒石冻成冰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;由此可以看出同样是寒冰系，许青林无论是天赋还是修为都要低穆白很多很多。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个，陆小斌。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“恩，水系！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“啊啊啊，我的天啊，我怎么会是水系，我应该是火系啊！”顿时那个叫陆小斌的同学怪叫了起来，方圆一百多米都听得见。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不要灰心，修炼什么系都一样，只要你能够达到中阶魔法师级别的话，你将获得更多的优势。”薛木生眼中带了几分同情，却用宽慰的口吻说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陆小斌也没有别的办法，只能够失魂落魄的回到自己位置上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其实，一开始的觉醒很重要，若是火系的话在初阶法师级别就拥有一定战斗力了，往后修行估计也能够占到不少便宜。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;水系的话，初阶水系技能是-水御，防御效果没有岩系强，没有精修的话作用不是很明显，至少在初阶魔法师是这样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;------------</p>',4,4),(30,'全职法师','第5章 天生双系（上）','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“张候！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站在离莫凡不算太远的泥猴子张候回头给了莫凡一个眼神道：“莫凡哥，我过去了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好好加油。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哼，祝你也是水系。”赵坤三马上说起了风凉话。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张候就是一个瘦猴干，手往那里一放，没多久一股奇异的风旋涡在觉醒石上显现，吹得老师得衣襟都有些鼓动了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“很好，风系，天赋不错，要好好修炼，切勿偷懒！”薛木生脸上同样露出了满意之色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;班主任薛木生心里很开心，因为才到十几个号，自己班里就出现了两个优质天赋的学生，假如他们好好用功的话都是能够考上好的大学学府。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然最让自己捡到宝的是1号学生穆白，卓越寒冰系天赋，加上他背后的寒冰系世家，未来的栋梁之才了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;看来这一届8班应该有机会名列前茅。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“噗~~~~~”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;觉醒石上，一道橙色的火苗突然窜起。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一幕让全班人一下子瞪大了眼睛。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“火……火系！！！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“靠，居然还是个妹子，这女生叫什么啊？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一时间，觉醒的那个女生成为了全班的焦点，系算是元素系里面最为出彩的了，是所有魔徒级魔法师觉醒的首选，假如他们可以选择的话……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可惜火系比较傲娇，概率算低，一个班级里面最多也就3个，十分之一的概率都不到。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“她叫周敏，好像是我们某个老师的女儿。”有人小声的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“长得还很漂亮啊，赶紧去认识下。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那可小心点，这周敏看上去很文静很乖，其实是暴力狂，不然一个女孩子怎么觉醒的是火炎系。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有道理，有道理。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“算是女从母业，文华老师知道的话应该会很欣慰的。”班主任薛木生说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“谢谢老师。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……风系”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……光系”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“老师，我去天台，你别拉我。”那个光系的同学说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……水系。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“光系同学，等我下，我和你一起去天台。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;终于几乎全班的系都觉醒了，莫凡手握着48号学号，内心狂跳不止，恐怕谁都没有莫凡内心这么紧张，一方面他是最近三个月才接触到的魔法，另一方面他真的担心会出现觉醒失败！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“48号，莫凡！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“到你了！”张候转过头来鼓励道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“砸锅卖铁才进来的，就别抱什么希望了，运气好点弄个水系、光系，修炼不到中阶法师级别就废了，运气不好，觉醒失败，啧啧啧，直接什么都陪掉咯。”赵坤三满嘴的风凉话道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你能闭上你狗嘴吗？”张候有些怒道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在张候心里莫凡就是他大哥，虽然他魔法成绩很差，这次进魔法高中也是另谋途径，但他绝不希望看到莫凡觉醒的比别人差。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;九年义务魔法教育其实就是为觉醒打下基础，也为之后的修行打下各种理论，不至于修行中摸不着头脑，假如没有学好，一方面觉醒会失败，另一方面修炼止步不前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡迈开了步子，穿过了人群。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“就是他啊，好像是走后门进来的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是谁啊，长得还挺帅的。”一个小女生萌萌的问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“很渣的一个学生啦，我原来就在他隔壁班。”另一个蘑菇头大嘴巴的女生说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这样啊，那觉醒的可能很差。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“能不能觉醒成功还是问题。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我说，那几个圣光系的、凌水系的，你们不要灰心啊，等等你们看这位莫凡同学的觉醒你们就很欣慰了，这家伙考试个位数的，基础差得要命，觉醒很可能失败。”这个时候赵坤三和张候骂急了，干脆大声的嘲讽了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他这么一说，那些圣光系、凌水系的还真就擦亮眼睛了，假如有人奇葩的觉醒失败，那真是太让人……宽心了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不要喧哗！”薛木生瞪了一眼挑事的赵坤三。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;穆白嘴角却微微一浮，要的就是莫凡当众出丑，这家伙本就是一个渣，有什么资格接近穆宁雪大小姐，自己这么帅，魔法天赋又好，还那么努力，穆宁雪应该会和自己关系很贴近才是。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“渣渣，我可是听说你家把房子都卖了供你来觉醒，你要觉醒失败了真就找个河跳下去一了百了好了，免得给你们家蒙羞。”穆白细声细语着，就在莫凡从他身边经过的时候说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡看了这个穆白一眼，忍下了骂这个傻x冲动，走到了老师薛木生的面前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“莫凡，把手放到这上面来吧。”薛木生语气平和的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;作为班主任，薛木生其实很了解眼前这名学生的情况……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;魔法中考，基本上是倒数，因为上头的嘱咐才进了天澜魔法高中，据薛木生了解这莫凡就是一个普通家庭，没背景，没天赋，成绩还差得不敢看，这样得学生送到自己班上来也是一个头疼的事情，很容易就拉低全班的整体评估啊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;唉，没办法，算自己倒霉吧，但愿这小子别真的觉醒失败了，那自己真要成全年段的笑柄了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡手在不停的颤抖，终究还是有些紧张。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“平稳点，别抖。”薛木生提醒道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡用左手抓着自己右手，这才成功的把手掌放在觉醒石上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很冰凉，也不知是不是上个人觉醒的是寒冰系原故，整个觉醒时有一股冰凉……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;咦，不对，怎么开始发麻、发热了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“闭眼，感受自己的精神世界！”班主任薛木生说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;真是一个问题学生，这基础流程都不知道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莫凡急忙照做，闭上眼睛。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所谓的精神世界人在闭上眼睛的时候脑子里会出现一片虚无，正常人会在虚无之中不停的想象，像电影片段一样出现那些模糊的想象画面，而将自己的脑海完全放空什么都不想的时候，脑海就是一片虚无，如同没有星辰的夜空。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;精神世界里什么都没有，但是就在莫凡将手放在觉醒石上的时候，他能够感觉到一股电流的力量……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这股力量从手掌传递到全身，紧接着像是拥有某种神奇魔力一样突然在自己那片虚无的精神世界里划过了一道紫色的弧线，绚丽绝伦，震撼心灵！！</p>',4,5),(31,'圣墟','第一章 沙漠中的彼岸花','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大漠尽头，落日都显得有些诡异了，渐渐化成一轮蓝日，有种魔性的美，而火云也被染成了蓝色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风皱眉，虽然他知道，沙漠的天气最是多变，但眼前实在不太正常。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一片寂静，他停下脚步。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在进大漠前，他曾听当地的老牧民讲过，一个人走在沙漠中，有时会听到一些古怪的声音，会见到一些奇异的东西，要格外谨慎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当时他并未在意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;依旧宁静，沙漠中除却多了一层朦胧的蓝雾，并没有其他变故发生，楚风加快脚步，他想尽快离开这里。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大漠的尽头，落日蓝的妖异，染蓝了西部的天空，不过它终究快要消失在地平线上了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风的速度越来越快，开始奔跑，他不想呆在这种诡异、充满不确定性的地方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在沙漠中，海市蜃楼那样的奇景多发生在烈日当空下，眼下不相符，这不像是什么蜃景。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，前面传来轻响，像是有什么东西破沙而出，而且声音很密集，此起彼伏。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风倏地停下脚步，盯着沙漠，前方地面蓝光星星点点，像是散落一地蓝钻，晶莹透亮，在落日的余晖中闪耀着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那是一棵又一棵嫩苗，不足一寸高，自沙漠中破土而出，带着美丽的光泽，剔透而妖异，遍地皆是。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;短暂的停滞，随后沙沙声成片，蓝色灿灿，所有嫩苗都快速拔高，一瞬间生长起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天边，蓝日下沉，即将消失，雾气弥漫，浩瀚的大漠如同披上了一层诡异的蓝色薄纱。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“啵！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;花朵绽放的声音传出，沙漠中一片湛蓝，在夕阳即将消失的刹那，这些植物开始绽放出成片的花朵。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大量的蓝花，晶莹点点，犹若梦幻，有些醉人，遍开在沙漠中，非常不真实。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这种植物一尺多高，通体如蓝珊瑚般透亮，花瓣一条条，妖艳而迷人，宛若盛放在另一片国度，带着魔性，吸引人的心神。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风退后一步，然而，身后也已满是这种植物，蓝光流动，一眼望不到边。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他很吃惊，仔细的看着，努力辨认，这像极了彼岸花，一条条花瓣展开，又向后弯曲，极其美丽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过，彼岸花红的鲜艳，而它却是蓝色的，从未听闻有蓝色彼岸花。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;彼岸花真实存在，带着浓烈的宗教色彩，关于它有太多的传说，但楚风不信这些，只为眼前的景象而惊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沙漠干燥、缺水，只有极其稀少的耐旱植物偶尔可见，零星散落着。而彼岸花喜欢阴森、潮湿的环境，无论如何也不该在这里出现，还如此的妖艳。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这里遍地都是，一眼望不到尽头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大漠浩瀚，薄雾染蓝了落日，浸透了天边，而整片空旷无垠的沙漠都生出蓝色的彼岸花，说不出的奇异、神秘！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一缕淡淡的芬芳飘漾，让人沉迷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风用力摇头，小心的迈步，避开这些花，他发现只有一个地带没有这种植物，那就是———黄河古道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在岁月中多次变迁，几经改道，它贯穿这片大漠，如今已近干涸，蓝色彼岸花开遍两岸，拥簇着它。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;花开两岸，彼此遥见。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;终于，太阳沉下去了，而也正是在此时，这些植物盛放，花开到极致，化作蓝色的海洋，流光溢彩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然暮色降临，但这里蓝色光泽缭绕，极致炫目，艳丽的出奇。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风站在黄河古道上，心中无法宁静，但是他却不作停留，沿河道快速前进。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天色渐暗，最后的落日余晖也已不见了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蓝色的大漠光彩点点，而后突然间，砰然一声，所有蓝色彼岸花怒放后，竟然在一瞬间同时凋零。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;妖艳的花瓣枯萎，接着整株的植物开始干枯，它们失去色彩，耗尽生机，迅速发黄，而后碎裂，像是在一瞬间失去了数十年。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“砰！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最后的刹那，遍地干枯的蓝色彼岸花寸寸断裂，化成了粉末。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这诡异的景象，很难解释。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它们如同烟花般，短暂的绚烂，美丽到极致，而后便凋零，成为灰烬。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;枯黄的粉末落在沙地间，在暮色中很难辨出，而此时蓝雾也早已消失，大漠恢复了原样，像是什么都不曾发生过，再次宁静。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风没有驻足，大步前行，在暮色中，他翻过许多座沙丘，终于见到了地平线上的山影，要离开大漠了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天色渐黑，他终于走出来了，清晰的看到了山地，也隐约间看到了山脚下牧民的帐篷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;再回头时，身后大漠浩瀚，很寂静，跟平日没什么两样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山地前方，灯火摇曳，离山脚下还较远时就听到了一些嘈杂声，那里不平静，像是有什么事情正在发生。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此外，还有牛羊等牲畜惶恐的叫声，以及藏獒沉闷的低吼声。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有异常之事吗？楚风加快脚步，赶到山脚下，临近牧民的栖居地。</p>',2,1),(32,'圣墟','第二章 后文明时代','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时还有孩子在哭泣，女人在安抚。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有几名老牧民在祷告，很虔诚，最后更是跪下，朝着远方的高大山脉叩首，非常郑重。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于楚风的到来，牧民并不吃惊，因为经常有外来者路过，借宿牧民的帐篷中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很长时间后，山脚下的嘈杂声才变弱。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风用热水擦洗过身体，喝着浓香的酥油茶，身上的疲惫顿时消退不少，他将身上所有的糖果都送给了孩子们。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;几个孩子小脸红扑扑，那是高原红，带着腼腆的笑，看起来非常淳朴，分完糖果后一哄而散，很快乐与满足。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不久前这片山地究竟发生了什么，这是楚风心中的疑问，难道这里也曾经盛开近乎妖异的蓝色彼岸花？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;帐篷中的老牧民头发花白，脸上皱纹很深，带着明显的忧色，他看向帐篷外，凝视着远方的山脉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很快，楚风得悉，这里果真也出现过蓝雾，在山地中缭绕，许多牲畜都因此受惊，表现的非常暴躁，想要逃离。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过，并没有诡异的蓝色彼岸花，且雾气很稀薄。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“您为什么要向山中叩首？”楚风问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那是圣山的方向。”老牧民答道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;昆仑，素有神山、圣山之称，神话色彩最为浓烈，从《山海经》到《淮南子》，再到《史记》等，各种古册记载繁多。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;早先时，这片山地附近只有一层较浅的蓝雾，可是有人看到，在昆仑山脉方向却是蓝的异常浓郁。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那里大雾汹涌，晶莹通亮，氤氲沸腾，后来更是如同光束般，蓝光滔滔，炽霞一道道，非常的盛烈。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仿佛有一轮炫目的蓝色大日被浓雾包裹着，在那里沉浮，虽然隔着很远，但不时射出光芒，如同闪电般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;远远望去，无比神秘，蓝光璀璨，不断飞舞，极其耀眼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所以，一些年老的牧民向那个方向叩首，虔诚祷告。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;显然，那里的异象极度惊人，蓝雾浓郁的化不开，带着炽盛霞光，比楚风在沙漠中所见到的更甚。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这些神异景象到底因何而起？楚风思忖着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他想到了一种可能，或许是山中的地震引发的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;过去，有一地曾发生过类似之事，那山谷中时常突发惊雷，轰击活物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山脉如果发生剧烈地震，可能会引起磁场异常值超高，在电磁场效应下，云层中的电荷和山中的磁场作用，导致电荷放电，再加上极光效应，使那里色彩斑斓，成为十分特殊的雷区，迥异于常。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风并不迷信，觉得山中发生的事多半只是一种自然现象。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可是，任他怎么解释，老牧民说什么都不信，并且对他怒视，认为他亵渎了圣山，差点把他赶走。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;事实上，的确有说不通与牵强的地方，就是楚风自己也未彻底弄透彻呢，比如不久前大漠中的妖艳之花。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他轻叹，在这“后文明时代”，有许多事都无解，尽管人们努力用过去的规律来解释，可是这世界却越来越难以被理解了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;战争曾将大地半摧毁，险些化作废土，虽然经过漫长的恢复，大地再次生机盎然，但昔年的灿烂时代终究难以还原。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在后文明时代较为漫长的岁月中，曾经发生数起神秘变故，影响很大，可至今无解。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;清晨，红彤彤的太阳跃出地平线，灿灿的朝霞滑过山丘，落在帐篷前，草地上，尽显朝气蓬勃之象。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风告别这个部族，再次上路。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他一路向西，进入高原。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沿途，他了解到，那神秘的蓝雾波及范围极广，最起码所走过的区域都曾出现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不会又是一起神秘变故吧？”他自语。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;历史上那几次，曾闹出很大的风波，到现在还没有确切答案呢。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;藏区的天空格外的蓝，云层洁白，离地面很低，仿佛稍微一伸手就能触碰到，戈壁、山地、草场都很宁静，这里像是一片与世无争的净土。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一路上，楚风听到很多传闻。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有牧民说，圣山上的活佛苏醒了，所以才有蓝光流淌，大雾弥漫，遮拢四方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;还有人说是金刚菩提神树在生长，要开花结果了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“龙獒要出世了！”也有人这样说。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在当地人看来，真正的獒生在野外，可与狮虎为敌，而被人所豢养的不属于真獒。更有一种传说，圣山中有龙獒，数百年能出现一头，力大无穷，可以降魔。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;数日后，楚风临近圣山区域。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他已经了解到，沿途各地都曾出现淡淡的蓝雾，多半跟历史上的那几次一样，又是一次重大的异变。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，这也意味着，又像前几次那样，一般的人始终都不会了解到内因！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;甚至，大部分人都不知道会引发怎样的后果。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说也奇怪，本已是深秋季节，藏区的天气应该很冷才对，可近几日来，楚风一路西行，却渐渐感觉到了一股暖意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;前些天，黄叶凋零，败叶满地，而现在却不同了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;树上残存的叶子仿佛又恢复了生气，不再枯黄，不再飘落。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;尤其是临近昆仑，沿途上，无论是野草，还是荆棘树木等，在暖和的天气中绿的发亮，生机勃勃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;已是深秋，却缺少那种萧瑟。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“天气变暖了，难道是异变导致的？”楚风猜测。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;终于，昆仑山在望。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;隔着很远，就感觉到一种压迫感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;巍峨高大的山脉，气势磅礴，连绵而恢宏，宛若天地的脊梁，横亘在那里。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它壮阔而雄浑，无以伦比，古之大山莫出其右者。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这片山脉承载着无穷的传说，自古至今都笼罩着极其浓重的神话色彩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;原本进入藏区后楚风就要踏上回程了，然而一路上不断听闻到昆仑山异动，曾蓝光烁烁，他想临近看一看。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“就是这里。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风到了目的地，站在山脚下，巍巍巨山犹若神之巨城，恢宏而庞大，坐落在西部大地上，苍莽与雄浑之气扑面而来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这仅是昆仑山脉的一小段而已，前些天的那个傍晚这块区域曾经蓝光炽盛，附近的人都看到了，不过这些日子罕有人敢真正走近。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风进山，逐渐攀登。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山势渐高，有些陡峭，巨石横陈，路越发不好走，而一路上草木葱郁，在这深秋季节明显不正常。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“前些天真的发生过地震？”楚风观察着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山体上有龟裂的痕迹，地表有不少粗大的裂缝，还有明显是从山势较高处滚下的巨石，一些崖壁更是断落。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是这座巨山，曾经发生异常景象。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是什么？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风看到一块巨石，上面有字，刻痕很深，被土石埋着大部分。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;地震后，山体小部分断落、滑坡，这巨石是从地下深处显露出来的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大石上有一层绿意，像是干枯的苔藓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“西……王！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风用手抚摸石上的刻字，认出这两个字，这是金文，一种年代非常久远的文字，古时多刻于钟鼎之上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一般人很难辨识。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一刹那，浮思不断，楚风出神，怎么会是这两个字？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这里见到西王二字，怎能不让人作出联想，在那上古时代还真有西王母不成？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“或许只是古人来此凭吊所留下的碑文吧。”楚风摇头，自我解释。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有些不对！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;忽然，他怔住了，在抚摸那刻字痕迹时，他发现所谓的“干枯苔藓”不正常。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“绿铜锈！”这个发现让他心中一震。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这块碑曾被尘封，埋在山体中，仔细想来也不会有什么苔藓才对，它所历经的时代十分漫长，直到剧烈地震后才重见天光。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它竟为青铜材质！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可是，这么大的一块古铜属实罕见。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“殷墟出土的司母戊鼎也不足两千斤，便号称最大青铜古器，而这块铜碑……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风推开一些土石，保守估计，这块青铜最起码也得重达五六千斤，实在骇人听闻，在古代这绝对是一块稀世重器。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它绿锈斑斑，一看就是埋了漫长岁月的古物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果是石碑，楚风还认为是前人来此凭吊所留，现在这么大一块铜碑，他不确定了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在那遥远的古代，仅为了怀古，谁能消耗的起？</p>',2,2),(33,'圣墟','第三章 青铜昆仑','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“当！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青铜颤音震动，带着岁月的沧桑感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风放下手中的石块，确信这是铜碑无疑，这让他有些难以置信，数千斤重的青铜古器，这可不是小事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果传出去的话，肯定会引起轰动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;上面刻着西王二字，带着时光的沉淀，古朴中亦有神秘，吸引人的心神，真不知道是何年代所留。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“什么人将它埋在昆仑山中？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风敲打铜碑，金属颤音不断，可惜他并不是考古者，得不出什么有价值的结论。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“或许漫长岁月前这里曾经有一个极其辉煌的青铜文明。”他自语，这般推测。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他一向不迷信，即便昆仑有着太多的神话色彩，并且突兀见到刻有西王二字的青铜巨碑，他也不怎么相信那些传说。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风觉得，西王母即便真的曾经存在过，或许也只是某个远古强大部落的首领，而这里只是一片遗迹。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“剧烈的地震，引发山体磁场值异常，引动云层放电，再加上山上露出的巨大青铜，所以引来雷电缭绕？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风越发觉得，可能就是这一原因。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他很想将这块铜碑挖出来，仔细看个究竟，奈何它埋在地下有小半截呢，没有工具在手，很难成功。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这里徘徊片刻，他再次向上攀登。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山上的大裂缝很宽也很深，黑乎乎，触目惊心，一片破败景象。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沿途并没有什么路，山势险陡，巨石横陈，越向上越不好走。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山体宏大，独自走在上面，感受着它的雄浑，再想到关于它的诸多传说，楚风心中有些异样，眺望远方，巨山与天穹相连，景象十分壮阔。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;向上攀走了千余米，在地震后的大山上前行，这可不是一段好走的路程，遇到山石松动而滚落时，非常危险。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;前面，有一大堆土石，山壁在不久前断落下一截。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;隔着有段距离，楚风就看到了异常，他露出惊容，快速前进，向上攀去，想要确定所见是否为真。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“像是铜绿！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;远远的，他看到了一片绿痕，锈迹斑驳，就在断崖那里，这可不是一小簇，而是很大一片。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;终于到了近前，他看清楚了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“真是！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这可比早先看到的铜碑更让人心惊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山体上有一大块崖壁断落后，断开的地方、贴着山体陡峭处那里，露出尘封的真相。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;依着石壁，那里绿锈成片，古旧中带着神秘，这是铜质的建筑体，随着山体滑落部分而显现出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三座青铜房屋，古朴而寂静，背靠石壁，修建在那里，有些部分被土石埋着，但还是能看到大部分。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青铜房舍式样古老，恢宏中带着历史的厚重感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;铜质房屋上，那些瓦片也是青铜所铸，一块又一块，整齐而有规律，一眼望去，像是绿色的鳞片铺盖在那里。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风着实吃惊，心中无法平静下来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这可是轰动性的发现，青铜器物，这么的庞大，这可是几座铜质房屋啊，建在昆仑山上，曾被深埋着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是什么年代的东西，是何人所建？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;依照他的猜测，这片区域绝对曾经有过一片璀璨的青铜文明，存在岁月极其古远，现存的史书都不曾记载。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只是在心惊的同时，他也有不安与不解。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;司母戊鼎号称最大青铜古器，可现在看来不仅比那铜碑轻，和眼前的房屋比起来，那就更加不能算是重器了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;毫无疑问，这种房舍的建造比之铸鼎更难。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青铜房屋是铸成的，浇铸在一起，恢宏与肃穆中透着神秘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这如果被外界发现，一定会被视为瑰宝级青铜大器，过去从未见过样庞大的器物，具有颠覆性。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风遇事一向很镇定，但今日却不能从容了，西部大地上的山脉中竟然有这种青铜遗迹，实在惊人！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他尝试用力推开一扇青铜门，略微刺耳的金属摩擦声传出，铜门被打开了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风没有立刻进去，在外面站了片刻，通风后才谨慎的迈步，里面很安静，仿佛与外界隔绝了，落针可闻，但光秃秃，什么都没有。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无论是地上还是墙上，没有其他任何器物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其他两座青铜房屋也是如此，内部空旷，并无桌椅等。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仔细查看，没有遗露，铜屋就是空的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风退出来，看着三座青铜房屋，心中有着太多的不解，这是古人居住的建筑物，还是祭祀时所用？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在那遥远的个古代，这真的太奢侈了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;史书中有记载，殷商时代的司母戊鼎铸造时足足动用了两三百名工匠，密切配合才艰难完成。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;古时，如果铸造三座铜屋，那得有多么大的难度？！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风在这里驻足良久，最后再次上山，几个小时后，终于快要到山顶了，距离还有两百米左右，他出了很多汗水。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他的体质超好，身体修长，很强健，只是登这样的大山，长时间下来还是非常疲累。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;临近山顶，极目远眺，山脉起伏，大地壮阔，而个人则显得无比渺小，如同尘埃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站在巨山上，抬头看向那近在咫尺的碧蓝天穹，让人心胸都为之舒畅，可以忘记一切烦忧，个人的荣辱，所有的一切，都显得微不足道了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这里的海拔已经非常高，但却未见到积雪，并且依旧有草木，这让楚风觉得有些奇怪。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有雷击的痕迹！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风发现山上有焦痕，曾被雷电劈过，有大块区域的草木化成灰烬，一片乌黑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;除此之外，山石都被劈开了，大片的山体损毁。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这就更加让他确信，数日前这里大雾浓重，蓝光缭绕，其实是闪电，这片地方曾经遭遇过雷击。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;前方的路不好走，巨石堆积，楚风绕行，想从山体另一侧上去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;然而，当他转过来后，到了山顶的另一边时，身体微微发僵，瞳孔一阵收缩，第一次感觉这般的震惊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是见到铜屋时，他也没有如此。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这边的山体曾经发生过滑坡，土石翻滚下去很厚的一层，露出金属质感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“铜山！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;土石大面积翻落下去后，露出的景象实在过于惊人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山顶这块区域竟然是铜质的，曾被土层埋在下方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这可不是一小块区域，离山顶近两百米远，这大片地带都已露出青铜质感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这怎能不让人产生其他想法，这座山是青铜的，外表掩盖着土石，下方才是“真景”？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;实在是匪夷所思！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;真相如何，不得而知，但最起码这两百米高的山体是青铜，足以惊世。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风被惊住了，这是昆仑中的一座山，内部是铜质，颠覆了他的思绪，考验过去久已成型的观念。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他不信那些玄异的事，一向将所谓的传说当故事听。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但眼下这里透着诡异，解释不清。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;被雷击后，露出青铜山顶真相。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这里的事着实有些惊世骇俗！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风沿着铜山向上攀，距离山顶将近百米时止步了，因为这里异常陡峭，是一片青铜绝壁，很难向上了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，他闻到了一股清香，随风送来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这块地方都是铜料，是冰冷的金属，早先并没有看到草木，光秃秃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风仰头观看，仔细寻找。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;果然见到了，在青铜山崖上，有一株植物！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那里已经属于山顶区域，他退回后，从其他方位再次寻找可以向上攀爬的路径，逐渐接近，想看的仔细一些。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不久后，虽然依旧到不了山顶，但却看清了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一株绿油油的小树，三尺多高，它竟然扎根在青铜崖壁上，结有一朵花，含苞待放。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风确信自己没有看错，那里没有土石，有的只是青铜，它扎根在铜质的崖壁上，实在匪夷所思。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这过于惊人，有些不可理解。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他换了个方位，选择了一处更利于向上攀的地方，距离更近了，看的清楚，小树的确是扎根在青铜上！</p>',2,3),(34,'圣墟','第四章 奇树与猛兽','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;昆仑山顶，最高崖壁处，一株三尺小树独自生长。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;视青铜为土壤，作养料，顽强扎根，通体绿莹莹，带着光泽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;树干有手腕粗细，它虽不高大，但却带着老树皮，张裂着，如同一层层鳞片，竟有苍劲感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它的叶片如同绿玉雕刻而成，通透中富有灵性，形状如同幼儿的手掌，托着一些晶莹的露珠，清风拂过时，如同洁白珍珠在碧玉盘上滚动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在小树的顶端长有一个花骨朵，拳头大，通体银白，但带着金斑，于绝壁中待放，已经清香飘漾，很美。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;妖异的小树，静静傲立着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风尝试了几次，青铜山体这一侧的确上不去，他决定冒险，从满是滚石的那边攀登，但需要格外小心，不然可能会丢掉性命。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他从铜壁这一边退下，来到平坦地带，绕着山体而行，同时向上观望。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“它怎么会生长在青铜上？”楚风想不通。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他只能归根于，曾发生过数起变故的世界越来越无法让人理解了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风心绪已平静，他皱着眉，细想着这一切，奇异的植物，诡异的青铜山，这一切都不符合常理。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一道身影在他心间浮现，因为那个人曾说过一些话，当时他并未在意，可眼下却让他有些触动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有一天，也许路边的一株杂草都会结出拳头大的鲜红果实，我们所见到的平凡或许都将不在了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是林诺依说的话，很平淡，像是随口而出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就像她跟楚风说分手时一样，略有淡漠，声音有些远，像是她站在很高处，说了那些话语。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风以为她是在说两人间的事，无论是人生还是情感等没什么不可改变。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“她言有所指？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这后文明时代，世界曾发生过数起变故，虽然绝大多数人都不知道内情，但总有小部分人知晓真相。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林诺依到底知道什么？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;心中浮现她的身影，楚风叹了一口气，虽然有些怅然，但过去的就让它过去吧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他再次抬头，看向青铜山顶，露出异色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她真的言有所指吗，沿着她的思路，许多平凡或许都将要改变，那么本就不平凡的稀有异树呢？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这样的一株小树，即便在没有经历过异变前，它也肯定非凡！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;脚下的乱石很多，楚风已经走到青铜山体的边缘了，这边的路太难走。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，一阵狂风涌来，楚风眼皮直跳，他看到有一片阴影突然出现在地上，马上就要把他遮住了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有什么东西在接近！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他反应敏捷，身体矫健，直接一个翻身，快速躲避了出去，他在山上迅疾翻滚出去一段距离，在这个过程中更是取出防身的折叠弩，有效而快捷的组装。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在户外，尤其是一个人旅行，怎么能没有一些防身的器具呢，楚风转身就是一支钢箭，砰的一声射了出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，他看到了那是什么！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风的脸上浮现出难言的惊容，这生物的体形太大了，远超同类。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一头金色的猛禽，羽翼很亮，伸展开来足以五六米长，从天空中俯冲而下，刚才险些抓在他的身上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当的一声，钢箭横过，击中不远处的一块大青石，火星乱溅，擦着凶禽而过，竟被它躲开了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，那锋利的大爪子，擦中地上的土石，摩擦声刺耳，令人觉得发瘆，它腾空而上，风声呼呼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风脊背发寒，刚才如果不是他反应迅疾，被这凶禽抓中的话，后果不堪设想。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一般的鹰隼可以轻易抓裂野兔等猎物的头骨，这么大的一头金色猛禽，其力量可想而知，刚才如果稍微迟一步，那场面绝对很惨。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风第一时间倒退，选择有利地势，背靠在一块巨石上，然后他手持折叠弩，对准天空，谨慎的戒备着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;半空中，金色的猛禽盘旋，太凶猛了，贴着山体，荡起大风！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从未见过这么大的禽类。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;看它的样子，像是金雕，没有杂毛，通体光亮，体形大的吓人，非常威猛，金色瞳孔灿灿，野性十足，身上带着戾气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一般的金雕怎么可能会这么大，这一头算是异类，太不正常了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果是在古代，有些部族或许会将这头金色的猛禽当成大鹏鸟。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为，古人进行记载时，多有些夸张。五六米长的金色凶禽绝对算是一个异数，如果出现的话，肯定会引发大波澜。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;尤其是在这昆仑山地带，更会平添神秘色彩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;金色巨禽很凶，但是却没有立刻俯冲过来，而是在盘旋，它有着非同一般的敏锐，显然也意识到了楚风手中那把弩的威力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;忽然，楚风闻到了一股腥味。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三只雪豹从山体下方缓缓而来，悄无声息，瞳孔幽幽，嘴巴都粘着血迹，雪白獠牙锋利，显然不久前曾经猎杀过什么生物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它们盯着楚风，身体略微弓起，同时又看向半空中的金色猛禽，非常忌惮，发出不安的低吼声。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三只雪豹远比一般的同类强壮，锋利的爪子泛着寒光，有力的躯体做出随时会扑杀的预备动作。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风蹙眉，没有想到会遇上这种危险，空中有金色凶禽，地上有奔跑极速的豹子，他处境堪忧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，三头雪豹皮毛抖动，颈部的毛根根倒竖了起来，它们快速躲避，腾跃而起，进入乱石堆中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无声无息，山上多了一头牦牛，通体乌黑光亮，黑毛跟绸缎子一样，流动乌光，一对粗大的牛角扬起，对着天空。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这称得上是一头耗牛王，足有一丈多长，四肢粗壮，体格巨大，跟一堵黑色的小山似的，站在那里。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这让楚风心惊，这头黑色的牦牛这么庞大，走路居然跟雪豹一样没有声息，也是突兀出现的，没能提前发现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而且，那三头雪豹很怕这头乌黑光亮的大牦牛，躲进了石碓中，这很不正常！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黑色的大牦牛抬头看了一眼半空中的金色凶禽，而后便不动了，静静站在那里，望向青铜山顶。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这三种生物怎么都到了这里？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风知道身在险境中，没有轻举妄动，他在等待机会离开。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;远处，六七道兽影在奔跑，向山上而来，速度很快，露出白色獠牙，凶性一览无余。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那是六头狼，都很高大，强壮于同类，为首的“头狼”通体雪白，只有一只眼睛，绿光幽幽，显得最为凶狞。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它们到了近处后，短暂驻足，看到黑色的大牦牛后略有焦躁，又看向金色的凶禽，显出不安来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，平静被打破了，六头凶狼一起发力，沿着乱石区域同时向着山顶冲去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;与此同时，三只雪豹也动了，风驰电掣，速度极快，向着青铜山顶奔行。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风倒退，他想就此离开。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山顶附近，兽吼声不断，它们争着上去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;砰！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一声剧震传来，一头雪豹的面部变形了，血肉模糊，重重的从山上摔了下来，它是被一道黄影拍翻的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那像是一道闪电，太快了，斜刺里突兀的现身，冲入兽群中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那是一头獒，脖子那里的毛浓密而长，如同雄狮的鬓毛，个头跟一般的藏獒差不多大，爪子上带着豹血。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是，它太迅猛了，一跃就是数米远，扑向前方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;狼嚎声响起，很惨烈，有血花溅起，一头青狼被咬断脖子，甩飞了出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另一头狼则被猛犬撞的飞起，砸在石壁上，当场萎靡。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是藏区传闻中的真獒！”楚风吃惊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;据当地人讲，真獒生活在野外，可与虎豹为敌，数量极其稀少，罕被人发现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而这头真獒比传闻更厉害，快如闪电，闯入兽群中，才一接触而已，就解决掉一豹两狼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是獒王，楚风猜测，它甚至更厉害。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那头獒再次跃起，足有七八米远，大爪子落下，威力堪比熊掌，噗的一声，将一只野狼的眼睛拍出，翻滚了出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;落地的同时，这头獒又扑倒一头雪豹，吼声骇人，它们在那里翻滚，这是野性的释放。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;雪豹倒在血泊中，喉咙被咬穿，眼看活不成了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这头獒并没有受伤，颈项那里如同狮鬓的长毛炸立着，个头虽然不大，但是如同狻猊兽般，有股特别的气势，它再次跃起，冲向另外几头猛兽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风几乎有些不敢相信，竟有这般威猛的獒犬！几个起落而已，就快要将那些野兽解决完了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最后一头雪豹也死了，只剩下那头独眼的银色“头狼”拼命奔逃，向山下冲去，想活着离开这里。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可是，几个起落间，它就被那头獒给追上了，一张血盆大口张开，狠狠的咬断它的脖子，整颗狼头都差点断落下来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就这样九头凶猛的野兽在短时间内都被解决了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风紧握折叠弩，严阵以待，在那里戒备着，这个地方实在太危险了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那头獒安静了，嘴上全是兽血，但不是它自己的，它一动不动，仰头看着青铜山崖上的那株小树。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它个头不是很高大，但是，却显得格外的威猛，土黄色的鬓毛尽染其他猛兽的血，威势凛凛。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这个过程中，那头黑色的牦牛一直在盯着青铜山上的小树，自始至终都没有动过，竟然十分镇定。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而那头金色凶禽在半空盘旋，也跟不久前一样，俯视着这里。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三只诡异的生物都很沉稳，像是有人的灵性，非常沉得住气，目标是那株小树，但却没有立刻行动，似在等待着什么。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风深感惊诧，因为这三头生物实在非同寻常。</p>',2,4),(35,'圣墟','第五章 花开','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三尺高的小树顶端，拳头大的银白花朵彻底绽放，白雾散出，在青铜山崖弥漫，令那里宛若仙境般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那花瓣上带着金色的斑点，此时一齐发光，在白雾中，斑斑金色光粒如同星斗闪耀，熠熠生辉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这景象有些瑰丽，很迷人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三头生物等的就是这一刻，待它成熟！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它们争夺，剧烈撞击，利爪横空，这是原始野性的释放，无比疯狂，都想将那朵奇花独占为己有。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那乌黑的牦牛踏足时，震的山顶都在轻颤，力大无穷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;半空中，金色的凶禽张开大爪子，跟那粗糙的牛角撞在一起，响声震耳。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那头獒在低吼，声音沉闷，犹如雷鸣。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三头生物在厮杀，彼此攻击，争夺盛开的花朵。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这个过程中它们也在猛嗅，不顾一切的攫取花香。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青铜山顶白雾袅袅，朦胧间，金色斑痕摇动，像是有一小片星海在雾霭中发光，那里极其神秘与美丽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;砰！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它们触及到绿莹莹的小树，那头獒的一只大爪子擦中花朵。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大风鼓荡，金色凶禽俯冲，硬撼那头獒，锋利的爪子落下，想要将那头獒撕裂开来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;早先它们彼此不动手，因为相互忌惮，而现在花朵绽开后，它们为了争夺，不惜拼命，不管不顾了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在那头金色凶禽双翅扇动时，几片花瓣从那头獒的大爪子下凋落，随着狂风飘落向青铜山下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这地方山势极陡，裹着白雾的花瓣很快落向楚风那里。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他抬手就接住一片，香气浓郁的化不开，让他险些醉倒在这里，仔细看，带着金色斑点的花瓣内壁还有一层晶莹。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“花粉！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在上面粘上了一层花粉，流淌着光泽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风伸手，先后将四片花瓣接在手中，其中两片香气略淡，因为只粘了少许花粉，另外两片则馥郁芬芳，上面晶莹密布，香气浓郁的化不开。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青铜山顶的三头生物都向下看了一眼，眼神冷冽，而后又开始激烈搏杀，争夺未曾坠落的花瓣。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风见状，攥紧花瓣。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是，他很快发现异常，手心的花瓣不再温润，竟有一种枯萎感，他摊开手掌，发现四片花瓣上的晶莹消失了，花瓣也干枯！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一刹那而已，它们失去了光泽，没有了活性，变得枯黄。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是怎么回事？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;稍微一用力，其中一片花瓣化成碎屑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风惊愕，他将剩余的三片干枯花瓣抛下，冲着山顶喊道：“还给你们。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随后，他果断转身，不再理会这些，一路向大山下冲去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然急于逃离，但一路上他还是忍不住思忖，那四片花瓣为什么在他手中刹那枯萎了？这变化很古怪！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;路过铜屋、青铜碑时，他没有停留，一心想快速下山，后面的路程山势渐渐趋于平缓，速度可以加快了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;耗时很久，当楚风赶到山脚下时，红日都已西坠。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;庆幸的是，那三头非同一般的生物没有追下来，还在山顶争斗呢。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风满身都是汗水，在如此高的大山上进行这么剧烈的运动，哪怕他体质再好也觉得精疲力竭。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;实在过于疲乏，他坐在山脚下大口的喘气，很长时间后还能听到自己心脏剧跳的声响，他咕咚咕咚向嘴里倒水。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;回望身后的大山，当真像是迷一样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;西王铜碑，神秘铜屋，还有铜山，巍峨大山内部真为铜质的吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果有可能，他真想剥开这座大山的土层，仔细看一看内部究竟怎样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这座山体仅是昆仑山脉其中的一座，这片区域到底藏着什么秘密？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“得赶紧离开，万一那三头生物冲下来，将会无比危险。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;数日前，曾发生过地震，山体上有不少大裂缝，山脚下这里也不例外，楚风落足时小心的避开。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无意间，他在一道地缝中看到一块石头，三寸多高，四四方方，竟具有很规则的形状，倒也少见。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风随手捡起，继续上路。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不知道是否为错觉，一路上楚风总觉得体内有些异样，很微妙，偶尔会触到一丝暖流，在血肉中流淌。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仔细去体会，它又消失了，不去管它，则又在不经意间出现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;错觉，还是身体过敏？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他一阵怀疑，自己的感知紊乱了吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是从这只手开始的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他摊开左手，最早有所觉察时，正是左掌心部位，可那里并没有什么。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“四片花瓣曾在我左手中莫名干枯。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风一边赶路，一边琢磨这件事，他觉得没那么简单，这事有些古怪，让他很不放心。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那花瓣曾散发白雾，还有斑斑光点，无论怎么看都诡异。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身后的那座青铜昆仑山，今日着实冲击了他原有的观念，具有颠覆性，让他不得不多想。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那三头生物都不普通，它们在争夺树上那朵花，应该无害。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然有所顾虑，但是楚风觉得，这花朵应该对身体无害，不然的话怎么会引发罕见的凶兽厮杀，拼死争抢。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他摇了摇头，暂时不想这些了，大步向着有牧民居住区赶去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;夜幕下，无垠的高原上格外的安静，偶尔远方传来一声兽吼，则更平添了一种空旷与苍凉之感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风借宿在牧民家中，他决定明日就踏上回程。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;夜里，他静静的看书，同时体会早先时的那丝暖流，可是不可捉摸，似有似无，不知道是否会有什么变化。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;良久，他轻叹：“顺其自然吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为，百般尝试，他发现越是在意，去关注，越是察觉不到，相反不去理会反而能模糊的体会到。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“花粉，触媒。”楚风轻轻念出这几个字，他忽然想到一件事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;毕业离校时，林诺依的家人曾派车去接她，隐约间曾提到这些字，只是有些远，他未能听的清晰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然分手了，但他那时还是想送一送她，不过看到林家人略有冷漠，平淡的看向他，楚风当时只挥了挥手便走了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;略微出神，他不经意间看到身边的一块石头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这石头的形状居然这么有规则。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他在帐篷中掂量这块石头，虽然是正方体，但边沿部分无棱角，略微光滑，就像是打磨过，有些圆润。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仔细看，石块上竟有模糊的纹络，这是天然形成的吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;纹络很暗淡，不细看的话很容易就会忽略掉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是人为的痕迹吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在昆仑山山脚下时，他根本就没在意，只是觉得它很规则，顺手捡了起来，一路上想着铜山的事，心不在焉的在手中抛着，便带了回来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;现在，他突然发现，这石块有些特别。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风将石块洗净，在灯火下仔细观察。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;石块三寸高，呈灰褐色，有一些很模糊的纹络环绕着它，像是藤蔓，又像是自然形成的斑痕，十分陈旧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;是否为远古部落留下的旧石器？他这般猜测。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风翻过来掉过去的看，抚向那些痕迹，突然间，喀嚓一声轻响，在这宁静的夜间略有刺耳。</p>',2,5),(36,'万古神帝','第一章 八百年后','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“池瑶，我待你如挚爱，你为何要杀我？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘大吼一声，想前一扑，压得鎏金铸造的床榻“咯吱”一声，猛然坐了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;发现只是一个梦，张若尘才长长吐出一口气，用衣袖将额头上的汗珠擦干。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那不是一个梦！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他与池瑶公主发生的一切，又怎么可能是一个梦？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘本是昆仑界九大帝君之一的“明帝”的独子，年仅十六岁，便以逆天的体质，修炼到天极境大圆满。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是，正在他成为昆仑界年轻一代第一人的时候，却死在自己青梅竹马的未婚妻池瑶公主的手中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;池瑶公主，是九大帝君之一“青帝”的女儿。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;明帝和青帝是最好的至交，张若尘与池瑶公主更是指腹为婚，从小一起长大，一起修炼。一个英姿飒爽，一个美貌绝伦，堪称金童玉女，本来可以成为修炼界的一段佳话。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘怎么也料不到，池瑶公主居然会对他出手！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;死在池瑶公主手中之后，当张若尘再次醒过来，却发现自己已经在八百年之后。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;曾经的池瑶公主，平定九帝之乱，统一九国，建立第一中央帝国，成为整个昆仑界的主宰――池瑶女皇。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八百年前，称雄昆仑界的九帝，彻底的成为过去，消失在历史的长河之中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;九帝已死，女皇当立。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个时代，只有一位皇者，那就是池瑶女皇，统御天下，威临八方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“她为何要杀我？她的心怎么可以那么狠，还是说女人的心都如此的狠？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘的眼神锐利，心沉似铁，满腹疑问。但是，却没有人可以帮他解答。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八百年过去了，早已沧海桑田，物是人非，除了修为绝世的池瑶女皇，青春依旧，不老不死。曾经的那些故人，全部都已经化为黄土，变成白骨。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;即便是当年威风八面的九帝，也都全部在人间绝迹，只留下一段段让后人经久传诵的辉煌故事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“吱呀！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一个身体柔弱的宫装美妇人，从外面推门走进来，看着坐在床榻上的张若尘，带着关切的眼神，“尘儿，你又做噩梦了？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;眼前这个美妇人，是云武郡王的王妃，也是张若尘的娘亲，林妃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一具身体的原主人，因为体弱多病，三天前就病死在床榻上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘被池瑶公主杀死之后，再次醒过来，便出现在这一具身体里面，让原本病死的少年起死回生。更加巧合的是，这一具身体的原主人，也叫张若尘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘刚刚醒过来的时候，还很排斥林妃。毕竟在张若尘的眼中，林妃，只是一个陌生人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是，经过三天的接触，张若尘逐渐发现，林妃真的十分关心他，简直无微不至，见到张若尘做噩梦被吓醒，更是不顾天寒地冻，立即赶来张若尘的房间。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;上一世，张若尘从未见过自己的生母。据说，在自己出生的时候，她便去世了！没想到，被池瑶公主杀死之后，重生在这一具身体里面，竟然让他多了一位娘亲，感受到母爱的温暖。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“或许她还不知道，自己的尘儿，在三天前，就病死了！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是告诉她真相，她未必承受得住这个噩耗的打击。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘看着眼前这个美妇人，眼神变得柔和起来，微微一笑：“娘亲，不用为我担心，只是一个梦而已。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃单薄的身上披着一件枣红色的连帽貂裘，坐在张若尘的床边，抚摸着张若尘的额头，担心的道：“已经三天晚上了，你总是被噩梦吓醒，每次都叫‘池瑶’的名字。她到底是谁啊？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃自然不可能将“池瑶”这个名字，联想到第一中央帝国的女皇。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;况且，池瑶女皇统一昆仑界，建立第一中央帝国之后，便号称“大威大德女圣皇”，平时根本没有人敢提“池瑶”二字。会犯忌讳。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘道：“没什么，娘亲，你听错了！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃叹息了一声，道：“今后千万不要再直呼‘池瑶’二字，哪怕是在梦中也不行，那可是女皇的名讳。直呼女皇名讳是大不敬，一旦被有心人听到，会被处死的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘点了点头，紧紧的捏了捏手指，颇含深意的道：“绝对不会了！今后……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;今后，我将是她的噩梦。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃看着身材瘦弱、脸色苍白的张若尘，轻轻的叹了一口气，心中无比酸楚。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然生在郡王之家，但是，他却从小体弱多病，已经十六岁，依旧只能常年躺在床上，恐怕这辈子也只能这样子了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;外面，响起一阵凌乱的脚步声。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你们干什么？这里可是玉漱宫，谁给你们的胆子，敢随意乱闯进来？”一个容貌娇美的侍女，想要拦住闯进来的八王子，却被八王子轻轻一推，摔到十多米之外。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子可是一位武者，修为达到黄极境后期，一掌击出，足以将三百斤重的石盘打出十丈远，更何况只是一个百十斤重的侍女？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;手指一弹，就能将她弹飞出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那一个侍女惨叫一声，重重的摔落在地，左手手臂被摔断。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子穿着一身金缕衣，腰上缠着一根玉石带，身体健硕，手臂修长，步伐沉稳，走进玉漱宫，冷眼盯了那个侍女一眼，“一个奴婢也敢挡本王子的路，真是找死。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子的身后，跟着六位身穿麟皮铠甲的侍卫，身躯高大，虎背熊腰，显然都是战力强大的武道修士，属于王宫的禁卫。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃听到外面的动静，安抚了张若尘的情绪之后，便关上门，走了出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她盯着站在外面的八王子，微微的皱了皱眉头，道：“八王子殿下，这里可是玉漱宫，就算你是王子，也不能乱闯吧！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子张济抬起头盯着林妃，朗声道：“王后有令，林妃娘娘和九弟的寝宫，改到‘紫怡偏殿’。今后玉漱宫的主人，便是本王子的生母萧妃娘娘。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃的脸色微微一变，她早就料到这一天会来，但是，却没有想到会来得这么快。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃惨然的一笑，道：“王后这么快就要赶我们母子离开玉漱宫了吗？好吧！明天，我便和尘儿搬去偏殿。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子道：“对不起！娘亲说了，她今晚就想入驻玉漱宫。请林妃娘娘现在就搬去偏殿！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃知道张若尘体弱多病，经不起折腾，带着几分哀求的语气，道：“八王子殿下，你也知道你九弟体弱多病，夜已深了，天气寒冷，万一……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子冷冷一笑，丝毫都不客气的道：“林妃娘娘，这世上可怜的人多得去了，但是，不是每个人都值得可怜。既然九弟体弱多病，那还活在世上干什么？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“他可是你九弟！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃还想再说什么，突然，身后的门被推开。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘的身体虚弱，用手撑着门柱才能勉强站立，盯着不远处的八王子。他看似弱不经风的身体，像是蕴含着不屈的意志，道：“不用求他们，我们现在就搬走。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“尘儿，你怎么下床了？外面的天气寒冷，还不快回去。”林妃连忙上前去扶住张若尘，生怕他染上风寒。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘固执的摇了摇头，道：“娘亲，我们不需要求任何人，迟早有一天……我们会重新回到这里！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃看着张若尘坚定的眼神，似乎也被他的情绪感染，眼泪婆娑的点了点头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃和参扶着张若尘，一步步走出玉漱宫，除了那一个被八王子一掌推出去摔断手臂的侍女。别的那些仆人，全部都没有跟着他们离开玉漱宫。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有人都看得出来，林妃和九王子已经彻底失势，在郡王府中，再难有他们的立足之地。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;本来他们就是玉漱宫的仆人，现在自然明智的选择留在玉漱宫，全部都去讨好八王子这位新的主人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;紫怡偏殿，一般都是失宠的王妃居住的地方，十分偏僻，满地落叶，似乎已经很久没有人居住。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;夜以深，寒风萧瑟。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;坐在冰冷的石凳上面，张若尘瘦弱的身上裹着一件外衣，却依旧感觉到寒冷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这一具肉身太弱小了，只有修炼武道，才能让身体逐渐强壮起来。要不然的话，就算我现在是郡王之子，依旧只能受人摆布。”张若尘的心中暗想。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八百年过去了，张若尘也不知自己现在能去哪里？既然上天安排他重生在这一具身体里面，无论是为了将来向池瑶女皇复仇，还是为了那一位无微不至照顾自己的娘亲，他都必须要强大起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;今日遭受的屈辱和冷遇，完全都是因为自己太弱小，无法反抗，无法掌握自己的命运，甚至连自己居住的地方都被别人强占。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想要得到别人的尊重，想要获得温暖舒适的居住环境，就必须成为一名武者，证明自己的能力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在昆仑界，想要成为一名武者，必须要先开启“神武印记”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所谓的“神武印记”，就是神灵赐给人类的修炼武道的资格。没有开启“神武印记”的人，就永远也修炼不出真气，无法成为天地之间的强者。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘已经十六岁，依旧没有开启“神武印记”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;过了十六岁，便错过修武的最佳年龄，就算开启了“神武印记”，也不可能有多大的成就。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同样都是云武郡王的儿子，为何八王子就能高人一等？能够将张若尘和林妃赶出玉漱宫？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是因为，八王子在十岁的时候，便开启“神武印记”，现在已经是黄极境后期的年轻武者。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“只要让我开启了‘神武印记’，我就能修炼《九天明帝经》。以《九天明帝经》的玄妙，就算我已经错过最佳修炼年纪，依旧有可能追上别的天才，重新成为一名武道强者。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;《九天明帝经》是明帝修炼的至高宝典，除了明帝之外，便只有张若尘知道《九天明帝经》的完整修炼法决。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“明天就是祭祀大典，希望能够得到神灵的认可，将‘神武印记’开启。”张若尘紧了紧拳头，对开启“神武印记”充满渴望。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃将房间收拾整理好之后，便过来搀扶张若尘，“尘儿，你快早点休息吧！明天，还要去参加祭祀大典。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“娘亲放心，我明天肯定能够开启‘神武印记’！”张若尘道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯！娘亲相信你！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃深深的看了张若尘一眼，心头轻轻一叹。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其实，她对张若尘开启“神武印记”根本不报任何希望，毕竟张若尘已经十六岁了，过了十六岁，便几乎不可能还能开启神武印记。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是，做为一位母亲，她却必须要鼓励自己的孩子，给他信心。</p>',9,1),(37,'万古神帝','第二章 开启神武印记','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所谓的郡国，其实就是第一中央帝国的第一个郡，每年必须要向第一中央帝国上贡和纳税。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;郡国的国君，称为“郡王”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘现在的身份，就是云武郡王的第九子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;平躺在冰冷、坚硬的木床上，张若尘依旧在思索明天祭祀大典的事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这具身体的原主人，到十六岁居然都没有开启‘神武印记\'，肯定是被诸神抛弃的人。我要怎么做，才能有更大的机会开启‘神武印记\'？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在昆仑界，想要开启“神武印记”，必须得到神灵的认同。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;被称为，武权神授。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在祭祀大典的时候，神界和昆仑界之间会出现一条天地神桥，连接两界。诸神在享用祭品之后，将会赐给一些有天赋的人类“神武印记”，帮他们开启修炼之门。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天赋越高的人，就能越早得到“神武印记”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘的上一世，还在胎腹中的时候，就开启“神武印记”，可谓是天生奇才。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一世，到十六岁，居然都没有开启“神武印记”，那基本上就是被诸神遗弃的的人。就算明天再参加祭祀大典，几乎也不可能获得“神武印记”。请黑じ岩じ阁，谢谢！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘无法入睡，从床上坐了起来，手中捏着一枚枣核形状的白色晶石，两头尖锐，中间立体，晶莹剔透，没有丝毫杂质。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他开始研究这一枚白色晶石，或许，它可以帮助自己，得到某位神灵的认可，开启“神武印记”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一枚白色晶石，是在他十六岁成人礼的时候，明帝送给他的礼物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘也不知道这一枚白色晶石到底是什么东西，只是将它贴身佩戴在身上。没想到，来到八百年后，它居然依旧还在自己的身上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我会从八百年前来到八百年后，说不定就与它有关。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘紧紧的捏着白色晶石，闭上双眼，脑海中浮现出父亲明帝的身影，也不知父亲还活在世上没有？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一夜，王城下起了大雪。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第二天清晨，整个王城都被厚厚的积雪覆盖，一座座朱红色的宫殿、楼阁、亭台，全部裹上了一层冰雕雪衣。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;冬至日，全年最寒冷的一天。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;整个王城的武者，汇集到诸皇祠堂外，在郡王的带领下，祭祀诸神。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;诸皇祠堂外，用巨石堆砌着一座古老的祭台。祭台上，不仅绑缚着数以万记的牲畜，牛、羊、猪……，还有很多用铁链锁住的强大蛮兽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;文武百官、武道修士、王子嫔妃，无数等待开启神武印记的少年和少女，甚至是一些还在襁褓中的婴儿。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是一场举国盛典，不仅仅只是王城，在云武郡国的每一座城池，每一个小镇，每一个村落都要举行祭祀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哏哏！九弟，你都十六岁了，就算参加祭祀大典，也不可能获得‘神武印记\'，何必来丢人现眼？”八王子张济背负着双手，冷峭的一笑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;六王子就站在八王子的身边，冷着一张脸，道：“都说龙生九子，各有不同。父王何等英雄的人物，却生出你这个废物，十六岁了，连‘神武印记\'都没有开启，王族的脸都被你丢尽。你活在这世上干什么？为什么不去死呢？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这句话说得有些过，但却是在场几位王子的心声。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;帝王家的亲情，最是单薄，这一刻体现得淋漓尽致。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在昆仑界，能够开启“神武印记”的人并不多，十个人中也最多只有一个。可以说，每一位武者的地位都极高。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然对于武道强者来说，又是另一回事。武道高手的血脉强大，子孙后代也会将强大的血脉传承下来，开启“神武印记”的概率也就大得多。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云武郡王一共有九个儿子，其中八个都开启了“神武印记”，唯独只有张若尘，已经十六岁，却依旧没有开启“神武印记”，沦为王族的笑柄。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很多人都笑称为他“虎父犬子”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;甚至在朝中还有一些风言风语，声称张若尘并不是云武郡王的亲生儿子。虽然只是流言，却也让王族中人的脸上无光。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所以，别的那些王子，大多都认为张若尘是王族的耻辱，根本没有将他当成亲兄弟，恨不得他早些死掉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最近几年，就连云武郡王都有些疏远张若尘和林妃。在别的嫔妃和王子的排挤下，在昨夜，张若尘和林妃终于被赶出主殿，被迫搬去了偏殿。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘只是默默地站在那里，并不理会五王子和八王子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在没有足够的实力之前，任何的口舌之争，只会让别人更加的看轻你。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃与别的那些王妃站在一起，看到备受排挤的张若尘，心痛不已，但是，却一点办法都没有。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“祭祀大典，开始！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云武郡国的国师站在祭台上方，捧着一卷祝文，朗诵起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随后，祭台上方，吹起大法螺，大号角，一位位彩衣婀娜的宫女敲响编磬、编钟、镈钟等十六种乐器。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;接着，斩杀牲畜，以血祭天。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哗——”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;浓郁的血气，化为一根粗壮的光柱，直冲天穹，将云层击碎，冲入浩渺的天穹。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，一粒星光从天外飞落下来，落到一个六岁小男孩的眉心，与小男孩的身体融为一体，化为一个赤红色的“神武印记”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;人群中，发出一声惊呼，“薛都统的幼子，才六岁就开启神武印记了！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“赤焰神武印记，属于四品神武印记。太厉害了，将来前途无量！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;神武印记也分品级，从一品到九品。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一品神武印记最弱小，九品神武印记最强大。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有人都用羡慕的目光，盯着那一个六岁的小男孩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;六岁就开启四品神武印记，堪称天之骄子，将来的成就绝对不低。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云武郡国的众多武将之中，一个长得魁梧的男子拍着胸脯，朗声大笑，兴奋不已，“好！不愧是我薛亮的儿子，今晚都统府摆宴，各位可一定要来赏光。哈哈！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哗！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天空之上，又飞下来无数星光，落入一位位少年、少女的眉心，化为化为一个个神武印记。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其中，以一品神武印记最多，能够开启二品神武印记的少年都少之又少，最厉害的人，依旧是那一位薛都统的儿子，四品神武印记，让所有人都望尘莫及。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;开启神武印记的人，毕竟是少数，大概只有总人数的十分之一。每个人都兴奋莫名，终于得到神灵的认可，开启了武道之门。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那些没有开启神武印记的少年和少女，全部都十分失落，有的人更是痛哭失声，可谓是几家欢乐几家愁。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;眼看着祭祀就要结束，但是，张若尘却依旧没有开启神武印记。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;十六岁都没开启神武印记，几乎就是不可能还能开启神武印记，今后只能做一个平庸的普通人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有人都将他忽视，就像角落里的一粒尘埃，根本不受关注。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃最开始也抱着一丝幻想，希望自己的孩儿能够创造出奇迹，开启神武印记。就算不能成为武道强者，至少能够强身健体，不至于再被病魔折磨。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着祭祀接近尾声，林妃的希望，再次变成失望，甚至是绝望。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在张若尘都以为自己无法开启神武印记的时候，被他紧捏在手中的白色晶石，微微亮了一下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在祭祀结束前的最好一个刹那，一粒星光，从天而降，落到张若尘的眉心，化为一个白色的圆形神武印记。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哗！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一股灼热的感觉，从眉心传来，旋即传遍全身。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;开启了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘兴奋无比，终于开启神武印记。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只要开启神武印记就好，哪怕是一品神武印记，他也丝毫不在乎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;本来没有人注意张若尘，但是，在张若尘开启神武印记的那一刹那，却吸引了所有人的目光。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那不是九王子，他都十六岁了，而且体弱多病，居然还能开启神武印记！”很多人都以一种不可置信的眼神盯着张若尘，觉得很不可思议。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站在张若尘身旁的六王子和八王子也瞪大了眼睛，露出惊异的神色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这怎么可能？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃向着张若尘看过去，看到张若尘眉心的神武印记之后，欣喜的流下眼泪，立即冲到张若尘的面前，将张若尘肉身的身体紧紧抱住，“尘儿，你终于做到了！做到了！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云武郡王身边的一位老太监，走到张若尘的面前，笑盈盈的道：“恭喜林妃娘娘，恭喜九王子殿下开启神武印记！王后让老奴来请九王子殿下过去，她要亲自检测九王子殿下开启的神武印记的品级！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“王后！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃脸上的笑容立即僵住，有些紧张的将张若尘护在身后。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“娘亲，我们去见王后吧！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘察觉到林妃的微妙变化，心中暗道，看来这个王后娘娘不是善人，得多加小心。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p>',9,2),(38,'万古神帝','第三章 黄极境','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;王后娘娘坐在祭台下方的营帐中，头戴黄金凤冠，身披彩凤袍，看上起二十八、九的样子，秀丽端庄，并不像张若尘想象中那么苍老，反而还给人一种高贵的气质。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她道：“大王在生死殿闭关修炼，这一次‘祭祀大典’是由本后主持。国师，检测出九王子殿下开启的神武印记的品级了吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云武郡国的国师，手中捧着一卷铁书，摇了摇，道：“《神武印记全册》上面记载了昆仑界有史以来所有出现过的武道印记的图案，从一品武道印记到九品武道印记，全都记录在册。可是却没有一个印记与九王子殿下的神武印记相符。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;王后娘娘向着张若尘看了一眼，带着几分淡漠，“既然检测不出结果，估计就是没有品级的神武印记。在别的郡国，也出现过一些开启未知神武印记的武者，最终成就都十分有限。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子也站在营帐中，立即进言，道：“王后娘娘说得没错，九弟毕竟已经十六岁，错过了最佳修炼年纪，对他来说，就算开启四品神武印记，五品神武印记，也不会有什么成就了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;王后娘娘点了点头，赞同八王子的话，道：“既然九王子开启的只是没有品级的神武印记，而且，又已经十六岁，为了尽量节约王族的修炼资源，就只给九王子一份洗髓液吧！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃的脸色一变，道：“王后娘娘，开启神武印记的第一年，是最重要的一年。当年七王子开启神武印记之后，一共得到十二份洗髓液，每个月都能洗髓一次。尘儿，为何只能得到一份洗髓液？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;王后娘娘冷声的道：“七王子在三岁的时候，便开启七品神武印记。九王子岂能和七王子相比？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子献媚的道：“七哥，可是王后娘娘的嫡子，继承了王后娘娘的强大血脉，拥有七品神武印记，整个云武郡国谁人可以和七哥相比？将来云武郡国能不能更加兴盛，更加强大，全都系在七哥一人身上。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他又道：“本王子说一句不爱听的话，七哥的一根手指头，也比九弟的性命重要十倍、百倍，根本没有可比性。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃紧咬着唇齿，继续为张若尘争取，道：“可是八王子开启神武印记的时候，也得到了四份洗髓液。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;王后娘娘十分不悦的道：“天赋越高，得到的资源越多。九王子的天赋最低，得到的资源自然越少。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“可是……”林妃道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;王后娘娘更加不耐烦，沉声道：“本后已经做出决定，林妃，你若再敢多说一句，信不信本后让人将你再次拖出去杖责？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“再次……”张若尘听到王后娘娘的话，豁然抬起头来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;难道王后娘娘曾经杖责过娘亲？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃听到王后娘娘的话之后，浑身微微一颤，像是回忆起什么可怕的事，立即闭上了嘴巴。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此刻，已经有人将一份洗髓液送来，递到张若尘的手中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘接过洗髓液，便走到林妃的身边，转过身深深的盯了一眼坐在上方的王后娘娘，道：“娘亲，我们回去了吧！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯！”林妃轻轻的抿了抿嘴唇，苦涩的点了点头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;王后娘娘盯着张若尘和林妃离去的背影，道：“三个月之后，就是王族的岁末考核，九王子可一定要努力修炼，争取在三个月之内完成‘洗髓冲脉’，成为一名真正的武者。到时候，大王也会出关，希望你能够带给他惊喜。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随即又响起八王子不屑的声音，笑道：“就算有三份洗髓液，也不可能在三个月之内完成洗髓冲脉。我都花费了半年时间才做到，以九弟的资质，估计要花费一年以上，才有机会。哏哏！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘并没有回头，紧了紧拳头，心中的意念十分坚定，“你们就等着瞧吧，我绝对不会让你们失望！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;回到紫怡偏殿，张若尘便迫不及待的关上房门，立即修炼起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然不知道开启的是什么神武印记，但是，既然拥有了神武印记，就肯定可以修炼出真气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;首先，第一步，在神武印记的下方，开辟出“气池”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所谓的“气池”，就是容纳真气的池子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;池子越大，容纳的真气越多。随着修为的提升，“气池”也会逐渐扩大。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一般来说，要开辟“气池”，必须要在长辈指点下进行。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于拥有丰富的修炼经验的张若尘来说，开辟“气池”并不是难事，仅仅只是花费半个时辰，他便成功的开辟出“气池”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于别的修士来说，至少也要花费数天的时间进行尝试，才有可能将“气池”开辟出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘却只用了半个时辰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这具身体果然很弱小，竟然只是开辟出鸡蛋大小的一个气池，能够容纳的真气太少了！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;开辟出“气池”，还远远不够。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;气池，只是储存真气的地方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;真气要在全身运行，必须要开辟出属于自己的修炼经脉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只有完成这一步，才能拥有真气，成为一名真正的武者。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;接下来就是第二步，洗髓冲脉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘取出那一只装着洗髓液的小玉瓶，用鼻子轻轻的嗅了嗅，确定是真的洗髓液之后，直接将整瓶洗髓液倒进嘴里。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;洗髓液入喉之后，十分冰凉，但是，仅仅只是过去一个刹那，那一股冰冷便消失不见，取而代之的是一股滚烫的高温。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就像有一团火焰，在体内燃烧！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一丝丝火焰，钻进张若尘的全身的每一处经脉，然后，融入经脉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是这个时候！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“《九天明帝经》，第一层，太皇黄境天。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修炼不同的功法，开辟出来经脉路线自然不一样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;越是高深的功法，经脉路线就越多，越复杂，越惊奇。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;《九天明帝经》的运行经脉，一共多达三十六条。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然，张若尘现在并不需要将所有经脉全部开辟出来，只需要开辟出第一条经脉，就算是完成洗髓冲脉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“他们说我花费三个月的时候都不能完成洗髓冲脉，但是，我会用实际行动证明，今晚我就能完成洗髓冲脉，成为一名真正的武者！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘借助上一世的修炼经验，利用仅有的一缕真气，全力冲脉，很快就将第一条经脉打通了大半。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一股剧烈的疼痛，从体内传来，就像经脉要被扯断了一般，让张若尘浑身颤抖了一下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是别的人，遇到这样的情况，肯定已经冲脉失败。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是，张若尘却紧咬牙齿，以强大意志力坚持下来，任凭汗珠不停的往地上掉，始终不放弃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“只差……最后一小段……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;希望就在眼前，一定要冲脉成功！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一定要成功！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘一鼓作气，将为数不多的真气全部冲击过去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“轰！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一声巨响从体内传来，发出震耳欲聋的声音，差一点将张若尘震得晕厥过去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那一股疼痛感消失之后，只感觉一股冰凉的气流在经脉中流动，疼痛感渐渐消失，一股前所未有的舒爽传遍全身。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哈哈！成功了！成功完成洗髓冲脉，成为一名真正的武者！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子完成这一步，服用了四份洗髓液，整整花费了半年时间。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘仅仅只是用了一晚上，也只服用了一份洗髓液。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;完成洗髓冲脉，就是‘黄极境初期’的修为。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;武道修炼，分为“黄、玄、地、天”四大境界，即：黄极境、玄极境、地极境、天极境。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;每个大境界，分为七个小境界，分为是：初期，中期，后期，小极位，中极位，大极位，大圆满。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;四大七小。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘现在就是黄极境初期的修为。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黄极境的“初期”、“中期”、“后期”，开辟经脉，真气罐体，使肉身的力量变得越来越强，每提升一个小境界，力量就会增强一大截。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子就是黄极境后期的修为，可以徒手举起三百斤重的石盘，将石盘扔出十丈远。他打出一拳的力量，堪比一头蛮牛的力量。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黄极境后期的武者，大多都能修炼出“一牛之力”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在“后期”之上，就是“小极位”，“中极位”，“大极位”，“大圆满”四个境界。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突破黄极境后期，就能达到黄极境小极位，立即就能拥有“四牛之力”，力量翻了四倍。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所以，黄极境小极位的武者，对上黄极境后期的武者，简直就像大人对上小孩，一个打十个，也完全没有问题。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;再往上的“中极位”，“大极位”，“大圆满”，更加恐怖，拥有常人难以想象的力量。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那些境界离张若尘还很遥远，现在根本不用多想，稳扎稳打的修炼才是关键，一步一个脚印，才能走得更远。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八王子修炼了八年，也才达到黄极境后期，只能算是普通天赋。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“八王子花费半年时间，才完成洗髓冲脉。我只用了一晚上的时间。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“八王子花费了八年时间，才修炼到黄极境后期。我必须要在三个月之内，完成这个目标。对于我来说，并不是没有机会做到。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三个月之后，就是王族的岁末考核，所有王族的年轻武者，包括各位王子、郡主，皇亲国戚，全部都会参加考核，检验一年以来的修炼成果。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在王族中，想要获得地位，想要被人尊重，想要获得更多的修炼资源，就必须要证明自己的实力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三个月！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三个月之内，必须修炼到黄极境后期，就算不为了自己，也要为了那一位关心和疼爱自己的娘亲，为她争取到属于她的尊严。</p>',9,3),(39,'万古神帝','第四章 时空秘典','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;气池中的真气依旧很稀少，只有鸡蛋那么大的一团，不到气池容量的十分之一。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只有将气池中的真气修满，才能冲击下一个境界，黄极境中期。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘盘膝坐下，闭上双眼，眉心的神武印记打开，开始修炼《九天明帝经》第一层“太皇黄境天”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;真气，从气池中流出，沿着开辟出来的那一条经脉，流转全身。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着体内真气的运行，张若尘的身体就像是化为一个漩涡，开始缓缓的吸收天地之间的灵气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天地灵气，流进眉心的神武印记。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;神武印记又将天地灵气转化为真气，储存在气池。真气从气池中流出，沿着经脉，运转全身。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;真气在体内运行一圈，便是一个周天。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;整整运行了九个周天，当张若尘再次睁开双眼的时候，发现体内的真气数量增加了一倍，达到了气池容量的十分之一。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是别的武者知道，能够在如此短暂的时间之内，让体内的真气数量提升一倍，肯定会欣喜若狂。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是，张若尘却有些不满意，“修炼了九个周天，居然才增加了这么一点真气，太慢了！若是能够得到一枚灵晶就好了！我的修炼速度，还能在提升一倍！”亲手动輸入字母網址：неìУаПｇｅ。Сом即可觀看新章</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;要知道，张若尘修炼的可是《九天明帝经》，吸收真气的速度本来就比那些修炼低等功法的武者要快。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;现在的修炼速度，自然让他很不满意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是能够一枚灵晶，就能让修为速度提升一大截。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;灵晶，由灵气汇集成晶石。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;既可以在地底的矿脉中挖掘“天然灵晶”，也可以斩杀蛮兽，挖取蛮兽体内的“后天灵晶”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一枚灵晶的价值，一般相当于一千枚银币的价值，也只有王孙贵族，或者大家族培养的天才，才有机会得到灵晶。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一千枚银币，对现在的张若尘和林妃来说，绝对是一个庞大的数字，根本不可能买得起。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“灵晶！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘的心头一动，立即将那一枚一直佩戴在身上的白色枣核形的晶石取出来，托在手掌心。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这会不会是一块灵晶？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;体内真气运转，他眉心的神武印记，立即浮现出来，化为一个硬币大小的圆形光晕。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一道白色的真气，从眉心射出，击在白色晶石上面。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哗！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白色晶石的表面，浮现出四个古老的文字。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;太古怪了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘从来没有见过这样的文字，却一眼将四个文字认出来——“时空晶石”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;要知道，在上一世的时候，张若尘也曾将真气注入白色晶石，但是，却从未将晶石表面的文字激发出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“上一世，我的修为比现在强大数百倍，也没有让白色晶石的表面出现文字。这一世，才黄极境初期的修为，居然就让白色晶石出现了变化。说明根本不是真气强度的原因，而是真气属性的原因。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;比如，拥有烈焰神武印记的武者，使用火属性的灵晶效果最好，可以达到三倍的修炼速度。使用普通的灵晶，就只能达到两倍的修炼速度。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;自然界中，百分之九十的灵晶都没有属性。就像百分之九十的武者，也只能激发出没有属性的武道印记。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“难道我开启的神武印记也拥有某种属性，正好与这一枚时空晶石相契合？等一等，时空晶石是什么东西？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘上一世可是明帝之子，见识极广，听说过很多属性的灵晶，赤焰灵晶、玄冰灵晶、雷电灵晶、邪血灵晶……，但是，却从未听说过时空属性的灵晶。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为，时间和空间根本不是人力可以掌控，就算是神，也不能撼动时间和空间，必须要遵循时间和空间的规则。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在张若尘还十分不解的时候，时空晶石的表面浮现出一团光晕，化为一个漩涡。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;漩涡不断变大，包裹住张若尘的身体。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘只感觉一阵天旋地转，下一刻，他便来到一个密闭的空间，重重的摔落到坚硬的地面上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;幸好他完成了洗髓冲脉，达到黄极境初期，身体增强了不少。若是以他以前脆弱的身体，摔这一下，估计就已经摔死。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘从地上爬起来，活动了一下疼痛的筋骨，开始观察四周的空间。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个空间完全密闭，看不到门窗。空间的高度，大概是十米，长和宽差不多也是十米左右。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“怎么会这样？我从什么地方进来的？这里又是哪里？咦！那里有一座石台！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;整个空间中，只有一座石台。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;石台上面放着一幅卷起来的画，一本银色的铁书。除此之外，便没有别的东西了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘率先去拿那一幅画，但是，那一幅画却沉重无比，像是和石台连成一体，无论张若尘使用多大的力量，画卷也纹丝不动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;既然将画卷拿不起来，也将画卷打不开，张若尘只能暂时放弃，然后将目光盯向那一本薄薄的银色铁书。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;银色铁书的表面，书写着四个字：“时空秘典！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一次，张若尘有所准备，将真气运转全身，将全身的力量都调动起来，将《时空秘典》的第一页翻开。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这么……容易？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;《时空秘典》翻阅起来十分轻松，丝毫都不费力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘悻悻然的摇了摇头，停止运转真气，将《时空秘典》拿起来，捧在手中，仔细阅读。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;《时空秘典》的第一页上面记载的并不是什么修炼秘典，而是时空晶石上一代主人“须弥圣僧”的笔录。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘将须弥圣僧的笔录看完之后，终于明白了所有事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“原来我开启的神武印记，竟然是时空神武印记。根据须弥圣僧所说，亿万人中也没有一个能够开启时空神武印记。从古至今，仅仅只有两个人，算上我就是三个。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“须弥圣僧就是第二个开启时空神武印记的人，但是，根据他在《时空秘典》上面记录的时间，他在十多万年前就已经死了。十多万年前，那已经是中古时代，离现在太遥远了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘现在所处的空间，就是时空晶石的内空间。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内空间中的时间流动速度和外界完全不一样，在内空间中修炼三天，外界才过去一天。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘大喜，激动得道：“在里面修炼三天，外面才过去一天，岂不是比别人凭空多出三倍的修炼时间？真是太好了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘想要翻阅《时空秘典》的第二页，但是无论他使用任何办法，却都无法将第二页翻开。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“又无法打开。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘有一种想要将《时空秘典》摔在地上的冲动，忽然，他看的第一页的最后一排还有一行小字。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“修炼到黄极境小极位，将真气注入画卷，可以将画卷打开。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘再次将目光盯向那一副画卷，心中猜测，画卷中肯定记录了某种神秘的功法，说不定就与修炼时间和空间有关。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“努力修炼，争取早日突破到小极位，倒要看看，画卷里面到底隐藏着什么秘密？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“小极位”是黄极境的第四个小境界，再往上就是中极位，大极位，大圆满。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘的腹中传来一股饥饿感，便立即从时空晶石的内空间暂时退出去，再次回到他的房间。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他将时空晶石紧紧的捏在手中，有了这一块晶石，就有更大的把握在三个月之内修炼到黄极境后期。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘、林妃、侍女云儿，坐在一起吃早饭。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;吃的东西，仅仅只是白米和馒头，根本看不到肉类。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于武者来说，消耗的体力比普通人要大得多，吃的东西就显得相当重要。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;别的那些开启了神武印记的王子和郡主，每天吃的都是用蛮兽的血液炼制成的“血丹”，早就不再吃普通的食物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;服下一粒血丹，就能补充一整天的体力消耗，就算修炼一天的拳法、剑法，也不会感到饥饿。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而且，服用血丹还是提升血气，增强武体，增加肉身力量。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是张若尘就吃白米和馒头，就算每天吃八顿饭，也远远不够身体的消耗。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“尘儿，你现在已经开启神武印记，不能再吃普通的食物。这十枚血丹你先收下，若是不够，娘亲在想别的办法。”林妃将一只玉瓶取出来，递给张若尘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘正在啃着馒头，根本没有想到林妃居然能够拿出十枚血丹，有些疑惑的道：“娘亲，五枚银币才能买到一枚血丹，十枚血丹就是五十枚银币，你哪来那么多钱啊？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃笑了笑，道：“娘亲总是有办法的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;侍女云儿站在林妃的身后，道：“娘娘是将最喜爱的步摇金钗卖了，才去丹市兑换了十枚血丹！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃轻轻的瞪了云儿一眼，像是在怪她多嘴，随后又道：“尘儿，你别多想，只要你能够修炼成一位真正的武者，娘亲就算将所有饰品全部卖掉，也会支持你修炼下去。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘的心中无比感动，紧紧的捏着手中的玉瓶，咬着嘴唇，很想告诉林妃，自己现在已经修炼成一位真正的武者了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不行，不能这么做。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仅仅一晚上就开辟出气池，完成洗髓冲脉，实在太快了，若是消息不小心走漏出去，肯定会传到别的王子和王后的耳中。到时候，反而会害了自己，也害了林妃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他现在还是太弱小了，需要更加强大的力量。字-符防过-滤请用汉字输入неì岩ｇｅ擺渡壹下即可观,看最.新%章&節</p>',9,4),(40,'万古神帝','第五章 龙象般若','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;拿着血丹，张若尘返回自己的房间，继续卖力修炼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“娘娘，据说武者不仅仅只是要服用血丹，而且还要修炼功法，必须拥有修炼功法，才能开辟出经脉。”侍女云儿道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林妃望着张若尘离开的背影，抿着嘴唇，苦涩的点了点头，道：“我知道！但是，哪怕是最低品级的功法，也要卖五百枚银币以上，以我现在的财力，根本买不起。而且，现在是王后和国师在主持朝政，王后根本不可能让尘儿去‘藏书殿\'领取修炼功法和武技。看来只有一个办法了！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;侍女云儿道：“娘娘要去求林家人？可是三年前，娘娘已经和林家彻底闹翻，他们会给九王子殿下修炼功法？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“只要他们愿意给尘儿修炼功法，就算要我跪在地上认错，我也一样可以跪的。”林妃像是想到了什么往事，眼中不禁流下眼泪。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“可是当初明明不是娘娘的错……”侍女云儿轻轻的一叹。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;时空晶石的内空间，灵气十分充足，浓郁程度大概是外界的两倍。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;要知道，在云武郡国，那些名山大川的灵气浓度若是达到一点五倍，就已经算是修炼宝地，会遭到各大家族的争夺。亲手动輸入字母網址：неìУаПｇｅ。Сом即可觀看新章</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;坐在内空间的中央，张若尘将那一只装着十枚血丹的玉瓶拿出来，从玉瓶中倒出一粒血丹，拿到鼻尖轻轻嗅了嗅。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“血丹”虽然是用蛮兽的血液炼制而成，但却并没有血腥味，反而带着一股淡淡的清香。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;炼丹师在炼制血丹的时候，去掉了其中的血腥气，又加入了虎萝草和曼陀花。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;长期服用血丹，不仅能够为武者提供源源不断的体力，而且还能改善武者的经脉、骨骼、脏腑，使武者的身体变得更加强大。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“只是一品血丹。”张若尘轻轻的点了点头，喃喃自语的道：“以为现在的修为，服用一品血丹就足够了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘将一枚血丹服进嘴里，便立即将玉瓶重新盖上，放到石台上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在真气的催动下，血丹的血气，很快就融化开，为张若尘提供源源不绝的体力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我虽然达到黄极境初期，成为了一名武者，但是这一具肉身实在太瘦弱了，根本不能和别的武者相比。必须要将自己的身体炼得强壮起来，要不然在和同境界武者交手的时候，会吃很大的亏。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于武者来说，不仅仅只是要修炼真气，而且还要修炼武技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“龙象般若掌！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘的脑海中浮现出这一套玄妙的掌法，在他记忆中的武技秘典里面足以排进前三，正好适合他现在修炼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他的双腿分开，腰盘下沉，将体内的真气充盈到双腿，固定住身体，缓缓的抬起一双手臂，开始按照某种玄妙的规律，拍动双掌。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在脑海中，想象自己是一头力量无穷的太古神象，吞云吐雾的深渊魔龙，打出的每一拳都用尽全力，就像是要将体内的每一丝力量都打出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;每一掌打出去，全身的肌肉都被拉动，体内的真气也跟着融入肌肉和筋骨，使肌肉和筋骨发生脱变，变得更加有力，更加坚韧，甚至和真气融为一体。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;龙象般若掌，一种有十三招掌印，属于王级下品的武技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;确切的说，若是能够将龙象般若掌修炼到第十三掌，就算和神级武技相比也相差不多。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;功法和武技都分为五个等级：人、灵、鬼、王、神。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;龙象般若掌第一掌“蛮象驰地”，修炼成功，威力堪比人级下品的武技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;龙象般若掌第二掌“飞龙在天”，修炼成功，威力堪比人级中品的武技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;龙象般若掌第三掌“龙象归田”，修炼成功，威力堪比人级上品的武技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;龙象般若掌第四掌“龙形象影”，修炼成功，威力堪比灵级下品的武技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;龙象般若掌的第十三掌，被称为“龙象灭世”，威力堪比神级武技，发挥出来的力量不可想象。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;龙象般若掌的前几掌，只能算是低等武技，威力并不强大。而且，龙象般若掌，至刚至阳，极难修炼，能够修炼到第七掌的人都少之又少。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第七掌之后，每修炼一掌都需要耗费大量时间，大量精力，若是抵挡不住体内至刚至阳的力量，甚至有可能焚体自燃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;正是因为这些原因，导致龙象般若掌只能算是王级下品的武技。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然龙象般若掌十分难修炼，但是，却正好适合张若尘现在修炼，可以在短时间之内，将身体炼得强壮起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“龙象般若掌第一掌，蛮象驰地。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘先是立着马步，随后，快速踩动步伐，猛冲出去，快速打出掌印。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;静如山岳，动如蛮象。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一遍又一遍的练习，直到将体内的真气耗尽，才擦干汗水，盘坐在地，利用眉心的神武印记吸收时空晶石内空间中的灵气，转化为源源不绝的真气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在时空晶石的内空间中，一连修炼了九天，终于将龙象般若掌的第一掌“蛮象驰地”修炼成功。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内空间中的九天，外界也就只是过去三天而已。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“也不知以我现在的修为，施展出龙象般若掌的第一掌，力量有多强？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘从时空晶石的内空间走出来，来到后院，站在院落的中央，运转体内的真气，充斥在双腿。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“蛮象驰地。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他踩着规律性的步伐，猛然冲出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着步伐的迈进，一股强劲的力量，从双腿冲起来，经过腰腹、脊梁，涌到背部和双肩，从双臂喷涌出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然仅仅只是一招掌法，但是却调动了全身每一处肌肉的力量，爆发力自然相当惊人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嘭！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;双掌击在一块半人高的千斤大石上面，然后，立即收掌，踩着刚才的脚印，疾速退回原地。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘向着那一块千斤大石看去，只见大石的表面多出两个浅浅的掌形凹痕。大石的底部，向着泥土中下沉了两公分左右。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于这一掌的威力，张若尘还是颇为满意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然龙象般若掌现在只是人级下品的武技，却比别的人级下品的武技更加高明，爆发出来的力量也更加强大。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“品级越高的武技，修炼起来越难。若是我现在就直接修炼灵级的武技，根本不可能在九天之内修炼成功，至少也要花费半年以上的时间。而且，以我体内的真气数量，也无法施展出灵级的武技。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修炼武技和修炼功法的时间，必须合理的安排。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是偏重于修炼武技，而忽略功法的修炼，就会导致修为境界缓慢。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是偏重于修炼功法，而忽略武技的修炼，在与人交手的时候，就会吃大亏。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无论怎么说，修炼成龙象般若掌第一掌，张若尘在八百年后，终于初步拥有了自保能力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这九天，张若尘的修为也进步极大，气池中的真气已经修炼满，可以开始开辟第二条筋脉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是要开辟筋脉，就必须要服用洗髓液。王后仅仅只给了张若尘一份洗髓液，在开辟第一条经脉的时候就已经使用。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;要如何才能去弄第二份洗髓液，甚至更多的洗髓液？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“九王子，娘娘正四处找你，你在这里干什么？”侍女云儿看到站在院落中央的张若尘，便好奇的走了过来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云儿是林妃和张若尘身边唯一的侍女，大概十七、八岁的样子，长得颇为美丽，眼睛明亮，下巴尖翘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘走到云儿的对面，挡住云儿的视线，以免她看到远处的那一块千斤大石上的两个手印，关切的问道：“云儿姐姐，你手臂上的伤好些了吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云儿轻轻的摇了摇头，道：“伤筋动骨一百天，恐怕还要过两三个月才能痊愈。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她手臂上的伤，就是前几天被八王子一掌推出去摔断了骨头。对于她们这样的婢女，别说是摔断骨头，就算是将她们乱棍打死，八王子也不需要负任何责任。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在强者为尊的世界，弱者根本没有任何地位。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘道：“为什么不购买一份筋骨断续膏？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云儿忍着手臂的疼痛，苦涩的一笑：“一份最差的筋骨断续膏也需要花费两百枚银币才能买到，像我们这样的下等人，根本使用不起。九王子，你能够关心我们这些奴婢就已经很好了。赶快跟我去见娘娘，今天，我们要出宫。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张若尘跟在云儿的身后，好奇的问道：“出宫？去哪里？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“去见泞姗啊！很久没见到她了，你肯定很高兴吧？”云儿的脸上露出灿烂的笑容，盯着张若尘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;每次说到“泞姗”，他就肯定会面红耳赤，羞涩得像一个大姑娘。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“泞姗是谁？”这句话张若尘刚刚想要问出口，便立即咽了回去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很显然，病死前的张若尘，肯定是认识那一位叫做泞姗的女子，而且听云儿的语气，他们的关系说不定还很不一般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是张若尘问出“泞姗是谁”，肯定会露馅。既然如此，那就保持沉默，越少说话越好。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;幸好这些年，张若尘一直体弱多病，除了林妃一直关心他，便很少与人接触，要不然的话，估计他早就已经被人怀疑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云儿见张若尘竟然很镇定的样子，心头微微有些诧异，却也不多想，继续向着林妃居住的院落走去。字-符防过-滤请用汉字输入неì岩ｇｅ擺渡壹下即可观,看最.新%章&節</p>',9,5),(41,'邪王追妻：废材逆天小姐','第一章  撕心裂肺的背叛','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;夜晚，云落山顶峰星空下，凉风习习万籁俱寂。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落眼底温柔中带了一丝甜蜜，柔情脉脉地凝望着眼前男子：“云起，等退出组织，我们就在这里定居好不好？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云起剑眉星目中带着醉人的温柔：“丫头，就这么想退出么？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落转身眺望远处的夜空，回头眼底带着一丝明媚灿烂的笑容：“我从五岁进入组织，这十几年来一直过着杀杀的生活，没有一刻安宁日子。现在我已经厌倦了这样的生活，很想快点脱离出来。况且明姐已经答应我了，等交完这趟任务，就允许我退出。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说着，苏落拿出手中的小锦盒在云起面前晃了晃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沉浸在对未来美好生活向往中的她，没看到云起眼底闪过的一抹诡谲光芒。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“龙之戒？原来你已经拿到手了？”云起丹凤眼微眯，眼底是醉人的柔情。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯，所以这次你跟我一起退出，好不好？”苏落拉着云起的手，满眼希冀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好。”他轻柔的吻印在苏落光洁额头，强而有力的手一把将她拥入怀中，紧紧地拥抱着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;靠在他肩窝锁骨处，苏落眼底是满满的、幸福的微笑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;终于可以结束这刀头舔血的日子，回到最初的安宁了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青梅竹马十几年，他们从枪林弹雨中一起携手走过，他是她最重要的亲人，也是最信任的人，现在她已经怀了他的宝宝，等退出组织后……忽然，苏落浑身一僵，眼底晕满惨烈痛苦，她睁大眼睛，满眼都是难以置信的绝望。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一把推开云起，低头望去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此时，她的胸口插了一柄锋利匕首，鲜血沿着匕首不断地往出涌出，湿润了薄薄的素色长裙，胸口的鲜血有犹如妖冶怒放的彼岸花，炽热而诡异。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身为杀手，云起的匕首精准地没有一丝差错。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落踉跄着跪倒在碎石凌乱的地上，那双美眸中充满了惊诧绝望和难以置信，她实在没有想到，这个她最为信任口口声声称爱她的男人，会狠绝地将那柄自己送他的匕首，反手刺进自己心口。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那么决绝，冷酷，毫不留情。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;为什么……她张了张口，却因为绝望而发不出一丝声音。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云起冰冷而嘲弄地勾起唇角：“苏落，你太天真了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;你以为你为组织卖命多年，组织就会放过你吗？你以为我和你在一起真的是因为爱你吗？你简直太幼稚了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“……”苏落绝望地苦笑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她确实太天真幼稚了，竟然真的相信他会和她退出组织。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“想退出组织，从来只有一条路，那就是死！”云起捡起掉落在地上的小锦盒，冰冷地看了一眼痛苦绝望的苏落，转身决然离去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“云起，你难道就不天真吗？”苏落冰冷的声音在他身后响起。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;见云起停住脚步，苏落笑了起来：“你觉得，锦盒里装的是什么？”云起开锦盒，下一刻瞬间，他忽然脸色大变，“龙之戒呢？你把它藏哪了？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有个有钱的雇主出三十亿美元委托组织找这只龙之戒，如果最后找不到，不仅拿不到赏金，而且对组织的信誉也是极大的打击。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落站到悬崖边上，看着下面幽深不见底的黑暗，她嘴角绽放出得意却凄苦的笑，美的如同风雨飘摇中绽放的昙花：“云起，你永远都得不到龙之戒了，还有，别在我的坟头哭，脏了我轮回的路。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说着，苏落摇身一跃，整个人跳进黑暗悬崖。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;悬崖下方是惊涛拍岸的海水。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;------------</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这简直就是凌虐！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;时空转换。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;碧落大陆。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;痛。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落感觉自己全身如针扎似的疼痛，又似被重卡碾压过，痛的心脏几乎停止跳动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;迷迷糊糊中，她缓缓睁开沉重的眼皮，看着头顶脏污的白纱蚊帐，破旧的棉被，一时之间，她有些反应不过来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“苏落，你这个贱人，你怎么不去死？你还醒来做什么！去死去死去死！”一道尖锐刺耳的声音在苏落床前怒叫。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落发觉自己浑身无力，她虚弱地朝发出声音的地方望去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那是一个长相很漂亮的姑娘，大约十四五岁，一袭淡紫轻纱，头上灵蛇髻斜插宝蝶簪玉钗，小脸微微有些圆润，五官很是精致。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;小姑娘长的很漂亮，但行事却恶毒的很。此刻她手里拿着一根纳鞋底的针，针身很粗，泛着幽冷寒光。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她狰狞着双眼，毫不留情地一下一下刺在苏落身上，她刺的都是藏在衣服里的肉，不掀开衣服外人根本看不出来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;好痛！这简直就是凌虐！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落想说话，却发现自己嘴巴被破布堵住了，想反抗，却发现连抬起手指的力气都没有。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那恶毒丫头见苏落醒着，对着另外一个略大些的姑娘冷声吩咐，：“三姐，快打，快打死她！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;于是，这位三姑娘很听话的用力朝苏落脸上甩巴掌！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落眼底寒光闪闪：这番凌虐，这些巴掌，我苏落全都记下了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落再也熬不住，最后陷入泥沼般的黑暗中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“小姐……呜呜……小姐你不要死啊……”稚嫩的女声哭的凄惨悲切，似乎嗓子都哭哑了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;被一阵哭声吵醒，又感觉有人用力的摇晃她，苏落幽幽醒转。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“小、小姐？”绿萝正哭的伤心，抬眸对上苏落的视线，脸上顿时惊喜交加。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此时苏落也看清楚了眼前的小丫头。大约十四五岁，五官还算秀气，不过此刻脸上布满了红肿指印，双眼如桃子般肿胀，看起来好不狼狈。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;视线转移到房内，她发现桌子是缺腿的，椅子是破烂的，喝水的茶壶杯子也都是缺损的，整个屋子看起来就像非洲的贫民窟。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;忽然，苏落只觉得脑子一痛，记忆如潮水般涌来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;原来她是真的穿越了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这里不是她熟悉的任何朝代，而是从未在中国历史中出现过的碧落大陆，这是以武为尊的世界。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大陆上有四个国家，分别是东陵、西晋、南风和北漠，四个国家呈口字型围成一圈，在它们中间便是传说中的黑暗森林，里面魔兽横行，如果不是武者根本别想踏入。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落现在就在东陵国的大将军府。她的父亲是护国大将军苏子安，而她则是大家口中所谓的废柴草包呆子白痴四小姐。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在碧落大陆，每个孩子五岁的时候都会进行一场天赋测试，这场测试重要到足以决定人的一生。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这场测试之前，苏落曾是苏家的骄傲，因为她一出生就天生异象，霞光满天，彩虹铺道，神鸟绕了整个帝都飞行一圈，当时人人都称苏家四小姐必成大器。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是在五年后的天赋测试上，这位最被看好的苏家四小姐却爆出冷门，竟是天赋为零的废柴，根本不可能习武！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;由于期待太高、落差太大，苏子安一怒之下将苏落扔到偏院任由她自生自灭，而苏落的母亲也被嫌弃，最后郁郁而终。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;------------</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;下次一定弄死她！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;难道她真的就是传说中的废柴？苏落望着悠悠白云，眼底却闪过一丝冷笑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她苏落在现代经历了十几年的魔鬼训练，就算是天赋为零，她也能闯出自己的一片天！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她还记得穿过来的那一日，那两个丫头凌虐她的情景。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“小姐，三小姐和五小姐今日到花园里散步呢，两人都没带丫环，不知道在说什么呢。”绿萝提着食盒进来，将食盒搁在桌上，拿出菜色一一摆在桌上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一盘烂菜叶子，一碗发霉的豆腐干，还有两碗饭。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不吃了，我先出去下。”苏落将碗筷一推，眼底闪过一丝冷意。她苏落别的本事没有，就爱记仇，而且有仇必报。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;花园里，三小姐苏挽和五小姐苏溪，两个人正沿着花园的荷花池走着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;五小姐苏溪是嫡母所出，身份尊贵，而且小小年纪天赋惊人，是整个苏家的宠儿。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三小姐苏挽，她和苏落一样都是庶出，不过她嘴巴甜，而且平日里惯会巴结苏溪，一切以苏溪为主，所以两个人的关系看起来还挺不错。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;隐隐的，传来苏挽的声音：“五妹，听说死那丫头又醒过来了？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏溪冷笑：“她命贱的很，下毒都毒不死她，打也打不死，真是讨厌！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏挽又道：“那怎么办？那婚事岂不是……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏溪恶狠狠地握拳：“你放心，下次我一定弄死她！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此刻，她们正沿着荷花池散步，苏溪走在内侧，而苏挽则走在外侧。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落嘴里叼着一根稻草，听着她们商量着谋害自己，眼底闪过一丝冷意，她倒要看看，现在谁敢再对她动手！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;听说这苏溪天赋很高，现在小小年纪已经是二阶武士了。现在，自己虽然还没能力报仇，但收取点利息却是没问题的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落隐藏在梧桐树后，眼底闪过一丝狡黠的光芒。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她衣袖翻飞，一颗小石头好巧不巧地滚落到苏挽脚边。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏挽目视前方，哪里顾及的到脚下？她一脚踩上去，身子顿时重心不稳，歪歪斜斜的往苏溪那边倒去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;人在摔倒的时候，总有抓住身边一切可抓之物的本能，所以苏挽很幸运地扯住了苏溪的衣袖。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;然而，很不幸的是，就在两人歪歪斜斜的时候，忽然一记佛山无影脚猛然朝苏挽屁股踹去！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;毫无预兆的攻击让苏挽措手不及，而她此刻又牢牢揪住苏溪的裙子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;顿时，两个人双双朝水渠中飞去，嘭的一声，重重跌落进水渠中，淋成了落汤鸡。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而苏落此时早已经隐藏回了梧桐树后，双手环胸，眸中流光溢彩，坐等着看好戏。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她倒要瞧瞧这对合作无间的好姐妹内斗起来是如何的精彩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;面对这无妄之灾，其实苏溪挺无辜的，但是谁叫她谁不好得罪，偏偏去得罪苏落呢？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她被苏挽连累，一头栽进水里，本就脾气娇纵的她顿时气得大叫，一个巴掌就甩过去：“三姐你干嘛？自己摔倒就算了，干嘛要连累我也摔进去！！！”</p>',76,1),(42,'邪王追妻：废材逆天小姐','第二章  恶人自有恶人磨','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏挽好不容易浮出水面，迎接她的就是苏溪重重的一巴掌。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏挽真的是好无辜，她也不清楚自己怎么就摔倒了，不过她很肯定自己之所以会摔进水渠里，是因为有人狠狠踹了她后臀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏挽恨恨地捂住被打的右脸，欲哭无泪：“五妹，有人踹我，不是我想摔的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏溪冷笑：“这里就你跟我两个人，你觉得谁会踹你？你还不快上去找披风来给我穿？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“可是……”苏挽可怜兮兮地垂下眼睑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;被水淋湿的轻纱流仙裙此刻正紧致地贴在她身上，将她的身材凸显的玲珑有致，里面的红色肚兜很是明显。如果她就这个样子出去，被人看见的话，可不丢死人了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“要不，我们喊人吧？”苏挽抱着脑袋想半天，弱弱地建议。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不行！要是把男人叫来，被人看光了怎么办？你快去！”苏溪满脸凶狠！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不，不行……”苏挽死命摇头，怎么都不答应。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那你把身上的衣服扒下来给我穿！”苏溪气呼呼地就要去扯苏挽的外衣。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“五妹妹，住手，快住手……”苏挽抱紧胸口死活不让，她就外面一片薄纱，里面一件肚兜啊！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那你还快去？！”苏溪一把提起苏挽毫不留情地将她往岸上丢去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站在岸上的苏挽，身上的衣服紧贴着，胸前的两点小葡萄被风一吹，迎风而立，简直傲视群雄。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她冻得瑟瑟发抖，正欲往外拼命跑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;然而，正在此时，林中不知为何忽然着火了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;远远的似乎有人喊着：“走水了……林中走水了……大家快过去灭火啊……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;小树林就在这荷花池边上，离得非常近。周围几乎一目了然，没有假山也没有大石，根本无处躲藏。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;眼见无数的人往这里而来，苏挽被吓的脸色苍白，六神无主了，她双手护住胸前，急得在原地打转。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“快跑！快跑啊！”藏在荷花池内的苏溪急得大声催促！再不跑就来不及了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可是，现在已经来不及了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;眼看着无数的脚步声越来越近，苏挽吓的一哆嗦，她下意识地就往水渠里重新跳进去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嘭的一声，水花溅起来老高。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落差点笑出声来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过苏挽这个选择倒也不算笨。毕竟池里的水并不深，双腿可以站立，而且池中开满了莲花，遮遮掩掩的，不认真看根本不会发现里面藏了人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你又进来干嘛！”苏溪被气的头发都要竖起来了，她重重一巴掌就甩到苏挽脸上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏挽也不高兴了：“五妹妹，你别太过分了！”她也是有火气的好不好？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏溪凶狠地瞪着眼睛：“过分？谁过分了？如果不是你把我推进来，我会这样狼狈？你记住了，出去后我一定不会放过你！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“可我也不是故意的，确实是有人推我！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“谁推你啊，你倒是找出来给我瞧瞧！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无视纷乱的脚步，此刻两姐妹竟然直接就在水渠里吵起来了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落冷冷地看着，不好好意地笑着，漫不经心地看着这场难得的姐妹相残，她好期待接下去的戏码，一定要演的精彩才好，这样才不会枉费了她跑去放的那把火。</p>',76,2),(43,'邪王追妻：废材逆天小姐','第三章  俊美无双的少年','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;南宫流云一袭白衣悠闲的坐在荷花池不远处的繁茂树头，俊美的脸上微微勾起一抹弧度，俊眸里洋溢着兴致盈然，兴味十足。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;看来今天跑来大将军府果然是个不错的决定，没想到竟碰上这样一场好戏。还从来不知道大将军的几个女儿竟这般有趣呢。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;传说中白痴愚蠢的却反而机灵聪明；平日里被捧到天上的，却被整的狼狈凄惨。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;南宫流云的目光朝苏落望去，兴味地摸着光洁下巴。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这丫头年纪小小，头脑鬼的很，诡计迭出，身手倒也勉强可以看看。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;至于容貌嘛……南宫流云细细量着这丫头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过十四五岁的年纪，脸上是漫不经心的笑，一双美眸清澈动人，流光溢彩，不过眼底却似被隔了一层，反射出冷酷决绝的黑暗，黑的如同深渊，似乎谁也走不到她内心深处。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;南宫流云眼中闪过一丝戏谑，他得出结论：这丫头绝对是阴险狡诈，喜欢暗算，喜欢躲在暗处阴人，就算杀人还面带微笑的那种人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;简直与自己如出一辙呢，实在是太有趣了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;南宫流云忽然有一种找到同类的感觉，一种找遍了全世界才终于找到自己同类的那种奇妙感觉，这种前所未有的感觉让他对苏落顿时充满了兴趣。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此时，苏落忽然感觉到一丝不对劲，她发现似乎有一道灼热视线正牢牢锁住她。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落抬头望去，只见最高的那颗梧桐树上，一位俊美无双的少年正斜倚着，他嘴角挂着玩味弧度，看着她的眼眸里兴味十足。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只见他一袭翩然华丽的白衣软袍，眉若远黛，眼若桃花，浅浅的凤眸微眯，美绝人寰的俊颜上唇角邪魅勾起。他单手支额，随性地斜躺在高高地树桠上，片片纯白晶莹的琼花洒落在他身上，唯美地像从漫画中走出来的美少年。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他的眼睛清亮而犀利，仿佛洞悉一切，仿佛心底最深处的黑暗也给他这么一眼照亮了照清了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好看吗？”他笑吟吟地开口，脸上的神情似乎十分愉悦。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这虽然只有三个字，却一语双关。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;既可能在问这出戏好看与否，也有可能在问他的长相是否好看，或许，两者皆有。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落美眸微眯。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个男人什么时候来的？在她之前还是之后？她竟然一点都没有察觉到。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;是她的警觉性太低，还是他的武功修为太高？她知道自己一直告诉戒备着的，那么，是他的武功太高了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落嘴角缓缓弯起，冷冷开口，“看够了？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于不请自来还好整以暇看她好戏的人，苏落抱有一丝敌意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;南宫流云心中闪过一丝讶异，漆黑如点墨的凤眸对上了苏落的美眸，忽然，他发现自己胸口的心律跳动频率比以往快了一些。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他嘴角勾勒出一抹玩味的笑：“好一个阴险毒辣的女人。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落微微挑眉，似笑非笑的回了句：“好一个道貌岸然的男人。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;南宫流云对着苏落绽开一抹清华潋滟的笑，温润好听的声音邪魅低沉，“非也非也，本王与你是同类人。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;言下之意，苏落再嘲讽他，那么就是同时在嘲讽自己。</p>',76,3),(44,'邪王追妻：废材逆天小姐','第四章  胜者为王','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;好个腹黑狡诈的男人。等等……刚才他自称本王，如此说来他还是一位王爷了？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“过来。”对方那邪魅低沉的声音在她耳边响起。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落微微蹙眉。过去？他叫她过去她就过去，那岂不是很没面子？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;更何况，此时那树桠上已经几乎没有位置了，她上去了坐哪儿？难道坐他腿上吗？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;然而，还没等她想出个所以然来，忽然，她只觉得眼前一花，身子一动，再眨眼，她已经身在树顶，而且竟然真的稳稳地坐在这个第一次见面的男人怀里！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落哪里是会让人随便占便宜的？她下意识地一记手刀劈向对方颈脖动脉处――然而南宫流云的反应当真是快，还没等苏落的手划过，他已经单手将苏落的手反交在后。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个姿势，使得苏落胸前丰盈挺立，鼓鼓胀胀的，甚是傲人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落怎么都没想到，自己与此人的武功相差竟如此之大，自己在他面前竟然连一招都过不了！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;南宫流云邪肆一笑，修长润泽的手指漫不经心地划过苏落面容凝脂，优美的粉红色薄唇邪笑着上扬，带了点嚣张傲慢的味道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“丫头，你现在可打不过本王，怎样，还要继续吗？”南宫流云的声音痞性十足，带了丝邪魅低沉，煞是好听。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“放开我！”苏落侧眸，见那些人已经离的极近了，压低声音厉声警告。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“丫头，闲着无聊，我们来玩个游戏如何？”南宫流云神态怡然自得，声音邪魅低沉，眼眸中兴味十足。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落仔细想想，应该不会有比现在的情况更差的了，她冷着脸点头：“你说。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“就猜池里那两人吧，如果她们能躲过去不被发现，就算你赢，若是他们躲不过去，便算本王赢，如何？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“赌注是什么？”苏落穷的很，要赌钱那肯定是没有的，不过倒是可以空手套白狼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“胜者为王，败者……暖床？”南宫流云凤眸上挑，很有兴致地建议。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落神色清冷，冷冷瞪了南宫流云一眼，那眼神直白地像在看白痴。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;南宫流云表示很受伤，他捂住胸口，虚弱地建议：“胜者躺好……败者扑倒？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落简直无语了！这个男人还能再无耻一点吗？他们还是第一次见面好不好？第一次见面说话怎么就这么露骨呢？她一现代化过的人都觉得脸红。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这也不行，那也不行。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;南宫流云慵懒地拨弄着她耳边发丝，气定神闲地说，“难道你非要胜者为王，败者为后？若你执意如此，也不是不可以呢。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落没好气地翻白眼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“还为王为后呢。你不是太子吧？也不怕风大闪了舌头。”苏落揶揄地白了他一眼，干脆道：“哪里有那么复杂？如果我赢了，你欠我一个条件，若是我输了……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那就亲本王一口。”南宫流云打蛇随棒上，一点亏都不吃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个男人不占自己便宜是不是会死啊？真想狠狠抽他！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落瞪他一眼，却也因此，将他看的更为具体。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他眼底的笑随性慵懒，但与此同时，又有一种掩饰不住的绝世锋芒。这个人举手投足间气势逼人，流露出浑然天成的王者霸气。</p>',76,4),(45,'邪王追妻：废材逆天小姐','第五章  那就亲本王一口','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好。&lt;冰火#中文”好半晌，苏落才缓缓答道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这条件，怎么说都是她占便宜。亲一口而已，又不会少块肉？前世的她逢场作戏又不是没经历过。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她现在是什么都缺，正需要送上门来的冤大头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;于是，两个各怀鬼胎的阴险男女开始了他们的第一次交锋。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏靖宇一行人匆匆而来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏靖宇是苏家长子，夫人所出，天赋很不错，才二十岁，就已经是三阶高手，这是很多年青一代难以望其项背的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他并不是一个人来的，而是带了一群人浩浩荡荡地走过来，场面甚是威武。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏溪气的不行，但是又无可奈何，她将自己整个人藏身在荷花之后，生怕露出一丝端倪，心里已经将苏挽给骂的半死了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她怎么都没想到，她苏溪会有如此狼狈的一天！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果来的人单单只有苏靖宇那也就罢了，偏偏他的身边跟着的是太子殿下和世家公子。现在如果再出去，这般模样被他们看到，保准不到一刻钟的时间就传遍整个帝都，到时候她苏溪还怎么在帝都混？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏溪恶狠狠地瞪了苏挽一眼，她发誓，等她出去后，绝对不会放过苏挽这个贱人！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏挽也气的半死，她也没想到，自己运气会这么糟糕。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这眼看着场面是越来越糟糕了，来的人也越来越多了，这要是被发现了……难道说她们姐妹俩下水游泳？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是春天啊，又不是夏天。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;怎么办，到底该怎么办？苏挽急得蚂蚁般乱转。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏落看着那群锦衣少年，嘴角勾起玩味的笑容。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这倒是意外之下呢，没想到这群人会过来，看苏溪和苏挽悄悄地垂下脑袋缩在荷花后，苏落就明白，那些人的身份应该很了不得。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;为首那人应该是太子，只见他一袭淡黄色浅色锦袍，腰系腾龙束腰，刻意修整过的眉毛，高挑的鼻梁，浅淡薄唇，勾起的唇角里透出高贵不可一世的傲气，带着嚣张傲慢的味道，整个人看起来残暴阴柔，一看性子就不好。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孙靖宇一袭墨绿色锦袍，腰系水晶玉带，面容俊朗，剑眉星目，此刻他正紧蹙眉宇，神情似乎不悦。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;太子看着这场小火，哈哈大笑起来：“靖宇，你们府上这是干嘛呢？这是烧着玩儿呢？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不止太子，太子身后的世家子弟也都跟着笑起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为摆在他们面前的事实确实值得玩味，而且一看明显就是故意而为。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这哪里失火了？分明就是戏弄人嘛。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此刻，在他们眼前的是一个不大不小的火堆。底下是干枯易燃的草堆，而上面则覆盖了一层鲜活的绿叶。众所周知，柴没晒干，燃烧的时候烟雾特别大，而对方明显就是以此引诱他们过来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏靖宇此刻心中也有些狐疑，他好看的眉宇展颜一笑，对太子道，“可能是下人不小心弄出来的吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;太子摸着下巴，视线来回扫着：“哦？将军府的下人原来都如此胆大包天？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏靖宇心中一窒，忙解释道：“也有可能是妹妹调皮，弄出来玩的。现在既然没事，那便回去吧？今日老师教的功课靖宇还有点不明白，正好请教太子殿下。”</p>',76,5),(46,'轮回乐园','第一章 复仇者','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;夜晚时分，一座繁华中透漏着浮躁的二线城市。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;街道上车水马龙，夜生活的开始，让许多年轻人从闷热的房间中逃出，尽情挥霍着时光。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓蹲坐在一栋二层别墅的房顶，夜风吹过，让他感觉到一阵凉爽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身穿黑衣的苏晓，将身形隐藏在房顶的黑暗中，更何况他还戴着宽大的黑色兜帽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这身装束在酷热的夏天，就算是夜晚，也会让人感觉到闷热。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但对于苏晓来说，这都是小事，他已经在此等候两个小时，与他那不幸的经历相比，这不算什么。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;以苏晓现在的年纪，本应在大学中度过美好时光，可他却因为仇恨，在三年前放弃了学业，开始学习一些其他知识。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;人体解刨学、格斗、快速开锁等知识。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;漫长的等待开始，苏晓在等他的仇人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;两小时后，一辆黑色豪车缓缓驶入别墅院内，沉闷的引擎声熄灭，车门打开，一位略带醉意的中年人，从豪车内走出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可能是因为过量饮酒，中年人的脚步明显有些虚浮。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;别墅房顶的苏晓，拿起身边的一把长刀，长刀出鞘，刀身乌黑，借助夜色让人很难察觉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;纵身从五米高的房顶跃下，苏晓那灵活的手臂，在空中抓住别墅上突出的菱角，将下落的速度减缓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;平稳落地，苏晓落在了仇人的正前方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;没有任何废话，苏晓手中的长刀划破空气，发出一阵呜咽声后，一刀将仇人的喉咙斩开。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;鲜血喷涌而出，虽然苏晓极力躲避，但袖口与手背依然被鲜血侵染。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;已经醉酒的仇人，还没明白发生了什么，已经仰倒在地。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这种伤势与死亡无异，所以苏晓马上向人烟稀少的位置奔跑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;余光扫过，苏晓看到一名身穿制服的保安。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然他正在行凶，但被人当场目击也无所谓，他将相貌隐藏的很好。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可在下一刻，苏晓的寒毛直竖，那名保安居然在腰间拔出一把枪，黑黝黝的手枪，枪身修长，明显是装载了消音器械。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;保安怎么可能有枪，苏晓已经来不及仔细思考。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身后是至少十几面宽的别墅，那名保安距离他大概有二十米，如果选择逃，背后就彻底暴露给敌人，那就成了活靶子。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;遇到危险，苏晓的第一反应是解决危险的源头，绝不是逃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;挺身向前冲去，苏晓成S形奔跑，尽可能避免被枪击。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“噗、噗、噗……。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;安装了消音器的手枪，只发出微弱的枪声。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓才冲出五米远左右，就感觉小腿一麻，之后胸口传来同样的感觉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓知道这是中枪了，虽然他是个复仇者，但他并没中过枪。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无力的感觉在身体蔓延，苏晓并没有恐惧，只是有些不甘而已。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;复仇还没完成，他就已经倒下，而且是被未知的敌人杀掉，这是多么窝囊的一件事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;用尽最后力气，苏晓将手中的长刀抛出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一刀基本是听天由命，但老天仿佛察觉到苏晓的不甘，以及他那不公的遭遇，那把长刀在空中飞速旋转几圈后，竟奇迹般刺入到持枪保安的胸口。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;瘫倒在地的苏晓，脸上露出笑容。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那把长刀他淬了神经性毒素，而且是混合类，那名被刺伤的保安必死无疑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;意识开始模糊，苏晓感觉头部遭到重击，之后眼前漆黑一片。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在最后残存的意识中，苏晓隐约听到。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“猎杀者，‘轮回乐园’为你开启。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;--------------</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【身体传输中……】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【10%、50%、100%，传输完成，检核到猎杀者身体受到大量损伤，等修复……。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【猎杀者意识未苏醒，修复指令延后，现维持最低生存状态，维持时间十分钟……。“</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【滴…滴…，发现猎杀者天赋为成长天赋，生存时间追加两小时。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一片黑暗中，几排淡蓝色文字漂浮在半空，发出淡淡的微光。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在淡蓝色文字下方，苏晓满身血迹的躺在虚空中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;手指抽动，苏晓悠悠醒来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在醒来后，苏晓先是有些茫然，但回忆起那黑洞洞的枪口后，马上想站起身。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;剧痛传来，苏晓痛的险些再次昏厥。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;费力的坐起身，苏晓扫视周围，除了几行闪着蓝光的文字，四周漆黑一片。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【猎杀者，欢迎来到‘轮回乐园’】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一行文字凭空出现在苏晓面前，但苏晓并没去看那一行文字，而是开始检查身体的伤势。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;小腿被击穿，伤口处皮肉翻卷，手指都能轻易探入其中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这种血腥的情景，苏晓只是眉头皱起，并没有其他表现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他经历过比这残酷十倍的情景，所以这些并不能让他动容。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;胸口处的伤势更加严重，但无论是小腿或胸口处的伤势，都没流出血迹。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我没死？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;将手按在胸口，苏晓感觉到心脏在有力的跳动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【猎杀者，你并没有死亡，是/否愿意加入‘轮回乐园’，这里有你想要的一切。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓早就注意到那些可疑的文字，但出于对未知事物的警惕，他并没理会那些文字。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;现在的情况有些诡异，他受了必死的伤势，却奇迹般的活了下来，而且在他身旁，有一行血红色的数字正在倒数。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【1：35：10】，【1：35：9】……。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一小时三十五分零九秒，如果苏晓没猜错，这就是那些数字的意思，而且数字还在不停倒计时。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这血红色数字，给苏晓一种不祥的感觉，仿佛在这些数字归零时，将会发生很不好的事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【猎杀者，请尽快与‘乐园’沟通，以签订契约，否则你将在‘1小时35分’后死亡】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;果然，那些数字代表了他还能存活的时间，苏晓之前就有所猜测，他的伤势太重，早就应该死亡。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我的刀呢。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓没有在乎什么‘契约’或‘乐园’，而是向对方索要他的刀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那把刀是他父母留给他唯一的纪念，据说是他太爷爷缴获的一把军官刀，一直传到他这一代。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【现实物品无法带入‘乐园’中，请尽快签订契约。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓沉默，淡蓝色文字闪动，可能是没遇到过苏晓这种‘猎杀者’。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“契约？说说看，我需要付出什么，又能得到什么。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;逐渐减少的生存时间，让苏晓没有太多时间去耽搁，而且他感觉，他应该是遇到了什么‘超自然’事件，这让他的内心有些波动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓很需要力量去复仇，而‘超自然’事件在伴随巨大风险的同时，也可能获得非同寻常的力量。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【签订契约，你将穿梭在一个个‘衍生位面’中，去完成‘轮回乐园’发布的任务，获得‘世界之源’，最终以‘世界之源’的多少，来恒定奖励是否丰富。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【‘轮回乐园’无所不能。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大量文字浮现，苏晓仔细的阅读着。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“无所不能？能复生故去的人？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有淡蓝色文字忽然静止在半空，之后全部隐去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【鉴于你猎杀者的身份，不能。】</p>',61,1),(47,'轮回乐园','第二章 猎杀者','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;‘猎杀者’，这神秘的‘轮回乐园’，从一开始就这样称呼苏晓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个称呼，让苏晓感觉很奇怪，他并没有签订那所谓的契约。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“猎杀者这个称呼代表了什么。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓的这个问题，让‘轮回乐园’出现巨大的反应。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【普通人在签订契约后，将成为契约者，为‘乐园’执行任务，乐园给予奖励。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【所谓猎杀者，是一些天赋特殊的契约者，这些契约者的天赋更加适合战斗。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【猎杀者在执行普通任务的同时，‘乐园’将会同时发布猎杀任务，清理那些‘异常’的契约者。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【询问次数耗尽，‘乐园’不再回答猎杀者任何问题，请签订契约或等待死亡。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所有光芒消退，四周变得漆黑一片。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一张模样古朴的羊皮纸，在虚空中弹出，垂在半空。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓费力的站起身，走到羊皮纸前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;羊皮纸上满是奇怪的符号，在最下方位置，是大片的空白。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【是/否签订契约。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓隐约感觉，如果他同意签订这份契约，他的人生将会发生翻天覆地的改变。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他有太多事要做，就算前方是万丈深渊，他也只能纵身跃下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我~，同意。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓的话音刚落，手就不受控制的抬起，之后拇指按向羊皮纸的空白处。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刺痛在指尖传来，一抹鲜红的血迹将羊皮纸侵染，之后整张羊皮纸都变成淡红色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【契约成立！开始恢复猎杀者伤势。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;淡绿色光点在苏晓周围出现，攀附在他身体上，短短几秒钟的时间，他的伤势就彻底恢复。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【伤势恢复完成，强制觉醒猎杀者天赋。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身体才恢复正常，还没等苏晓松口气，一阵剧痛在身体各处传来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;剧痛来的非常突然，让苏晓身体各处的血管全部暴起。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但苏晓的忍耐力超乎常人，只是发出一声闷哼，单膝跪倒在地。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;剧痛来的突然，消退的也很快，苏晓察觉到，他体内仿佛有某种东西被唤醒。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那种感觉并不唐突，就好像他本身就有这种能力，但却一直没能察觉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【为了直观表现‘猎杀者’的综合素质，半数据化开启。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【警告；‘半数据化’为辅助战斗能力，心脏、大脑等重要组织严重受损，猎杀者依然会死亡。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【猎杀者专属试炼开始，如无法完成试炼，‘乐园’将剥离猎杀者身份，成为普通契约者，如在任务中死亡，猎杀者将会彻底死亡。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【乐园条例：一切都将等价交换，】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓刚阅读完那些文字，就感觉眼前一黑，之后失去知觉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【传送开始……】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;----------------------</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当苏晓再次醒来时，已经身处一间破烂的房间中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;房间只有十几平米大小，屋顶已经是半坍塌状，阳光直射而入。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;墙角处有大量蛛网，蛛网上已经布满灰尘，窗户与门已经不翼而飞，但四周没有丝毫的光线透入，只能看到大片的垃圾，一些垃圾已经通过门窗涌入到房间中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这间破烂的房间，应该是被掩埋在一座大型垃圾堆中，只有屋顶是出口。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刺鼻的味道传来，就在苏晓准备离开这里时，手臂上传来一阵灼烧感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一把利剑模样的黑色纹身，出现在他的小臂上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那种淡蓝色文字再次浮现，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但这次并不是漂浮在空气中，而是直接出现在他的视线内，就算视角调转，那些文字也一动不动，仿佛镶嵌在他的视网膜上一般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;文字的内容如下：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;进入世界；海贼王</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;世界难度：LV.6。炼狱（此难度为猎杀着试炼难度）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;世界之源；0%（猎杀者探索或与当前世界人物交流，将会增长，在结算世界时，与任务难度互相计算，形成最终评分）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;世界简介；大海，自由，海贼，海军，世界政府。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;主线任务；刺杀‘哥亚王国’国王。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;支线任务；无。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;警告：请勿在本世界提及‘乐园’的一切，如警告无效，将强制处决！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;提示：本世界为高等‘衍生世界’，请勿离开‘哥亚王国’区域，否则世界难度将大幅度提升！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;提示：因猎杀者初次进入世界，本世界语言猎杀者自动掌握，期限（三个自然日）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;世界，开始！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;文字隐去，苏晓开始梳理他了解到的信息。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;首先，他来到了一处很神秘的地方，这里名为‘轮回乐园’，‘轮回乐园’的目的他不得而知，但他的目的很明确，那就是在‘轮回乐园’中得到力量，有了力量才能复仇。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其次就是他现在的处境，他居然来到了‘海贼王’世界。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他虽然要复仇，但在闲暇之余，他会看一些动漫放松一些心情，人不能总是紧绷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;以苏晓对海贼世界的了解，这里是一个高危世界，到了后期，那些强者甚至能打沉一座岛，或独自屠灭一个国家，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然精通一些杀人技巧，但苏晓完全是一个普通人，到了这种满是非人怪物的世界，可谓是举步维艰。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从世界难度的‘地狱’两字，就能看出这个世界的难度。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;至于最后给出的任务，苏晓更是不明所以。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【刺杀‘哥亚王国’国王】，就这简单的几个字，没有其他线索。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在苏晓有些头疼时，一股信息从手臂上那利剑模样的纹身内传来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他可以用意识激活‘烙印’，所谓烙印，就是他手臂上那利剑模样的纹身。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这东西很重要，有许多功能。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓尝试激活烙印，很简单就做到，眼前浮现几行文字。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一，个人信息。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;二，任务</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三，储蓄空间。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;四，技能。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;五，装备。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;阶位过低，其他功能未解锁……。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓现在除了第一项（个人信息）和第二项（任务)外，其他暂时用不到。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他迫切的想知道，任务的具体内容，从之前‘轮回乐园’变现出的态度来看，绝不会容忍任务失败，一般失败，可能就是死路一条。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;什么降为普通契约者，应该根本没有那种可能，虽然只是短暂接触，但苏晓敏感察觉到‘轮回乐园’的残酷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;打开任务选项，任务的具体信息出现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【主线任务：刺杀‘哥亚王国’国王】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;难度等级：LV.3。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;任务简介：‘哥亚王国’地处东海，统治着附近的城镇与村庄，周边坐落着‘风车镇’以及‘科尔波山’。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;任务信息：用任意方法杀掉‘哥亚王国’国王，‘菲利普・赫伯特’，对方常年身处王宫内，刺杀难度较高。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;任务期限：七十二小时内。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;任务奖励：猎杀者身份完全激活。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……、</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;看到以上信息，苏晓终于明白他要杀谁，以及身处的位置。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;难怪四周这么多垃圾，他现在所处的位置，是在‘哥亚王国’附近的‘废品终点站’，在原著中那座因为天龙人的到来，被焚烧的垃圾山。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;原著中的主角蒙奇・D・路飞，以及重要的角色艾斯、萨博，就是在科尔波山长大，山上还生活着一群山贼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而‘哥亚王国’的首都，就在‘科尔波山’附近，紧挨着他所在的‘废品终点站’。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;四周这些垃圾，就是‘哥亚王国’的首都所产生。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想到这些，苏晓反而不着急离开，‘废品终点站’游荡着大量流民，非常危险，他还是先了解情况后，再做打算。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;将任务面板关闭，苏晓打开个人信息。</p>',61,2),(48,'轮回乐园','第三章 独行者','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;个人信息如下：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;契约者13013号。(为保护猎杀者，此为虚假编号，任何手段无法锁定猎杀者。)</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;姓名；苏晓（猎杀者）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;等级；LV.1。(一阶)。每10级为一阶，等级提升无任何属性加成，为猎杀者在‘乐园’内权限，与世界难度、任务难度等对应。）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;生命值；100%。（此属性无法完全数据化，根据受伤程度而改变）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;法力值；60。（智力×10，当前法力值恢复速度，每小时3点。）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;力量：6 （攻击力、负重等相关）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;敏捷：7 （移动、攻击速度等相关）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;体力：5 （生命值、防御力、异常抗性等相互关）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;智力：6 （法术伤害、法力值、感知等相关）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;魅力：3 （社交、召唤等相关）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;幸运：1 （物品开启、制造等相关，此属性极难提升，请猎杀者珍惜提升机会）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;注：成年男性的标准属性为5点，幸运为1点。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;杀戮天赋：噬灵者，杀死目标后，将会永久夺取1―15点法力值。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此天赋每个世界最多夺取100法力值。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【个人信息为方便猎杀者估测自身实力，与真正战斗力无关，属性值的高低会影响猎杀者的身体素质，但战斗并不只是身体素质决定，请猎杀者谨慎】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓仔细查看几遍后，将他的个人属性记在心中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他现在的等级是LV.1，等级的作用不必多说，相当于他在‘轮回乐园’中的权限。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他所进入的海贼王世界，难度等级是LV.6，而他的等级却只LV.1，这就说明了，他在海贼世界非常危险。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在一般情况下，‘轮回乐园’不会把契约者传送到高出五个等级的世界，这一切都与他‘猎杀者’的身份有关。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这所谓的‘猎杀者’身份，有什么好处苏晓还不得而知，但坏处却体现出来，那就是危险。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;进入LV.6的世界，之后接下LV.3的任务，而他却只有LV.1，这‘游戏’的难度好像有点大。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而且根据‘轮回乐园’的介绍，他在今后的其他世界中，需要帮助‘轮回乐园’清除一些‘异常’的契约者。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这基本代表了，他与大部分契约者是先天的敌人，一同完成任务这种事，就算苏晓同意，其他契约者也不会同意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一旦发现他的身份，只会群起而攻之。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;谁能确定苏晓的猎杀任务中，没有他们的编号？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;至于最上面标注的保护措施，苏晓对‘轮回乐园’都不怎么信任，更何况是‘轮回乐园’给出的保护？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但也不是没有好消息，他的各项属性都优于常人，这是他刻苦锻炼的成果。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然在海贼世界这种身体素质不算什么，但却给了他更高的起点。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓的实力，绝不是各项属性表现出的这些，他还有一种特长，那就是他懂得如何去战斗，并且敢于战斗。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他虽然没有那种洞悉一切的智谋，但却有一颗不惧受伤或死亡的心，并不是他完全不怕，只是相比普通人，他的胆量更强，敢去做一些危险的事。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一点，就是苏晓在‘轮回乐园’中闯荡的资本。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有时，一颗勇敢的心，远比强大的身体更为重要。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那么，让我见识一下，这海贼的世界到底有多危险。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓矫健的前冲几步，脚踩窗沿的一角，纵身跃起，双臂上伸，抓住房顶的墙檐，之后双臂发力，将整个身体向上牵扯，上身从天花板的破洞处中探出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在苏晓准备查看外面的情况时，一声脆响传来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“砰。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;是枪声，有些沉闷的枪声，苏晓马上做出判断。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;将身体缩回墙壁后，双手紧扣墙檐，苏晓的头慢慢探出，查看外面的情况。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“砰、砰、砰……。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;爆豆般的枪声，在外面连成一片，经过苏晓的观察，这应该是‘废品终点站’的两伙势力在火拼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;双方各有几十人，正在中距离枪战。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓的留意到，双方的衣物都十分破旧，而且面黄肌瘦，应该都是‘废品终点站’的流民。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“托比，这片地盘是我们的，你这是什么意思，大家都是‘拾荒’的可怜人，你难道想赶尽杀绝吗。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;枪身平息了一些，双方开始大声争吵。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你的地盘？老子现在有十几把枪，这里以后就是老子说了算，等攒够了贝利，老子就带人出海，那时老子就是海贼了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;罗吉尔，放下武器，老子给你一个投靠的机……。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;托比话还没说完，就被一声枪响打断，感觉将头缩回到垃圾山后。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;破旧房屋中的苏晓，刚好能看到说话那人，那是一位大胡子壮汉，正满脸狞笑的站在一堆垃圾后，看来并不是想接受对方的投降，而是要灭掉对方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这名为托比的大胡子壮汉，与那些面黄肌瘦的流民不同，这家伙身材高大，全身肌肉扎结。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓心中叹息一声，他终于体会LV.6的世界有多么危险。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果他之前冒然出去，一定会被打成马蜂窝，而这只是开局，之后应该更加危险。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在苏晓等待两伙人散去时，一道灵巧的身影，突然从房顶跃入到他所在的房屋中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呼~，好险，终于找到安全的……。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;来人话说到一半，就看到正攀附在墙壁上的苏晓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那个……。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对方刚要说话，苏晓已经松开那紧扣墙檐的手，身体自由落下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;落地后，苏晓压低身体，脚下一蹬地面，几片枯叶飞溅而起，他犹如一只灵活的猎豹般扑向对方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对方刚想解释些什么，但为时已晚。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓先是一拳打在对方小腹上，他能明显感觉到，对方柔软的小腹被拳头顶的凹陷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;来人痛苦的弯下身，刚想惨叫出声，但苏晓的手掌，已经捂住对方的口鼻，只传出一声闷哼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这还不算完，苏晓一手捂住对方的嘴，另一只手手掌平伸，手如利刃，指尖狠狠点在对方的胸口上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咚。”胸口被重击那种独有的闷响传来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓能感觉到对方的身体一软，眼中带着恐慌之色瘫软下去，只能发出轻声呢喃。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在指尖重击对方心脏位置时，苏晓感觉到十分柔软的触感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“女人？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他之所以这么做，是因为无法判断对方是敌是友，还是先制服好一些。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不~，不要伤害我。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;犹若蚊蝇般轻细的声音传来，苏晓上下打量着来人。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;参差不齐的黑色，脸上满是灰尘，但没被灰尘掩盖的脸颊非常白皙，亚麻色的衣服十分宽大，可以看出对方的身体很瘦弱，而且年龄不大。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你是谁。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对方的目的苏晓能猜出大概，应该是来避难。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我叫米娅，我真的没有恶意，求你别伤害我。”米娅的声音很小。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;心脏被重击，米娅没有昏迷已经算是体质很强，在短时间内，她都没有自由行动的能力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不许出声，否则杀了你。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果危及到生命危险，苏晓可是会杀人的，他还有血海深仇未报。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咕噜。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只有十五、六岁的米娅，咽了下口水。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好，我只是想避难，不会出声。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;米娅突然感觉，和面前这个男人待在一起，并不比外面安全。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;外面的枪声逐渐停息，乱战应该很快结束。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓在等待，一旦外面的乱战结束，他会马上出去，虽然这样做很危险，但也同样是个机会，他很需要武器。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;开局双手空空，不要说像样的武器，苏晓附近连一根木棍都没有。</p>',61,3),(49,'轮回乐园','第四章 武器与货币','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;枪声逐渐平息，受伤者的哀嚎声传来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;暗中观察的苏晓，感觉时机已经成熟，彪形大汉托比一方，已经将敌方的流民打退。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;流民们的战斗毫无策略可言，一番乱射后，就开始胡乱追击。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从房屋中翻出，苏晓平稳落地，但马上就将身体压低。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;映入眼帘的情景，是连绵不断的垃圾山，青烟在垃圾山上升腾而起，一股腐臭味直冲入鼻。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓没去管这些，眼睛紧盯着远处地上的一把枪，那是一把短柄燧发枪，虽然有些破旧，但却是一把能杀人的凶器。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;短柄燧发枪的主人已经死亡，小半个喉咙被流弹打碎，死相极惨。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可能是死亡的位置比较隐蔽，彪形大汉托比的势力，并没有发现这把枪，否则在武器稀缺的‘废品终点站’。绝不会将这把‘短柄燧发枪’扔在这。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;低俯身体，苏晓开始向那把枪靠近。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但就在此时，一名身体消瘦的流民，已经冲到那把‘短柄燧发枪’前，那满是污垢的手掌，抓向‘短柄燧发枪’。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓眉头皱起，并不是因为对方抢占了先机，而是怕对方引起那两伙势力的注意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随手捡起一块拳头大小的石块，苏晓全力将石块抛出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;石块带起极强的力道，狠狠砸在流民的手臂上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“砰。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;流民被打了一个踉跄，同时发出一声惨嚎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓急步冲到对方身后，身体侧偏，膝盖狠顶在流民的脊柱上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;流民身体前弓曲，口水喷出一股酸水，但就算受到如此重击，那流民居然只是向前踉跄两步，并没有倒下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓瞳孔一阵紧缩，海贼世界的人类，身体素质到底有多强，这还只是身体素质最差的流民而已。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但此刻已经顾不上那些，苏晓发现那流民已经目露凶光，仿佛被激怒的野兽般，满口黄牙咬紧，咬合肌突出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这幅狰狞的模样，如果换做普通人，一定会下意识退后，但苏晓不同，他有战斗的经验，心中明白，如果这时逃，那裸露出的后背就会挨上一枪。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;拳头紧握，苏晓在流民还没恢复来时，已经一拳打在对方下颚。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;人体下颚处分布着大量血管，如果突然遭到重击，会导致脑供血不足，之后引发供氧不足，短时间昏迷。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“砰。”几颗带血的黄牙飞出老远。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;挨了苏晓全力一拳后，流民直挺挺的向后倒去，仿佛一根栽倒的木棍般。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;普通人被击中下颚，会昏厥几十秒，但海贼世界的土著体质都迷之强大，所以苏晓将流民的身体翻过，瞄准对方后颈又是一拳。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;砰的一声闷响，流民生死不知。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓抓起地上那把‘短柄燧发枪’，马上有一行文字出现在他眼前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;还没等苏晓查看，远处隐约跑来一大群人，是追击失败的托比势力。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓心中一阵发寒，在这时一旦被发现，他可能连说话的机会都没有，会被直接打成筛子，这些人都已经杀红眼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;左右扫视一番，苏晓脸色有些难看，他所处的位置，四周全是高耸的垃圾山，这些垃圾山成环形，将四周遮挡。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓可不敢爬上那极不稳定的垃圾山，垃圾山随时都有塌陷的可能，其中不知道掩埋着什么，如果倒霉被利器刺中，在这种恶劣的环境下，就算不死于失血过多，也会死于感染。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所以苏晓只能翻进那间破旧的房屋中，那位叫米娅的少女，正面带惧色的看着他。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这次不用苏晓提醒，米亚主动开口。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我不说话。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;察觉到自己已经说话，米亚赶紧将双手握在嘴上，目光还瞟向苏晓手中那把‘短柄燧发枪’。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在苏晓进入房屋后不久，嘈杂的声音在外面传来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一阵怒骂后，外面逐渐安静下来，看来那些人是回来搜刮物资，但听那充满愤怒的声音，收获明显不怎么样。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓松了口气，开始查看之前出现的提示。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【获得，破旧的燧发枪（白色）】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;破旧的燧发枪（白色）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;产地：海贼王，利奥波特作坊</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;耐久度：6/30</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;填弹数：4/6(连射燧发枪为海贼王世界特有技术）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;攻击力：2~13（根据距离计算）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;装备需求：力量1点可使用，力量超过3点可灵活使用。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评分：3（注：白色装备评分为1到10，评分为10的白色装备，将标注‘稀有’，附加特殊属性。）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;简介：这是一把缺乏保养的燧发枪，这让它高达9点的评分下滑到3点，请谨慎使用它，或许下一枪就是它最后的一枪，也是你最后的一枪。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;价格：150乐园币（此物品为几率拾取物品，无法带出海贼王世界）。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【猎杀者初次获得装备，开始装备品级介绍】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【装备分为，白色、绿色、蓝色……，阶位不足无法获得以下资料，请猎杀者自行摸索。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【猎杀者初次接触乐园币，开始乐园币讲解。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【使用乐园币可与轮回乐园或其他契约者进行交易，此为轮回乐园唯一通用货币】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【警告；请谨慎使用每一枚乐园币，因为那是你生存或变强的资本。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓若有思索的点头，看来轮回乐园有着严禁的制度，不仅将各种武器性能标注，并且还有完善的货币机制。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然苏晓知道接下来会经历许多危险，但他心中却有些期待，期待轮回乐园能让他变强。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然他现在还不清楚变强的方法，但‘轮回乐园’中一定有这种方法，否则他无法完成今后更加困难的任务。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仔细检查一番手中的【破旧的燧发枪】，苏晓脸上露出笑意。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;终于有了一把武器，不然在这充满危险的世界中，他难以心安。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但相比起枪械，苏晓更喜欢使用冷兵器，比如刀，那种刀锋细窄，异常锋利的长刀。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果他的配刀在身边，苏晓的战斗力最少提升五成。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;基本情况苏晓已经了解，现在就需要准备完成任务，任务时限只是七十二小时，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓不想尝试任务失败会怎样，所以他只能全身心投入到任务的完成中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可想要刺杀一个国家的国王，绝对不是简单的事，那就需要一些其他办法，至少也要能靠近那位国王。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;秘密潜入王宫的想法，苏晓从未有过，因为在海贼世界，有种东西叫做见闻色霸气。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;根据苏晓的记忆，这位国王应该是与世界贵族天龙人有关，身边就算出现精通见闻色霸气的高手，也不是没有可能。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不然凭苏晓的暗杀能力，杀死一位国王的任务难度，绝不可能是LV.3.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在满是监控设备的现实世界，苏晓都能实现复仇计划，更何况是在科技落后的海贼世界？</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但现在想这些还太早，首要的事，是了解他所处的位置，‘轮回乐园’可没有地图这种功能，什么都要靠他自行摸索。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓的目光，看向那躲在墙角的少女，对方就生活在‘废品终点站’，对这里一定很了解。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在察觉到苏晓的目光后，少女米亚身体蜷缩起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你要做什么。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女的声音有些颤抖，还带着哭腔，她一直隐藏性别，因为在废品终点站，女人是一种很危险的性别。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你饿吗？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓蹲在少女面前，脸上露出温和的笑意，但他的双眼中却有些冷冽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他不是一个善良的人，仇恨早就抹去他心中的善良。</p>',61,4),(50,'轮回乐园','第五章 枪火','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;米亚点了点头，但又马上摇头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在废品终点站，有一种东西最为稀缺，那就是食物，那些不思生产只靠拾荒为生的流民，基本都在饥饿的边缘徘徊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓在怀中掏出几块压缩饼干，这是他随身携带的应急食物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他观察过，在‘废品终点站’的远处就有一片森林，他手中有枪，并不会为了食物而发愁。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;米亚有些畏惧的接过一块压缩饼干，她从没见过这种食物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在苏晓的指导下，米亚剥开了压缩饼干，小心的咬了一口。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好吃~。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;已经饥肠辘辘一天的少女，狼吞虎咽的吃了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你叫米亚吧，带我离开这里，这几块饼干都是你的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;吃光手中压缩饼干的米亚，直直的盯着苏晓手中那几块压缩饼干，咽了下口水。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“可以，但你不能伤害我。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓没有回答对方，而是从那间破旧房屋内翻出，恢复了一些的米亚紧随其后。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你要去哪？你不是‘废品终点站’的居民吧？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓脚步突然停止，眼中浮现寒芒，将手中一块压缩饼干扔在地上，之后一脚踩碎。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“带我离开这里，其他不要问。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果少女米亚表现出任何异常，苏晓那只紧枪的手，就会对准米亚的额头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;米亚被苏晓的举动吓了一跳，退后几步。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好~，好的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;米亚不敢再说，快步走在前方。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“带我去‘哥亚王国’的入口。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓警惕的打量着四周，不时能看到目露凶光的流民，那些流民注意到苏晓手中的枪后，马上收起贪婪的目光。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;回忆着动漫中得到的情报，苏晓开始思考接下来的对策。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所谓‘废品终点站’，其实就是一座大型垃圾场，因为‘哥亚王国’在城外倾倒了大量的生活垃圾，才有了如今的景象。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那些目露凶光的流民，原本只是‘哥亚王国’的居民，这些人或是犯了重罪，又或是得罪了贵族，才被驱赶到此。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;垃圾腐败后产生一种带有毒素的青烟，在‘废品终点站’中蔓延，时刻侵蚀着流民的生命。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最明显的体现是，苏晓的生命值已经从100%，下降到98%。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在‘废品重点站’中没有医生，没有食物，犯罪与疾病在此蔓延，这里是无法地带，一切都以力量的强弱为准。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过米亚的领路，苏晓很快走出‘废品终点站’，来到一面高墙前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黑石堆砌的高墙耸立，一扇不大的城门出现在苏晓视线内。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“只能到这里了，‘哥亚王国’有法律，流民不能进入城中，一旦发现就会被处死。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;米亚虽然在和苏晓说话，但眼睛却一直盯着苏晓手中的压缩饼干。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;将压缩饼干抛给米亚，苏晓不再理会对方，米亚也直接跑开，给苏晓领路只是因为食物的诱惑，没有其他原因。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;‘哥亚王国’城门大开，城门两侧站着几名守卫。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这只是侧门，作用是方便倾倒垃圾。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那些守卫虽然看似懒散，但眼中却不时闪过寒光，这些守卫应该经常杀人，杀那些意图闯入城中的流民。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓隐藏在一旁，并没着急进入城中，他在等待时机。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;和那些守卫硬拼根本不可能，海贼世界的居民体质都异常强大，少女米亚在被他重击后，居然能在短时间内恢复正常，这让苏晓警惕起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓猜测，那些守卫的属性，至少都是在十点以上，甚至可能更高。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一小时、两小时，直到三小时后，已经到了中午，气温快速提升，天气炎热起来，苏晓等待的机会，终于出现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一辆装满生活垃圾的马车从城中驶出，刺鼻的腐臭味传出老远，那些守卫根本没去看那辆马车，反而下意识退开几步。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“各位大人，需要例行检查一下吗。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;车夫将马车停下，有些畏惧那些守卫。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“赶紧走，臭死了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;守卫厌烦的挥着手掌，没有丝毫检查的意思。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;远处的苏晓在看到这一幕后，眉头一挑，快步跟上那辆马车。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;马车在深入垃圾山后，找了一处空地开始倾倒垃圾，之前还唯唯诺诺的车夫，腰间赫然多出一把匕首，弱肉强食，就是‘废品终点站’的规则，</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;点燃一支烟掩盖臭味，车夫拿起铲子，开始清扫那辆满是污渍的马车。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在车夫忙的满头大汗时，苏晓仿佛一只野猫般，悄无声息的来到车夫身后，就在苏晓准备打晕车夫时，脚下却踩到了一个半碎的玻璃杯。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咔吧。”一声脆响传来，车夫的身体一抖。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓也是一惊，在这脏乱的‘废品终点站’，脚下是很深一层垃圾，踩上去非常松软，根本察觉不到脚下有什么。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但此时箭在弦上不得不发，所以苏晓直接扑上前去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那名车夫也不是什么良善之辈，转身拔出腰间的匕首，直接向苏晓刺来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;车夫那凶恶的表情让苏晓明白，对方是想要他的命。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓的目光开始冰冷起来，在这任务世界，不是你死就是我亡，对敌人的仁慈，就是对自己的残忍。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;举起手中的燧发枪，苏晓将枪口对准了车夫的头。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果是普通人被枪指着，绝对会马上停止或后退，可这名车夫不同，他居然更快的扑来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓面无表情的扣下了扳机，枪体后的击锤撞在燧石上，火星溅射而出，枪膛内的火药被引燃，在狭小空间内大量火药被引燃，爆发出强大的动能，随着火光与烟雾四溢，枪膛内的弹丸喷发而出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“砰。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;浓烟升起，一股刺鼻的火药臭味传开，一颗铅弹直接没入车夫的头颅，车夫前冲的身形戛然而止，直接被打退出两米远，身体抽搐两下后死亡。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【提示：你杀死了黑市商人】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【你的天赋‘噬灵者’发动，永久增加5点法力值，现有法力值65点。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【你获得宝箱（白】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【这是你首次杀敌，储蓄空间开启（2平米），在返回轮回乐园后，储蓄空间可使用乐园币升级大小。】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;轮回乐园的提示让苏晓知道，这根本不是车夫，而是一位黑市商人，应该是利用车夫的身份在废品终点站内做着某种交易。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黑市商人死后，尸体上方漂浮着一个木箱，箱子整体成白色，只有手掌大小。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓尝试将漂浮在半空的木箱拿起，虽然只有手掌大小，但入手沉重。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【宝箱（白)，是/否开启】</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓并没有打开【宝箱】，而是将【宝箱】和【破旧的燧发枪】放入到储物空间内。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;意念一动，【破旧的燧发枪】在手中出现，苏晓对新出现的储物空间很满意，这是个非常实用的功能。如果利用好这种能力，对他完成任务有很大帮助。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这里并不安全，所以苏晓没有选择打开【宝箱（白）】，万一宝箱打开后，给他来个金光万丈，那他就危险了，现在首要的是进入‘哥亚王国’的首都城。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苏晓把上衣脱下，换上那名黑市商人的衣物。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;犹豫了一下，苏晓直接扑倒在地面的沾水中，恶臭传来，他的衣服和脸颊上，都沾染了大片污水，让人无法看清他的面容。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;坐在马车前，苏晓有些生疏的操控着马匹，向城内行进。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此时他手中拿着一把匕首，正是那名黑市商人手中的匕首，但这把匕首，与他想象的有些不同。</p>',61,5),(51,'凡人修仙之仙界篇','第一章 仙界篇外传一','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“在下韩立，是从灵界飞升的。这里就是传闻中的飞台！除道友外，好像并无其他人。”韩立单手轻轻一按池水，就一下飞出了水池，身上青光一闪，更是无声息的一点水渍都没有了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这时他已经发现，掌天瓶元合五极山等重宝全都安然无恙的在其体内，倒是魔光等人却藏在灵兽袋中全无任何反应了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而此地看起来只是一个十分简易高台，除了中心处的水池外，高台四周则被一层蔚蓝光幕笼罩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;光幕之外，隐约可见鹅毛大雪漫天飞舞，外面竟是一个晶莹的冰雪世界。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“原来是韩道友。灵界话，这可有些头痛了？下面的那些界面，十个倒有两三个都叫此名的。算了，我就随便给你登记一下吧。反正上面一般也不会来查这种事情的。”高升先是眉头一皱，但马上又一展的无所谓说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;接着他两手虚空一抓，手中顿时出现了一面晶莹玉书和一根淡银色巨笔，并飞快往上面书写着什么。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩立心中虽然有一分诧异，但面上并未露出，反而站在原地静静看着对方的举动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;片刻后，高升两手光芒一闪，玉书和巨笔同时消失不见，取而代之的多出了一张紫金色的薄薄玉牌，并面带笑容的冲韩立递了过来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是？”韩立并没有马上接过来，反而不动声色的问了一句。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是道友的仙牌，每一名飞升仙人登记在册后，都可以领取的东西。道友以后凭此东西，可以进入各大仙城而无需遭受任何盘查的，并且还进入一些特殊地方，而无需缴纳任何费用的。呵呵，这可只是你们飞升仙人才能有的待遇，像我等这些从仙界本土真仙可没有这般待遇的。”高升用一种异常羡慕的口气说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦，这是为何？”韩立终于露出一丝讶然来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“真仙界无边无际，本土仙人更是数以亿万记，但从下界靠自己力量飞升上来的仙人却少之又少的。但各个仙域中的高阶仙人中，飞升仙人和本土仙人数量却差不了多少。这代表了什么，道友应该很清楚了吧。”高升含笑的解释道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“原来如此，我有几分明白了。既然仙界有此规定，那我也就不客气了。”韩立有些恍然，点了点头，接着一抬手，就将紫金色玉牌接了过来，低首一看后，只见上面赫然铭印着自己的全身影像，栩栩如生，旁边则还标注了自己名字还有“飞升”等几个金篆文。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩立袖子一抖，玉牌顿时一闪的不见了踪影，口中又一笑的问道：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“听高兄口气，仙界似乎颇有秩序，但不知道友口中的上面指的是……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“此事韩兄不用多问，只要看完这枚记忆石中所记东西，也就大概有些了解了。”高升没在多说什么，手一扬，又抛过来一枚蓝汪汪的晶石，回道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那翰某多谢了。”韩立一把将晶石抓住，脸上露出一丝谢意，将其往额头上一贴，也就明白了使用方法，再略一催动法决后，就将神识浸入了其中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;足足一盏茶工夫后，韩立才轻吐一口气的将晶石从额头上拿开，并缓缓说道：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“原来如此，仙界如今被分成无数仙域，而这些仙域则又被各个仙宫加以监管着。这倒有些意思，只是监管而不统治？”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“道友果然是明白之人。既然这样，我给道友明说一下仙界如今的大势情形了。不知道友可有兴趣听上一二！”高升听到韩立的言语，脸上却浮现一丝奇怪之色的问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“还请高兄指点一二。”韩立一拱手，脸色凝重了几分。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“韩道友不用客气。仙界情形的确和仙界颇有些的不同的。仙域虽然名义上都是以仙宫为尊，但实际上每个仙域都有不少实力不在仙宫之下的大势力存在，甚至有些超强实力还能稳稳压住自己仙域仙宫一头。只是诸位道祖都需要仙界维持一定的秩序，以免出一些不好收拾的大乱子，这才默认各个仙域名义上都以仙宫为主的。当然这些仙宫本身也是仙域中有数的大势力，否则也根本无法服众的。而这些仙宫执掌者也大都很识趣，一般也不会去触怒其他和自己相当的大势力，所以现在的仙界还算安宁。只要不去触犯某些仙界禁律，自呢个在仙界能好好逍遥的。而道友是飞升仙人，情形和本土仙人还略有不同的。刚飞升真仙纵然经过雷劫洗礼，体内真元已经开始改变，可以接受仙灵力了，但若转化完全恐怕还需要数百年时间之久的。若在下是道友的话，必定会选择仙域某一势力依附，等体内真元彻底转化完成后，才会再考虑其他的事情。否则道友是初临仙界，光是日常的仙灵晶消耗，恐怕也是一笔不小的数目。”高升也不再客气了，侃侃而谈了一番。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩立一边听着，一边露出若有所思的神色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“多谢高兄指点，听道友口气也不是仙宫之人吧。”韩立笑了一笑后，忽然问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“韩道友看出来了，在下的确不是仙宫之人，而是石矶殿中人。之所以会出现在此，只不过是在应付仙宫交给本殿的一些差事而已。不过若是道友若肯现在加入本殿的话，高某自然是极为欢迎的。石矶殿不但能够提供其他势力所给的一切待遇，并且我还可做主，让道友在本殿登天阁中免费任选一部高阶仙家功法修炼。”高升也笑了起来，丝毫没有隐瞒之意的回道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;------------</p>',31,1),(52,'凡人修仙之仙界篇','第二章 仙界篇外传二','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“仙家功法？”韩立双目一眯，反问了一句。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“仙家功法是专门供仙人以上修炼的法决，只有修炼此等功法才能真正炼化仙灵力，让低阶仙人一步步向高阶仙人之境前进。不过大部分低中阶仙家功法即使修炼圆满，也只能让人提高一两个境界而已，到时又不得不再挑选一部重新修炼了。只有那些各大势力各自珍藏的高阶仙家功法，才具有让真仙直接修炼到极高境界的能力。而此种等阶仙家典籍，在外面是少有流传的。即使有那么一些，也都是一些大路货色而已。”高升似乎看出了韩立心中疑惑，主动解释了一番。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦，难道没有直通道祖境界的仙家法门吗？”韩立缓缓问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“道祖？哈哈，韩道友志气之高还真不是一般人可想的。但可惜，哪怕是仙界传闻的几大神典，也没有哪一部可以让人直接修成道祖的。现在仙界已知的数百道祖，每一人都是经历了不知多少次劫难，外加承受了庞人难以想象的造化天缘，才能最终有此境地的。就算最高深莫测的仙家功法，也只能让人修炼到道祖下的大罗境，再往上的话，只能完全靠自己了。”高升露出了一丝怪异的神色，但哈哈一笑后，还是加以解释了一番。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“看来成就道祖，是一件可望不可及的事情！但不知道祖之下，还有几层仙人境的。”韩立闻言，未流露出惊异的神色，反而点点头后又问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“虽然我等被下界人统称为真仙，按实际上真仙境只是仙人中低阶存在，上面还有金仙、太乙、大罗等几大境界。每一大境界的修炼，动不动都要百万年以上的苦修，故而其中还会再分细分几个阶位。纵然是同一大境界之人，有阶位相差的话，神通实力也会天壤之别的。”高升不加思索的回道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“高兄现在是何种境界”韩立若有所思的问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“惭愧的很，在下修成真仙已经数百万年之久了，但一直卡在真仙高阶无法再进一步。若是没有其他的造化，此生也只能在此境界渡过了。”高升闻言，苦笑了起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩立听了这话，脸色微微一变。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对方实力他丝毫无法看透，竟然还只是真仙境而已。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这般说来，像在下这般的新进飞升仙人，应该只是真仙境低阶了。”韩立喃喃说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“低位？勉强算是吧！毕竟韩道友现在仙元力还未彻底转化过来，只能算是伪仙，还要再等上数百年时间，才能算是真正进入低阶真仙境。”高升笑了一笑的回道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩立听了自然更是半晌无语，好一会儿，才再次问道：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“听高升口气，仙界中除了我等真仙外，普通修炼者也不少吧。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“仙界中的真仙以下修炼者的确存在。我等仙人纵然神通广大，但也不能保证自己后代也一定同样能修炼到仙人境界的，这些人因为无法吐纳接受仙灵力，寿元有限，只能再自行结婚生子，又会诞生更多的凡人。如此不断循环下，人数之多可想而知了。”高升似乎对这些凡人颇不以为然。“</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“原来如此！看来一部高阶仙家功法，对我等这些真仙的确十分重要了。”韩立点了点头，开始思量起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我们石矶殿对对飞升仙人算是颇为看重的了，其他势力不会开出比此好多少的条件。而且道友可以先在本殿担任外门客卿之位，若是过了几百年真觉得本殿真不适合道友的话，只要拿出和所选仙家功法相等的东西，也可自行离去的，本殿并不会加以阻拦分毫的。”高升似乎看出了韩立心中的最后一丝犹豫，又如此的劝说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“还有这种条件！好，既然高兄说的如此之好，那韩某就先去贵殿亲眼看上一看吧。”韩立目光一闪后，终于做出了决定。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就像对方所说，他现在最重要的事情还是先转化体内仙元力，将真仙境真正巩固下来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在此情形下，他先依附一大势力后，再慢慢了解整个仙界，也是一个不错的选择。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;高升听到韩立回复，自然大喜，连称韩立绝不会后悔后，就袖子一抖，一道虚影从其背后一分而出，再一凝后，赫然幻化成另外一名身穿银袍的“高升”。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你在这里先替我当值一段时间，我先带韩道友去石矶殿。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那要快去快回了，万一被此区域的监察使发现，我可无法替你掩饰多久的。”银袍高升面无表情的回道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这个你不用担心。这座飞仙台地处如此偏远之地，监察使者怎会轻易到此，上一次有监察使者出现，是好几百年前的事情了吧。再说我只是送人，多则半月少则数日就会返回的。”高升毫不在意的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你有数就行，到时候可别真连累到我了。”银袍高升不再多说什么，几步走到水池边上盘膝坐下，闭上双目，竟就此入定起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩立见此情形，脸上自然有一丝讶色浮现。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“让韩兄见笑了，这是我修炼的一具垢土化身，虽然实力还不及我，但对付一名低阶真仙还是绰绰有余的。有他替在此轮值，我也可安心离去了。走，高某这就送是道友去石矶殿。”高升一边笑着解释道，一边单手掐诀冲高台外光幕一点。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“噗”的一声。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;光幕应声而开的现出一个一人高圆形通道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;高升大步从中一走而出，韩立则一飘的紧随而去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;方一离高台的瞬间，漫天大雪铺天而下，同时一股奇寒之力迎面一卷而来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;纵然以韩立的肉身强横，也不禁眉头一皱。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;旁边高升目睹韩立情形，脸上不禁一丝讶色闪过。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;北寒仙域既然以寒字作为仙域之名，平常时外面温度就冰冷无比，若是碰到这样的雪天，空气中蕴含的奇寒之力厉害更是可想而知了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;即使他们这些本仙域土生土长的低阶仙人，也必须在佩戴一两件特殊炼制的防寒器物，才敢直接面对雪天的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而韩立这般一名刚刚飞升的仙人，竟也能面不改色的视此雪如无物，这怎不让高升暗暗心惊。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;高升心念飞快转动着，但手中动作却丝毫不停。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只见他蓦然往下方打出一道白色法决，下方厚厚雪层中数声清鸣之音传出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在雪花狂舞之中，两头通体洁白的巨大孔雀从雪地中一冲而出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“道友请！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;高升脚步一抬的上了其中一头雪孔雀后，用手指一点另外一头，笑着冲韩立说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;韩立微微一笑，也身形一晃的出现在了另一头孔雀上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在高升一低喝后，两头仙禽当即展翅而飞，迎着晶莹雪花的冲天而起。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而韩立在仙界行程，则就此踏出了第一步。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;正文卷</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;风起灵寰</p>',31,2),(53,'凡人修仙之仙界篇','第三章 狐女','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;辽阔荒地，渺无人烟。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一眼望去，这片苍黄的土地上，除了杂草灌木，便是随处可见的灰白色石头，大小不一，形状各异。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;骄阳悬空，炙烤着大地，漫空都是黄色的粉尘，纷纷扬扬。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;浑黄的天地间，一个瘦弱娇小的身影渐渐清晰，奋力朝着前方疾跑。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是一个约莫六七岁的女童，鹅黄罗衣上绣着朵朵淡粉梅花，一头乌黑头发绾成了双髻，一双灵动大眼黑溜溜地，小巧琼鼻下小嘴嫣红，衬得整个面庞细致清丽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童右手紧紧攥着一只巴掌大小的拨浪鼓。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这拨浪鼓应该有些年月了，两侧鼓面有些泛黄，上面绘着几条青蛇图案，连着双耳的两枚弹丸上下翻动，不时落在鼓面上，发出几声轻响。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童年纪不大，身形却是颇为灵动，几个起落，便出现在了一处齐人高的杂草丛前的空地上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;许是跑得急了，她雪白的额头上已满是汗珠，左颊上一条汗水流淌下来，直流入白嫩的脖颈中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她伸出左手擦了擦，紧绷的圆圆脸蛋红扑扑的，犹如熟透的苹果，脚步却没有停下分毫。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“妖孽，哪里跑！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在此时，后方远处蓦然传来一声男子厉喝。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童闻言身子一颤，吓得脸色“唰”的一下都白了，抓住拨浪鼓的右手抬起，奋力一转，嘴唇翕动几下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;鼓面顿时泛起一层淡淡青光，随着弹丸落在鼓面上发出一声轻响，一道青光从鼓面飞出，落在身前的一簇杂草之上。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;杂草表面顿时泛起一层青光，但青光只是一闪即逝，似乎什么也没有发生。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童右手接连转动几下，一道道青光随着拨浪鼓的两枚弹丸落下，从两侧鼓面一一飞出，落在周围的杂草之上，泛起一阵此起彼伏的青光。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;做完这一切后，女童脸色苍白了几分，但其顾不得休息，忙不迭的一头钻入前方的杂草丛中。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在女童身形没入杂草丛中没多久，后方十余丈外一阵尘土飞扬，一个身影疾驰而至，出现在了这片杂草丛前。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身影主人是一个四十多岁年纪的虬髯大汉，身材高大，单手提着一柄明晃晃的朴刀，看起来颇有几分凶神恶煞之感。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其目光落在前方草丛前留下的纤小足印，身形一动，正欲提刀冲入。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;结果就在此时，前方草丛一阵青光乱晃下，一下射出五六条青色长蛇出来，纷纷蛇口大张的朝虬髯大汉狠狠咬去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虬髯大汉先是一怔，继而左手飞快摸出一张符箓朝身上一拍，一层白色光罩浮现而出，同时右臂再一抖下，手中朴刀嗡嗡声大响，冲前方一劈而出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嗤啦！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一道数尺长寒光从朴刀中卷射而出，青光闪动下，当即便有三条青蛇被从中一刀两截的被劈飞，接着“砰砰”两声，其余两条青蛇也被大汉身上的白色光罩弹了开来，并在大汉刀光一个翻卷下，同样被砍成两段。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这些青色长蛇一个照面便被虬髯大汉击溃，并就此现出原形，竟是几簇绿色杂草所化。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虬髯大汉哼了一声，正要继续冲入草丛，突然面色一动，将朴刀一收的站在了原地。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身后沙沙之声传来，两个身影联袂而至。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其中一个瘦瘦长长的青年道士，一身灰色长袍看起来有些破烂，手里一把白马尾拂尘，另一人却是个短小精悍的马脸男子，脸上有几处淤青，头发看起来乱糟糟的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“两位道友没什么事吧？”虬髯大汉回头看向二人，问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这妖孽不愧妖狐之女，当真狡猾得紧，刚刚我和齐道友追的急了些，不小心着了道，没什么大碍的。倒是燕道友怎么停在了这里，那妖孽如今何在？”马脸男子摆了摆手，有些疑惑的问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虬髯大汉没有说话，朝眼前齐人高的杂草丛指了指，草丛前的纤小足印依旧清晰可见。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那还等什么？我等这便一鼓作气，将之擒下吧。”马脸男子见此，便要冲入草丛。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“此妖这般逃了一日一夜，怕是也快山穷水尽了，不过其擅使木属性幻术，在这片草丛中可谓如鱼得水，贸然追进去，怕是不妥。”齐姓道士眼睛微眯了一下，说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“难道就任其这般逃走？这妖孽小小年纪便如此狡诈，任其成年还不得遗祸苍生。”马脸男子略一迟疑，倒是停下了身形，不甘的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我等正道之士，除妖降魔自是义不容辞，这妖狐既被我等三人寻到，自不会再纵虎归山。”虬髯大汉正气凛然的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“燕道友言下之意，看来已有所对策了？”齐姓道士眼珠一转，问道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这片草丛面积不小，若是我等三人这般盲目追入，虽最终也能将此妖手到擒来，但恐怕还要花费不少功夫。听说冯道友前不久购入了一张火云符，而齐道友的引风术可谓炉火纯青，两相配合下，这片草丛自将荡然无存，此妖也无所遁形了。”虬髯大汉缓缓的说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“火云符……也不是不行。不过抓住这只妖狐后，皮毛归我，其他我什么都不要，如何？”马脸男子沉吟了一下后，道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不行，这妖狐必须活捉。”虬髯大汉摇了摇头，干脆的拒绝道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“燕道友这是何意？我这火云符可是初级中阶，花了我不少身家。”马脸男子面色一沉，声音蓦然一寒。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“冯道友稍安勿躁，若我没猜错话，燕道友应是血刀会外堂弟子，不久前会中可是下了任务，若能活捉此妖狐，不仅可成为内堂弟子，还能获一枚叱血丹和一千灵石奖励吧。”齐姓道士大有深意的望了虬髯大汉一眼，说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“没想到齐道友消息灵通，对于我们血刀会之事倒是知道不少。明人不说暗话，两位若能助我活捉这只妖狐，所有灵石奖励给两位平分，如何？”虬髯大汉面不改色的冲二人上说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“燕道友这般说了，贫道自然没有意见。”齐姓道士将手中拂尘往胳膊上随意的一搭。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“既然如此，那就事不宜迟，动手吧。”马脸男子说着，从怀中掏出一张符箓，有些肉疼的看了一眼后，往前方一抛，并念念有词起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那符箓表面顿时泛起一层红光，继而爆裂开来，化为一团赤红色烈焰，犹如一片火云一般，朝下方杂草丛压下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;呼！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;下方齐人高的杂草丛在火云触及之下，立刻汹汹燃烧起来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;齐姓道士见此，口中一声无量天尊后，骤然将手中拂尘朝外猛地一甩。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刹那间，“呜呜”声大作，数股白蒙蒙狂风凭空浮现，并呈扇形的朝着前方扩散而开。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;风助火势，火借风力，如狂涛骇浪一般沿着杂草丛迅速蔓延开来，半空中黑烟滚滚，草灰横飞。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虬髯大汉三人目光盯着前方的火海，眼也不眨一下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“在那里！”突然，齐姓道士一声呵斥，身形已如箭矢般弹射而出，朝着前方某处疾掠而去，手中拂尘一抖，尘须表面泛起青光隐隐。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虬髯大汉和马脸男子闻言，俱是精神一振，紧随齐姓道士身后冲了出去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只见前方二三十丈处，正被火海侵蚀的草丛中蓦然窜出一个娇小身影，赫然正是此前遁入草丛的那个女童。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只是此刻的她，身上鹅黄罗衣已多处破损，白皙脸蛋上更是被熏得乌黑一片。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她此前匿入这片面积颇广的杂草丛，为了不发出声响，小心翼翼的在其中改变着前进方向，听到身后没有声响传来，本以为可以松一口气，没想到对方竟用火烧来逼自己现身。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童小脸紧绷，神色间满是惊惶，二话不说的朝前方狂奔而去，有些慌不择路的样子，小口中不住咳嗽。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;结果其尚未逃出多远，只觉身后凉风习习，密密麻麻的青丝浮现而出，朝着其背后一卷而至。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;几乎是同时，女童头顶处传来“嗤嗤”声响，一张丈许大小的漆黑大网，朝下方一罩而下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童眼露绝望之色，但紧接着，其目光落在了前方十余丈外的一棵有些枯槁的小树，眼睛微微一亮，狠狠一咬自己舌尖。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“抓住了！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;马脸男子身形最慢，但看到前方被虬髯大汉和齐姓道士联手封住去路的女童后，顿时面露大喜之色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只见大片青丝横跨数丈的往前方一个倒卷，将女童捆的结结实实，同时上方漆黑大网也随之罩落。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但就在此时，无法动弹的女童身影青光一闪的溃散开来，化为了一株枯槁小树。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“木遁！”虬髯大汉和齐姓道士身形落下，望着眼前的变化，面色一沉。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“在那里！”马脸男子忙朝着前方不远处一指。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只见原本那里的一株枯槁小树一个模糊下，化为了一个手持拨浪鼓的女童，回头望了一眼后，便转身朝前方夺路而去。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“追！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;或许是因为催动木遁的缘故，女童此刻脸上丝毫血色也无，脚步也变得虚浮起来，跑的跌跌撞撞，速度自是大减，与后面三人的距离飞快缩短。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哎哟！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童蓦的一声惊呼，一个不慎的被足下一块石头绊倒，整个人往前飞扑出去，撞在了斜前方一块足有丈许来高的灰白巨石上，并重重的跌落在地。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“继续逃啊！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虬髯大汉等三人见此，倒是放缓了脚步，成犄角之势缓缓合围上来，马脸男子更是舔了舔舌头，阴测测地说道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三人联手之下，追了此女差不多一日一夜，一路上还吃了对方不少暗亏，此刻心中自然有些怨愤难平，尤其是马脸男子，更是为此搭入一张火云符。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童身子本就娇小玲珑，此刻在三人步步逼近下，整个人更是紧贴着身后巨石蜷缩成一团，但一双眼睛中，此刻却满是怨恨神情。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;看到女童脸上神情，三人自然毫不放在心中，反而马脸男子往前一步跨出，抬起一只手，阴沉说道：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“虽然不能杀你，但今日也要好好教训你这妖孽一番！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在此时，出人意料的一幕发生了。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只听“喀”的一声！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童紧靠的那块丈许高巨石表面，竟一声脆响的裂开无数细缝，不少小石子簌簌滚落而下，洒落在女童身上头发上，让其身子一个激灵。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咦”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;马脸男子见此，口中轻咦一声，抬起的右手也悬在了半空没有落下，虬髯大汉二人的注意力也暂时从女童身上移开，看向了其身后的巨石。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“喀啦”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在虬髯大汉三人目睹下，巨石表面的裂缝如蛛网般迅速扩散，不少石片从巨石上剥落，坠落在地。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“啊”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童显然也没料到身后的巨石会出现此等异样，吓得一下闭上了眼睛，双手抱着脑袋，一动也不敢动。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“轰”的一声，巨石终于在一声巨响中寸寸碎裂开来，大块石头四溅飞开，落在地上，掀起一片黄土飞扬。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虬髯大汉三人均被吓了一跳，脸带警色的稍后退了几步，但双目仍盯着巨石方向。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这……这是什么？”马脸男子脸色一动，有些愕然的叫道。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;透过渐渐落定的尘土，可以发现原本的巨石已是荡然无存，取而代之的，是一个身材异常高大的男子身影。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此人一身青色衣衫，头发和脸上蒙着一层灰蒙蒙的石灰，似乎此前便身处巨石中的样子，依稀可辨其面容普通，皮肤微黑，双目直勾勾的望着前方，看起来有些木然迟钝，但整个人比虬髯大汉还要高上一个头来。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这突如其来的一幕，让女童和虬髯大汉三人全都目瞪口呆起来。</p>',31,3),(54,'凡人修仙之仙界篇','第四章 石头哥哥','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“石头哥哥！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在虬髯大汉三人尚有些发愣之时，一个惊喜的脆生生声音蓦然响起。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那女童不知何时已翻身爬起，并一把抱住了这突然从巨石中冒出的高大青年。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童本就生得娇小玲珑，脑袋只到高大青年的腰际，此刻一双白嫩小手正死死抱住了青年大腿，身子有大半侧转着躲在了青年身后，一双水汪汪大眼睛望着高大青年，滴溜溜转动不停。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虬髯大汉等人闻言再次一愕，不禁互望了一眼，目光再次落在了高大青年身上，见其在听到女童叫唤后，面上没有丝毫表情，依旧发呆般的望着前方，三人脸上顿时生出了狐疑之色。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童心中一紧，抱住青年大腿的一双小手不住晃动，带着哭腔的忙又大声呼喊道：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“石头哥哥，乐儿总算找到你了！……这些坏人要抓乐儿，石头哥哥帮我打他们！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一次，高大青年竟似乎是听到了女童的呼喊一般，头颅缓缓垂下几分。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;女童抬头迎向了高大青年目光，结果看到对方双目空洞，心中又咯噔一下。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过这一幕落在虬髯大汉三人