/*Table structure for table `comments` */

DROP TABLE IF EXISTS `comments`;

CREATE TABLE `comments` (
  `cid` int(11) NOT NULL AUTO_INCREMENT,
  `c_bnum` int(11) DEFAULT NULL,
  `c_account` varchar(16) NOT NULL,
  `c_to_account` varchar(16) DEFAULT NULL,
  `ccontent` varchar(300) DEFAULT NULL,
  `ctime` datetime DEFAULT NULL,
  `ctype` int(11) DEFAULT NULL,
  `state` int(11) DEFAULT '0',
  `updatetime` datetime DEFAULT NULL,
  PRIMARY KEY (`cid`),
  KEY `comments_ibfk_1` (`c_bnum`),
  KEY `comments_ibfk_2` (`c_account`),
  CONSTRAINT `comments_ibfk_1` FOREIGN KEY (`c_bnum`) REFERENCES `books` (`bnum`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `comments_ibfk_2` FOREIGN KEY (`c_account`) REFERENCES `users` (`uaccount`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

